contract xYSLToken is IERC20, IERC20Metadata, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    uint256 public constant FEE = 125;
    uint256 public constant FEE_BURN = 50;
    uint256 public constant FEE_DIVIDER = 1000;
    uint256 public constant MAX_HARDCAP = 80000 * (10**18);

    address public adapter;

    struct LockedFunds {
        uint256 lockBlockTimestamp;
        uint256 lockTime;
        uint256 lockAmount;
        uint256 unlocked;
    }

    mapping(address => bool) public withdrawn;
    mapping(address => bool) public whitelist;
    mapping(address => LockedFunds) public usersLocked;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    string private _name;
    string private _symbol;

    ///@notice Modifier that gives access only for minter
    modifier minterOnly() {
        require(hasRole(MINTER_ROLE, _msgSender()), "Caller is not a minter");
        _;
    }

    modifier checkHardcap(uint256 _amount) {
        require(_totalSupply + _amount <= MAX_HARDCAP, "Hardcap reached max amount");
        _;
    }

    /**
     * @dev Sets the values for {name} and {symbol}.
     *
     * The default value of {decimals} is 18. To select a different value for
     * {decimals} you should overload it.
     *
     * All two of these values are immutable: they can only be set once during
     * construction.
     */
    constructor() {
        whitelist[address(0)] = true;
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _name = "xYSL token";
        _symbol = "xYSL";
    }

    /*****
     * Admin Interface
     *****/

    function addToWhitelist(address[] memory _recipients) external onlyRole(DEFAULT_ADMIN_ROLE) {
        for (uint256 i = 0; i < _recipients.length; i++) {
            whitelist[_recipients[i]] = true;
        }
    }

    function removeFromWhitelist(address[] memory _recipients) external onlyRole(DEFAULT_ADMIN_ROLE) {
        for (uint256 i = 0; i < _recipients.length; i++) {
            whitelist[_recipients[i]] = false;
        }
    }

    ///@notice Sets minter role
    ///@param _minter Address that will be set as minter
    function setMinter(address _minter) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_minter != address(0), "Null address provided");
        _setupRole(MINTER_ROLE, _minter);
    }

    ///@notice Remove minter role
    ///@param _minter Address that will be removed as minter
    function removeMinter(address _minter) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_minter != address(0), "Null address provided");
        revokeRole(MINTER_ROLE, _minter);
    }

    function setAdapter(address _adapter) external onlyRole(DEFAULT_ADMIN_ROLE) {
        adapter = _adapter;
    }

    /*****
     * Minter Interface
     *****/

    function mint(uint256 amount) public minterOnly {
        _mint(_msgSender(), amount);
    }

    function mintFor(address recipient, uint256 amount) public minterOnly {
        _mint(recipient, amount);
    }

    ///@dev Available only for minter contract. Transfers tokens to the user and locks them
    ///@param account Choosen account for mintage
    ///@param amount Amount of tokens to mint
    ///@param amount Lock time of the minted tokens
    function getAirdropped(
        address account,
        uint256 amount,
        uint256 lockTime
    ) external minterOnly {
        require(amount > 0 && lockTime > 0, "Incorrect amount");

        if (usersLocked[account].lockAmount > 0) {
            usersLocked[account].lockAmount += amount;
        } else {
            usersLocked[account] = LockedFunds(block.timestamp, lockTime, amount, 0);
        }
        IERC20(address(this)).transferFrom(_msgSender(), account, amount);
    }

    /*****
     * ERC20 Interface
     *****/
    /**
     * @dev Returns the name of the token.
     */
    function name() public view virtual override returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

