     *
     * Requirements:
     *
     * - `account` cannot be the zero address.
     */
    function _mint(address account, uint256 amount) internal virtual checkHardcap(amount) {
        require(account != address(0), "ERC20: mint to the zero address");

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);
    }

    /**
     * @dev Sets `amount` as the allowance of `spender` over the `owner` s tokens.
     *
     * This internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an {Approval} event.
     *
     * Requirements:
     *
     * - `owner` cannot be the zero address.
     * - `spender` cannot be the zero address.
     */
    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    /**
     * @dev Hook that is called before any transfer of tokens. This includes
     * minting and burning.
     *
     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * will be transferred to `to`.
     * - when `from` is zero, `amount` tokens will be minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens will be burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal {
        unlock(from);

        uint256 stillLocked = lockedTokens(from);
        if (stillLocked == 0) {
            return;
        }

        require(amount <= balanceOf(from) - stillLocked, "Not enough available tokens");
    }

    /**
     * @dev Hook that is called after any transfer of tokens. This includes
     * minting and burning.
     *
     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * has been transferred to `to`.
     * - when `from` is zero, `amount` tokens have been minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens have been burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal {
        if (whitelist[from] || whitelist[to]) {
            emit Transfer(from, to, amount);
            return;
        }

        if (adapter == address(0)) {
            emit Transfer(from, to, amount);
            return;
        }
        uint256 fee = (amount * FEE) / FEE_DIVIDER;
        uint256 feeBurned = (amount * FEE_BURN) / FEE_DIVIDER;
        uint256 feeAdapter = fee - feeBurned;

        _balances[to] -= fee;
        _balances[address(0)] += feeBurned;

        _balances[adapter] += feeAdapter;
        IxYSLAdapter(adapter).transferSurcharge();

        emit Transfer(from, to, amount - fee);
        emit Transfer(from, address(0), feeBurned);
        emit Transfer(from, adapter, feeAdapter);
    }

    /*****
     * Locked tokens Interface
     *****/

    function lockedTokens(address _user) public view returns (uint256) {
        uint256 airdropLock = usersLocked[_user].lockAmount - usersLocked[_user].unlocked;
        return airdropLock;
    }

    ///@notice Shows unlocked sYSL
    ///@param _user Account
    ///@return Amount of linearly unlocked sYSL
    function unlockedTokens(address _user) external view returns (uint256) {
        return usersLocked[_user].unlocked;
    }

    ///@notice Shows unlockable xYSL amount
    ///@param _user Account
    ///@return Amount of currently unlockable tokens
    function unlockable(address _user) public view returns (uint256) {
        return _vested(_user) - usersLocked[_user].unlocked;
    }

    ///@notice Shows free sYSL balance
    ///@param _user Account
    ///@return Balance which can be transferred to another wallet
    function availableTokens(address _user) external view returns (uint256) {
        return balanceOf(_user) - lockedTokens(_user) + unlockable(_user);
    }

    ///@notice Unlocks vested tokens (linearly locked)
    ///@param account Address to unlock tokens for
    function unlock(address account) public {
        LockedFunds memory lf = usersLocked[account];
        if (lf.lockAmount == 0 || lf.unlocked == lf.lockAmount) {
            return;
        }
        if (block.timestamp >= lf.lockBlockTimestamp + lf.lockTime) {
            usersLocked[account].unlocked = lf.lockAmount;
        } else {
            usersLocked[account].unlocked = (lf.lockAmount * (block.timestamp - lf.lockBlockTimestamp)) / lf.lockTime;
        }
    }

    function _vested(address account) internal view returns (uint256) {
        LockedFunds memory lf = usersLocked[account];
        if (lf.lockAmount == 0 || lf.unlocked == lf.lockAmount) {
            return 0;
        }
        if (block.timestamp >= lf.lockBlockTimestamp + lf.lockTime) {
            return lf.lockAmount;
        } else {
            return (lf.lockAmount * (block.timestamp - lf.lockBlockTimestamp)) / lf.lockTime;
        }
    }
