        emit MarketingFeeChanged(burnFee, marketingFee_);
        marketingFee = marketingFee_;
    }

    function setMarketingAddress(address marketingAddress_) external onlyOwner{
        emit MarketingAddressChanged(marketingAddress, marketingAddress_);
        marketingAddress = marketingAddress_;
    }
