    /* RFI Management */
    function excludeAccountFromFee(address account) external onlyOwner() {
        require(_isExcludedFromFee[account] == false, "Account already excluded from fee");
        _isExcludedFromFee[account] = true;
        emit ExcludeAccountFromFees(account);
    }

    function multiExcludeAccountsFromFees(address [] memory accounts) external onlyOwner() {
        for(uint256 i=0; i<accounts.length; i++){
            _isExcludedFromFee[accounts[i]] = true;
            emit ExcludeAccountFromFees(accounts[i]);
        }
    }

    function includeAccountInFee(address account) external onlyOwner() {
        require(_isExcludedFromFee[account] == true, "Account already included in fee");
        _isExcludedFromFee[account] = false;
    }

    function excludeAccountFromRewards(address account) public onlyOwner() {
        require(!_isExcludedFromRewards[account], "Account is already excluded");
        require(_excludedFromRewards.length < MAXIMUM_EXCLUDED_ACCOUNTS, "Too many addresses excluded, the maximum number of excluded accounts is limited to: 30");

        if(_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }

        _isExcludedFromRewards[account] = true;
        _excludedFromRewards.push(account);
        emit ExcludeAccountFromRewards(account);
    }

    function multiExcludeAccountFromRewards(address [] memory accounts) external onlyOwner{
        for (uint256 i=0; i<accounts.length; i++){
            excludeAccountFromRewards(accounts[i]);
        }
    }
    /* End of RFI Management */
