    "contracts/NFTstaking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "hardhat/console.sol";
import "./RewardVault.sol";

/**
 * @title Highstack Farm Certificates (HS-CERT)
 * @author highstack.co
 * @notice This is a stakeable contract instance that allows users to stake tokens in return for rewards.
 *         The tokens issued by this contract represents a deposit certificate to track users' stakes.
 */

contract HighstackNftFarmCerts is ERC20, Ownable, ReentrancyGuard {
    using SafeMath for uint256;

    IERC20 private _rewardsToken;
    IERC721 private _stakedERC721;
    PoolInfo public pool;
    bool public isETHreward = false;
    uint256 private _cumulativeOrigTotalRewards = 0;
    address private constant _burner =
        0x000000000000000000000000000000000000dEaD;

    struct PoolInfo {
        uint256 cumulativeRewards; // supply of rewards tokens ever deposited into this farm.
        uint256 paidOutRewards; // supply of rewards tokens paid out.
        uint256 curRewardsSupply; // current supply of rewards
        uint256 totalTokensStaked; // current amount of tokens staked (or... total shares)
        uint256 accERC20PerShare; // Accumulated ERC20s per share, mul by 1e36.
    }

    struct StakerInfo {
        uint256 rewardDebt; // Reward debt - how much should be deducted from calculated pending based on when user stakes && harvests.
        uint256[] nftTokenIds; // make sure we store NFT token IDs here
    }

    // mapping of userAddresses => tokenAddresses that can
    // can be evaluated to determine for a particular user which tokens
    // they are staking.
    mapping(address => StakerInfo) public stakers;

    mapping(uint256 => uint8) public tierById;

    event Deposit(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);

    receive() external payable {
        if (isETHreward) {
            addRewards(msg.value);
        }
    }

    /**
     * @notice The constructor for the Staking Token.
     * @param _name Name of the staking token
     * @param _symbol Name of the staking token symbol
     * @param _rewardsTokenAddy Contract address of token to be rewarded to users
     * @param _stakedTokenAddy Contract address of token to be staked by users
     */
    constructor(
        string memory _name,
        string memory _symbol,
        address _rewardsTokenAddy,
        address _stakedTokenAddy
    ) ERC20(_name, _symbol) {
        if (_rewardsTokenAddy != 0x0000000000000000000000000000000000000000) {
            _rewardsToken = ERC20(_rewardsTokenAddy);
        } else {
            isETHreward = true;
        }

        _stakedERC721 = IERC721(_stakedTokenAddy);

        pool = PoolInfo({
            cumulativeRewards: 0,
            paidOutRewards: 0,
            curRewardsSupply: 0,
            totalTokensStaked: 0,
            accERC20PerShare: 0
        });
    }

    function setTokenTiers(uint256[] memory _tokenIds, uint8 weighting)
        external
        onlyOwner
    {
        for (uint256 _i = 0; _i < _tokenIds.length; _i++) {
            tierById[_tokenIds[_i]] = weighting;
        }
    }

    // This is used to add rewards for ERC20 tokens to be instantly distributed.
    function addRewards(uint256 amount) public nonReentrant onlyOwner {
        require(pool.totalTokensStaked > 0, 'No one to distribute rewards to :(');
        pool.accERC20PerShare = pool.accERC20PerShare.add(
            amount.mul(1e36).div(pool.totalTokensStaked)
        );
        if (!isETHreward) {
            _rewardsToken.transferFrom(msg.sender, address(this), amount);
        }
        pool.cumulativeRewards = pool.cumulativeRewards.add(amount);
    }

    function stakedTokenAddress() external view returns (address) {
        return address(_stakedERC721);
    }

    function rewardsTokenAddress() external view returns (address) {
        return address(_rewardsToken);
    }

    // Main Farm functions (public)
    // this always harvests tokens.
    // TODO: Add in special ids for legendaries and assign weight or give double farm certs.
    function stakeTokens(uint256[] memory _tokenIds) external nonReentrant {
        if (balanceOf(msg.sender) > 0) {
            _harvestTokens(msg.sender);
        }
        uint256 _finalAmountTransferred = 0;
        for (uint256 _i = 0; _i < _tokenIds.length; _i++) {
            _stakedERC721.transferFrom(
                msg.sender,
                address(this),
                _tokenIds[_i]
            );
            uint8 tier = tierById[_tokenIds[_i]];
            _finalAmountTransferred = _finalAmountTransferred.add(
                (tier == 0 ? uint256(1) : uint256(tier))
            );
        }

        // send farm tokens equivalent to number of LPs staked.
        if (_finalAmountTransferred > 0) {
            _mint(msg.sender, _finalAmountTransferred);
        }
        StakerInfo storage _staker = stakers[msg.sender];

        // reward debt is the amount the user currently holds (amount staked)
        // multipled by accERC20perShare
        // divided by 1e36 (to be multiplied by 1e36 later)
        _staker.rewardDebt = balanceOf(msg.sender)
            .mul(pool.accERC20PerShare)
            .div(1e36);
        for (uint256 _i = 0; _i < _tokenIds.length; _i++) {
            _staker.nftTokenIds.push(_tokenIds[_i]);
        }
        _updNumStaked(_finalAmountTransferred, "add");
        emit Deposit(msg.sender, _finalAmountTransferred);
    }

    // pass 'false' for shouldHarvest for emergency unstaking without claiming rewards
    // This should always be true under normal circumstances
    function unstakeTokens(bool shouldHarvest) external nonReentrant {
        StakerInfo memory _staker = stakers[msg.sender];
        uint256 _userBalance = balanceOf(msg.sender);

        if (shouldHarvest) {
            _harvestTokens(msg.sender);
        }

        // this burns the farm certificates
        transfer(_burner, _userBalance);
        for (uint256 _i = 0; _i < _staker.nftTokenIds.length; _i++) {
            _stakedERC721.transferFrom(
                address(this),
                msg.sender,
                _staker.nftTokenIds[_i]
            );
        }
        if (balanceOf(msg.sender) <= 0) {
            delete stakers[msg.sender];
        }
        _updNumStaked(_userBalance, "remove");
        emit Withdraw(msg.sender, _userBalance);
    }

    // forfeit all rewards under emergency and get staked tokens back.
    function emergencyUnstake() external {
        StakerInfo memory _staker = stakers[msg.sender];
        uint256 _amountToRemoveFromStaked = balanceOf(msg.sender);
        require(
            _amountToRemoveFromStaked > 0,
            "user can only unstake if they have tokens in the pool"
        );
        transfer(_burner, _amountToRemoveFromStaked);
        for (uint256 _i = 0; _i < _staker.nftTokenIds.length; _i++) {
            _stakedERC721.transferFrom(
                address(this),
                msg.sender,
                _staker.nftTokenIds[_i]
            );
        }
        delete stakers[msg.sender];
        _updNumStaked(_amountToRemoveFromStaked, "remove");
        emit Withdraw(msg.sender, _amountToRemoveFromStaked);
    }

    function calcHarvestTot(address _userAddy) public view returns (uint256) {
        StakerInfo memory _staker = stakers[_userAddy];
        uint256 _accERC20PerShare = pool.accERC20PerShare;
        return
            balanceOf(_userAddy).mul(_accERC20PerShare).div(1e36).sub(
                _staker.rewardDebt
            );
    }

    function _harvestTokens(address _userAddy) private returns (uint256) {
        StakerInfo storage _staker = stakers[_userAddy];
        uint256 _num2Trans = calcHarvestTot(_userAddy);
        if (_num2Trans > 0) {
            if (isETHreward) {
                (bool sent, bytes memory data) = _userAddy.call{
                    value: _num2Trans
                }("");
                pool.curRewardsSupply = address(this).balance;
            } else {
                _rewardsToken.transfer(_userAddy, _num2Trans);
                pool.curRewardsSupply = _rewardsToken.balanceOf(address(this));
            }
        }
        _staker.rewardDebt = balanceOf(_userAddy)
            .mul(pool.accERC20PerShare)
            .div(1e36);
        pool.paidOutRewards = pool.paidOutRewards.add(_num2Trans);
        return _num2Trans;
    }

    // update the amount currently staked after a user harvests
    function _updNumStaked(uint256 _amount, string memory _operation) private {
        if (_compareStr(_operation, "remove")) {
            pool.totalTokensStaked = pool.totalTokensStaked.sub(_amount);
        } else {
            pool.totalTokensStaked = pool.totalTokensStaked.add(_amount);
        }
    }

    function _compareStr(string memory a, string memory b)
        private
        pure
        returns (bool)
    {
        return (keccak256(abi.encodePacked((a))) ==
            keccak256(abi.encodePacked((b))));
    }
}
