// File: project/dao/donate.sol


pragma solidity ^0.8.0;




contract ERC20PayToken is Ownable{
    using SafeMath for uint256;

    address public receiver;

    event Mint(address indexed token, address indexed sender, uint indexed amount, uint source);
    event Redeem(uint indexed _type, address indexed receiver, uint indexed amount);
    event WithDrawalToken(address indexed token, address indexed receiver, uint indexed amount);

    address public destroyAddress = address(0x000000000000000000000000000000000000dEaD);
    address public tokenAddress = address(0x5ECCD741761E2C7642DbBFC86Dd6D524d9e840BE);
    mapping(address=> uint) public account;
    uint public tokenRatio = 10000000;
    bool public mintSwitch = true;
    bool public redeemSwitch = true;
    bool public withDrawalSwitch = true;
    uint public ethTotalAmount;

    struct MintLog {
        uint _type;
        uint amount;
        uint256 time;
    }
    mapping(address=> MintLog[]) private mintLog;


    receive() external payable {}

    constructor() {
        receiver = address(this);
    }

    function setTokenAddress(address _tokenAddress) external onlyOwner returns(bool){
        tokenAddress = address(_tokenAddress);
        
        return true;
    }

    function setTokenRatio(uint ratio) external onlyOwner returns(bool){
        tokenRatio = ratio;

        return true;
    }

    function setMintSwitch(bool _switch) external onlyOwner returns(bool){
        mintSwitch = _switch;

        return true;
    }

    function setRedeemSwitch(bool _switch) external onlyOwner returns(bool){
        redeemSwitch = _switch;

        return true;
    }

    function setWithDrawalSwitch(bool _switch) external onlyOwner returns(bool){
        withDrawalSwitch = _switch;

        return true;
    }

    function getMintLog(address _address) public view returns (MintLog[] memory) {
        return mintLog[_address];
    }

    function mint(uint source)  public payable virtual returns (bool) {
        require(mintSwitch, "Not open yet");
        require(msg.value >= 1 * 10 ** 17, "Donation amount must be greater than or equal to 0.1");
        uint amount = msg.value.mul(tokenRatio);
        account[msg.sender] = account[msg.sender].add(amount);

        ethTotalAmount = ethTotalAmount.add(msg.value);

        mintLog[msg.sender].push(MintLog(2, msg.value, block.timestamp));

        emit Mint(address(0), msg.sender, msg.value, source);
        return true;
    }

    function redeem(uint _type, uint amount) public returns (bool) {
        if (_type == 0) {
            //redeem
            require(redeemSwitch, "Not open yet");
            require(amount >= tokenRatio * 10 ** 17, "redemption must be greater than or equal to 1,000,000");

            IERC20(tokenAddress).transferFrom(msg.sender, destroyAddress, amount);
            uint eth = amount.div(tokenRatio);
            payable(msg.sender).transfer(eth);

            mintLog[msg.sender].push(MintLog(0, eth, block.timestamp));
        } else {
            //withDrawalToken
            require(withDrawalSwitch, "Not open yet");

            require(account[msg.sender] >= amount, "Insufficient address balance");
            uint balance = IERC20(tokenAddress).balanceOf(address(this));
            require(balance >= amount, "Insufficient contract balance");

            IERC20(tokenAddress).transfer(msg.sender, amount);

            account[msg.sender] = account[msg.sender].sub(amount);
            mintLog[msg.sender].push(MintLog(1, amount, block.timestamp));
        }
       

        emit Redeem(_type, msg.sender, amount);
        return true;
    }

    function withDrawalToken(address token, address _address, uint amount) external onlyOwner returns(bool){
        if (token == address(0)) {
            payable(_address).transfer(amount);
        }else {
            IERC20(token).transfer(_address, amount);
        }

        emit WithDrawalToken(token, _address, amount);
        return true;
    }

    function setReceiver(address _receiver) external onlyOwner returns(bool){

        receiver = _receiver;

        return true;
    }
}
