    "contracts/launchpad/ERC20Fee.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "../IUniswapV2Router02.sol";
import "../IUniswapV2Factory.sol";
import "./interfaces/IERC20InitExt.sol";

contract ERC20Fee is ERC20, Ownable, IERC20InitExt, Initializable {
    using SafeMath for uint256;

    mapping(address => bool) private _isExcludedFromFees;

    uint internal sellTax;
    uint internal buyTax;
    uint internal constant PERCENT_DIVIDER = 1000;
    string private _name;
    string private _symbol;
    uint8 private _decimals;

    IUniswapV2Router02 internal ROUTER;

    address private feeTo;
    address[4] private tokenFees;

    mapping(address => bool) internal pancakeSwapPair;

    event ExcludeFromFees(address indexed account, bool isExcluded);

    modifier transferTaxFree() {
        uint256 taxSell = sellTax;
        uint taxBuy = buyTax;
        sellTax = 0;
        buyTax = 0;
        _;
        sellTax = taxSell;
        buyTax = taxBuy;
    }

    constructor(
    ) ERC20("ERC20Fee", "ERC20Fee") {}

    function name() public view override returns (string memory) {
        return _name;
    }

    function symbol() public view override returns (string memory) {
        return _symbol;
    }

    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    function initialize(
        address owner_,
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        uint256 totalSupply_,
        address _router,
        address _feeTo,
        uint _sellTax,
        uint _buyTax
    ) external override initializer {
        require(_sellTax <= PERCENT_DIVIDER, "Sell tax must be less than 100%");
        require(_buyTax <= PERCENT_DIVIDER, "Buy tax must be less than 100%");
        _transferOwnership(owner_);
        _name = name_;
        _symbol = symbol_;
        _decimals = decimals_;
        _mint(owner_, totalSupply_);
        ROUTER = IUniswapV2Router02(_router);
        tokenFees = [
            0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56,
            0x55d398326f99059fF775485246999027B3197955,
            0x2170Ed0880ac9A755fd29B2688956BD959F933F8,
            0x7130d2A12B9BCbFAe4f2634d864A1Ee1Ce3Ead9c
        ];
        feeTo = _feeTo;
        sellTax = _sellTax;
        buyTax = _buyTax;
        initializePairs();
        _isExcludedFromFees[address(this)] = true;
        _isExcludedFromFees[owner_] = true;
    }

    function isPair(address _address) public view returns (bool) {
        return pancakeSwapPair[_address];
    }

    function initializePairs() private {
        IUniswapV2Factory FACTORY = IUniswapV2Factory(ROUTER.factory());
        address pair = FACTORY.createPair(address(this), ROUTER.WETH());
        pancakeSwapPair[pair] = true;
        // for (uint i = 0; i < tokenFees.length; i++) {
        //     pair = FACTORY.createPair(address(this), tokenFees[i]);
        //     pancakeSwapPair[pair] = true;
        // }
    }

    function transferIsWhitelist(
        address sender,
        address receiver
    ) internal view returns (bool) {
        return _isExcludedFromFees[sender] || _isExcludedFromFees[receiver];
    }

    function _transfer(
        address sender,
        address receiver,
        uint256 amount
    ) internal virtual override {
        uint256 taxAmount;

        bool isWhitelisted = transferIsWhitelist(sender, receiver);
        if (!isWhitelisted && (buyTax > 0 || sellTax > 0)) {
            if (isPair(sender)) {
                //It's an LP Pair and it's a buy
                taxAmount = (amount * buyTax) / PERCENT_DIVIDER;
            } else if (isPair(receiver)) {
                //It's an LP Pair and it's a sell
                taxAmount = (amount * sellTax) / PERCENT_DIVIDER;
            }
        }

        if (taxAmount > 0) {
            super._transfer(sender, feeTo, taxAmount);
        }
        super._transfer(sender, receiver, amount - taxAmount);
    }

    fallback() external {
        revert();
    }

    function excludeFromFees(
        address account,
        bool excluded
    ) external onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function getPulicData() public view returns (uint, uint, uint, address) {
        return (sellTax, buyTax, PERCENT_DIVIDER, feeTo);
    }

    function setFeeTo(address _feeTo) external onlyOwner {
        feeTo = _feeTo;
    }
}
"
