    bool public inSwap;

    modifier lockTheSwap() {
        inSwap = true;

        _;

        inSwap = false;
    }

    event SwapTokensForShiB(
        address shibAddress,
        uint256 value,
        uint256 timestamp
    );

    function swapTokensForSHIB(uint256 tokenAmount) private lockTheSwap {
        _approve(address(this), address(_uniswapV2Router), tokenAmount);
        uint256 oldShibAmount = IERC20(shibAddress).balanceOf(
            receiveSHIBAddress
        );
        address[] memory path = new address[](3);

        path[0] = address(this);

        path[1] = address(usdtAddress);

        path[2] = address(shibAddress);

        _uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            receiveSHIBAddress,
            block.timestamp
        );

        uint256 newShibAmount = IERC20(shibAddress).balanceOf(
            receiveSHIBAddress
        );
        emit SwapTokensForShiB(
            address(receiveSHIBAddress),
            newShibAmount - oldShibAmount,
            block.timestamp
        );
    }

