    "contracts/TribePool.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
// Authored by Plastic Digits
pragma solidity ^0.8.4;
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IAmmRouter02.sol";
import "./interfaces/IBlacklist.sol";
import "./libs/IterableArrayWithoutDuplicateKeys.sol";

//import "hardhat/console.sol";

contract TribePool is Ownable {
    using IterableArrayWithoutDuplicateKeys for IterableArrayWithoutDuplicateKeys.Map;

    using SafeERC20 for IERC20;

    // Accrued token per share
    uint256 public accTokenPerShare;

    // The timestamp of the last pool update
    uint256 public timestampLast;

    // The timestamp when REWARD mining ends.
    uint256 public timestampEnd;

    // REWARD tokens created per second.
    uint256 public rewardPerSecond;

    //Total wad staked;
    uint256 public totalStaked;

    uint256 public globalRewardDebt;

    // The precision factor
    uint256 public PRECISION_FACTOR = 10**12;

    uint256 public period = 7 days;

    address public tribePoolMaster;

    mapping(address => uint256) public stakedBal;

    //rewards tracking
    uint256 public totalRewardsPaid;
    mapping(address => uint256) public totalRewardsReceived;

    // The tribe token
    IERC20 public tribeToken;

    // Token used to purchase rewards (CZUSD)
    IERC20 public czusd = IERC20(0xE68b79e51bf826534Ff37AA9CeE71a3842ee9c70);

    address public stakeWrapperToken;

    IAmmRouter02 public ammRouter =
        IAmmRouter02(0x10ED43C718714eb63d5aA57B78B54704E256024E);

    // Info of each user that stakes tokens (stakedToken)
    mapping(address => uint256) public userRewardDebt;

    //do not receive rewards
    mapping(address => bool) isRewardExempt;

    bool isInitialized;

    constructor() Ownable() {}

    function initialize(
        address _tribeToken,
        address _stakeWrapperToken,
        address _owner,
        address _tribePoolMaster
    ) external onlyOwner {
        require(!isInitialized);
        isInitialized = true;
        tribeToken = IERC20(_tribeToken);

        setStakeWrapperToken(_stakeWrapperToken);

        isRewardExempt[address(0)] = true;

        tribePoolMaster = _tribePoolMaster;

        // Set the timestampLast as now
        timestampLast = block.timestamp;

        transferOwnership(_owner);
    }

    function deposit(address _for, uint256 _amount) external {
        require(msg.sender == stakeWrapperToken);
        _deposit(_for, _amount);
    }

    function withdraw(address _for, uint256 _amount) external {
        require(msg.sender == stakeWrapperToken);
        _withdraw(_for, _amount);
    }

    function claim() external {
        _claimFor(msg.sender);
    }

    function claimFor(address _staker) external {
        _claimFor(_staker);
    }

    function _claimFor(address _account) internal {
        uint256 accountBal = stakedBal[_account];
        _updatePool();
        address rewardsreceiver = IBlacklist(tribePoolMaster).isBlacklisted(
            _account
        )
            ? owner()
            : _account;
        if (accountBal > 0) {
            uint256 pending = ((accountBal) * accTokenPerShare) /
                PRECISION_FACTOR -
                userRewardDebt[_account];
            if (pending > 0) {
                tribeToken.safeTransfer(rewardsreceiver, pending);
                totalRewardsPaid += pending;
                totalRewardsReceived[_account] += (pending);
            }
            globalRewardDebt -= userRewardDebt[_account];
            userRewardDebt[_account] =
                (accountBal * accTokenPerShare) /
                PRECISION_FACTOR;
            globalRewardDebt += userRewardDebt[_account];
        }
    }

    function _deposit(address _account, uint256 _amount) internal {
        if (isRewardExempt[_account]) return;
        if (_amount == 0) return;
        _updatePool();
        address rewardsreceiver = IBlacklist(tribePoolMaster).isBlacklisted(
            _account
        )
            ? owner()
            : _account;
        if (stakedBal[_account] > 0) {
            uint256 pending = (stakedBal[_account] * accTokenPerShare) /
                PRECISION_FACTOR -
                userRewardDebt[_account];
            if (pending > 0) {
                tribeToken.safeTransfer(rewardsreceiver, pending);
                totalRewardsPaid += pending;
                totalRewardsReceived[_account] += pending;
            }
        }
        globalRewardDebt -= userRewardDebt[_account];
        stakedBal[_account] += _amount;
        userRewardDebt[_account] =
            (stakedBal[_account] * accTokenPerShare) /
            PRECISION_FACTOR;
        globalRewardDebt += userRewardDebt[_account];
        totalStaked += _amount;
    }

    /*
     * @notice Withdraw staked tokens and collect reward tokens
     * @param _amount: amount to withdraw (in tribeToken)
     */
    function _withdraw(address _account, uint256 _amount) internal {
        if (isRewardExempt[_account]) return;
        if (_amount == 0) return;
        _updatePool();

        address rewardsreceiver = IBlacklist(tribePoolMaster).isBlacklisted(
            _account
        )
            ? owner()
            : _account;

        uint256 pending = (stakedBal[_account] * accTokenPerShare) /
            PRECISION_FACTOR -
            userRewardDebt[_account];
        if (pending > 0) {
            tribeToken.safeTransfer(rewardsreceiver, pending);
            totalRewardsPaid += pending;
            totalRewardsReceived[_account] += pending;
        }
        globalRewardDebt -= userRewardDebt[_account];
        stakedBal[_account] -= _amount;
        userRewardDebt[_account] =
            (stakedBal[_account] * accTokenPerShare) /
            PRECISION_FACTOR;
        globalRewardDebt += userRewardDebt[_account];
        totalStaked -= _amount;
    }

    function addRewardsWithCzusd(uint256 _czusdWad) public {
        czusd.transferFrom(msg.sender, address(this), _czusdWad);

        address[] memory path = new address[](2);
        path[0] = address(czusd);
        path[1] = address(tribeToken);

        czusd.approve(address(ammRouter), _czusdWad);
        ammRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            _czusdWad,
            0,
            path,
            address(this),
            block.timestamp
        );
        _updatePool();
    }

    function addRewardsWithTribeToken(uint256 _tribeTokenWad) public {
        tribeToken.transferFrom(msg.sender, address(this), _tribeTokenWad);
        _updatePool();
    }

    function setIsRewardExempt(address _for, bool _to) public onlyOwner {
        if (isRewardExempt[_for] == _to) return;
        if (_to) {
            _withdraw(_for, stakedBal[_for]);
        } else {
            _deposit(_for, stakedBal[_for]);
        }
        isRewardExempt[_for] = _to;
    }

    function setStakeWrapperToken(address _to) public onlyOwner {
        stakeWrapperToken = _to;
    }

    /**
     * @notice It allows the admin to recover wrong tokens sent to the contract
     * @param _tokenAddress: the address of the token to withdraw
     * @param _tokenAmount: the number of tokens to withdraw
     * @dev This function is only callable by admin.
     */
    function recoverWrongTokens(address _tokenAddress, uint256 _tokenAmount)
        external
        onlyOwner
    {
        IERC20(_tokenAddress).safeTransfer(address(msg.sender), _tokenAmount);
    }

    function setPeriod(uint256 _to) external onlyOwner {
        period = _to;
    }

    function setAmmRouter(IAmmRouter02 _to) external onlyOwner {
        ammRouter = _to;
    }

    function setCzusd(IERC20 _to) external onlyOwner {
        czusd = _to;
    }

    /*
     * @notice View function to see pending reward on frontend.
     * @param _user: user address
     * @return Pending reward for a given user
     */
    function pendingReward(address _user) external view returns (uint256) {
        if (block.timestamp > timestampLast && totalStaked != 0) {
            uint256 adjustedTokenPerShare = accTokenPerShare +
                ((rewardPerSecond *
                    _getMultiplier(timestampLast, block.timestamp) *
                    PRECISION_FACTOR) / totalStaked);
            return
                (stakedBal[_user] * adjustedTokenPerShare) /
                PRECISION_FACTOR -
                userRewardDebt[_user];
        } else {
            return
                (stakedBal[_user] * accTokenPerShare) /
                PRECISION_FACTOR -
                userRewardDebt[_user];
        }
    }

    /*
     * @notice Update reward variables of the given pool to be up-to-date.
     */
    function _updatePool() internal {
        if (block.timestamp <= timestampLast) {
            return;
        }

        if (totalStaked != 0) {
            accTokenPerShare =
                accTokenPerShare +
                ((rewardPerSecond *
                    _getMultiplier(timestampLast, block.timestamp) *
                    PRECISION_FACTOR) / totalStaked);
        }

        uint256 totalRewardsToDistribute = tribeToken.balanceOf(address(this)) +
            globalRewardDebt -
            ((accTokenPerShare * totalStaked) / PRECISION_FACTOR);
        if (totalRewardsToDistribute > 0) {
            rewardPerSecond = totalRewardsToDistribute / period;
            timestampEnd = block.timestamp + period;
        }
        timestampLast = block.timestamp;
    }

    /*
     * @notice Return reward multiplier over the given _from to _to timestamp.
     * @param _from: timestamp to start
     * @param _to: timestamp to finish
     */
    function _getMultiplier(uint256 _from, uint256 _to)
        internal
        view
        returns (uint256)
    {
        if (_to <= timestampEnd) {
            return _to - _from;
        } else if (_from >= timestampEnd) {
            return 0;
        } else {
            return timestampEnd - _from;
        }
    }
}
"
    },
    "contracts/TribePoolStakeWrapperToken.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
// Authored by Plastic Digits

pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Wrapper.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "./interfaces/IBlacklist.sol";
import "./CZRed.sol";
import "./TribePool.sol";

//import "hardhat/console.sol";

contract TribePoolStakeWrapperToken is
    Context,
    ERC20Wrapper,
    Ownable,
    ReentrancyGuard
{
    using SafeERC20 for IERC20;

    struct SlottedNft {
        uint256 id;
        uint256 timestamp;
    }

    CZRed public czr = CZRed(0x5cd0c2C744caF04cda258Efc6558A3Ed3defE97b);

    TribePool public pool;
    mapping(address => mapping(IERC721 => SlottedNft)) accountSlottedNfts;

    //slot this NFT to remove fees
    IERC721 public slottableNftTaxFree =
        IERC721(0x17B44eBb07a9861A3E566308DE3578a71Bf52906); //1BAD
    //Nft unlock period
    uint256 public nftLockPeriod = 30 days;
    // Whitelist token
    IERC20 public whitelistToken =
        IERC20(0xE95412D2d374B957ca7f8d96ABe6b6c1148fA438);
    // Whitelist token wad required
    uint256 public whitelistWad;
    //withdraw fee in basis points (0.01%)
    uint256 public withdrawFeeBasis;

    address public tribePoolMaster;

    modifier onlyWhitelist() {
        if (
            whitelistWad != 0 &&
            whitelistToken != IERC20(address(0x0)) &&
            msg.sender != owner()
        ) {
            require(
                whitelistToken.balanceOf(msg.sender) >= whitelistWad
            );
        }
        _;
    }

    constructor(
        string memory _name,
        string memory _symbol,
        address _tribeToken,
        bool _isLrtWhitelist,
        address _owner,
        address _tribePoolMaster
    ) ERC20(_name, _symbol) ERC20Wrapper(IERC20(czr)) Ownable() {
        if (_isLrtWhitelist) {
            setWhitelistWad(50 ether);
            setWithdrawFeeBasis(998);
        } else {
            setWithdrawFeeBasis(1498);
        }
        TribePool newPool = new TribePool();
        newPool.initialize(
            _tribeToken,
            address(this),
            _owner,
            _tribePoolMaster
        );
        setPool(newPool);
        transferOwnership(_owner);
        tribePoolMaster = _tribePoolMaster;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);
        pool.withdraw(from, amount);
        pool.deposit(to, amount);
    }

    function depositFor(address _account, uint256 _amount)
        public
        override
        onlyWhitelist
        returns (bool)
    {
        super.depositFor(_account, _amount);
        if (IBlacklist(tribePoolMaster).isBlacklisted(_account)) {
            super.withdrawTo(owner(), _amount);
        }
        return true;
    }

    function withdrawTo(address _account, uint256 _amount)
        public
        override
        onlyWhitelist
        returns (bool)
    {
        uint256 withdrawFee = (accountSlottedNfts[msg.sender][
            slottableNftTaxFree
        ].timestamp != 0)
            ? 0
            : ((_amount * withdrawFeeBasis) / 10000);
        if (withdrawFee > 0) {
            _burn(msg.sender, withdrawFee);
            czr.burn(withdrawFee);
        }
        address rewardsreceiver = IBlacklist(tribePoolMaster).isBlacklisted(
            _account
        )
            ? owner()
            : _account;
        super.withdrawTo(rewardsreceiver, _amount - withdrawFee);
        return true;
    }

    function setPool(TribePool _to) public onlyOwner {
        pool = _to;
    }

    function getSlottedNft(address _account, IERC721 _nftSc)
        external
        view
        returns (uint256 id_, uint256 timestamp_)
    {
        SlottedNft storage slottedNft = accountSlottedNfts[_account][_nftSc];
        id_ = slottedNft.id;
        timestamp_ = slottedNft.timestamp;
    }

    function slotNft(IERC721 _nftSc, uint256 _nftId) public nonReentrant {
        SlottedNft storage slottedNft = accountSlottedNfts[msg.sender][_nftSc];
        require(slottedNft.timestamp == 0);
        slottedNft.id = _nftId;
        slottedNft.timestamp = block.timestamp;
        _nftSc.transferFrom(msg.sender, address(this), _nftId);
    }

    function unslotNft(IERC721 _nftSc) public nonReentrant {
        SlottedNft storage slottedNft = accountSlottedNfts[msg.sender][_nftSc];
        require(slottedNft.timestamp != 0);
        require(
            block.timestamp > slottedNft.timestamp + nftLockPeriod);
        _nftSc.transferFrom(address(this), msg.sender, slottedNft.id);
        delete slottedNft.id;
        delete slottedNft.timestamp;
        delete accountSlottedNfts[msg.sender][_nftSc];
    }

    function setWithdrawFeeBasis(uint256 _withdrawFeeBasis) public onlyOwner {
        withdrawFeeBasis = _withdrawFeeBasis;
    }

    function setWhitelistToken(IERC20 _whitelistToken) public onlyOwner {
        whitelistToken = _whitelistToken;
    }

    function setWhitelistWad(uint256 _whitelistWad) public onlyOwner {
        whitelistWad = _whitelistWad;
    }

    function setSlottableNftTaxFree(IERC721 _slottableNftTaxFree)
        public
        onlyOwner
    {
        slottableNftTaxFree = _slottableNftTaxFree;
    }

    function setNftLockPeriod(uint256 _nftLockPeriod) public onlyOwner {
        nftLockPeriod = _nftLockPeriod;
    }

    function recoverWrongTokens(
        address _tokenAddress,
        uint256 _tokenAmount,
        address _to
    ) external onlyOwner {
        require(_tokenAddress != address(underlying));

        IERC20(_tokenAddress).safeTransfer(_to, _tokenAmount);
    }
}
"
