    function balanceOf(address account) external view returns (uint256);
    function mint(address _address, uint256 _amount) external;
    function burnFrom(address account, uint256 amount) external;
    function transferOwnership(address newOwner) external;
}

contract Swap is Ownable, Pausable, Initializable {
    using SafeMath for uint256;
    using SafeMath for uint16;
    using SafeERC20 for IERC20;

    enum SwapType {
        mint,
        redeem
    }

    struct SwapLog {
        SwapType swapType;
        uint256 totalBUSD;
        uint256 totalFee;
    }

    IERC20 private FeeToken;
    IERC20 private StableToken;
    IStableMMP private mmUSD;
    IPancakeRouter private PancakeRouter;

    mapping(address => SwapLog[]) public swapHistory;
    uint256 private digit = 10**18;
    uint256 public mintFeeRate;
    uint256 public redeemFeeRate;
    uint256 public totalMintFee;
    uint256 public totalRedeemFee;
    uint256 public maxFeeTxAmount;
    address public feeTokenAddress;
    address public stableTokenAddress;
    address public wbnbAddress;
    address[] private stableOutputPath;
    address[] private stableInputPath;

    event DevWithdraw(address indexed user, uint256 amount);
    event Mint(address indexed user, uint totalBUSD, uint256 feeToken);
    event Redeem(address indexed user, uint totalBUSD, uint256 feeToken);

    function initialize(
        uint256 _mintFeeRate,
        uint256 _redeemFeeRate,
        address _FeeToken,
        address _StableToken,
        address _WBNBToken,
        IStableMMP _mmUSD,
        IPancakeRouter _PancakeRouterAddress
    ) public initializer {
        setMintFeeRate(_mintFeeRate);
        setRedeemFeeRate(_redeemFeeRate);

        maxFeeTxAmount = 400000 * digit;
        feeTokenAddress = _FeeToken;
        stableTokenAddress = _StableToken;
        wbnbAddress = _WBNBToken;
        FeeToken = IERC20(_FeeToken);
        StableToken = IERC20(_StableToken);
        mmUSD = IStableMMP(_mmUSD);
        PancakeRouter = IPancakeRouter(_PancakeRouterAddress);

        if (address(wbnbAddress) == address(0)) {
            stableOutputPath = [feeTokenAddress, stableTokenAddress];
            stableInputPath = [stableTokenAddress, feeTokenAddress];
        } else {
            stableOutputPath = [feeTokenAddress, wbnbAddress, stableTokenAddress];
            stableInputPath = [stableTokenAddress, wbnbAddress, feeTokenAddress];
        }
    }

    function getFee(uint256 amount) private view returns(uint256) {
        uint256[] memory amounts = PancakeRouter.getAmountsIn(amount, stableOutputPath);
        return amounts[0];
    }

    function getMintFee(uint256 amount) public view returns(uint256 stableFee, uint256 fee) {
        stableFee = mintFeeRate.mul(amount).div(digit);
        fee = getFee(stableFee);
    }

    function getRedeemFee(uint256 amount) public view returns(uint256 stableFee, uint256 fee) {
        stableFee = redeemFeeRate.mul(amount).div(digit);
        fee = getFee(stableFee);
    }

    function totalStable() public view returns(uint256) {
        return StableToken.balanceOf(address(this));
    }

    function totalFee() public view returns(uint256) {
        return FeeToken.balanceOf(address(this));
    }

    function setMintFeeRate(uint256 _mintFeeRate) public onlyOwner {
        mintFeeRate = _mintFeeRate;
    }

    function setRedeemFeeRate(uint256 _redeemFeeRate) public onlyOwner {
        redeemFeeRate = _redeemFeeRate;
    }

    function setMaxFeeTxAmount(uint256 amount) external onlyOwner {
        maxFeeTxAmount = amount;
    }

    function transferTokenOwnership(address _newOwner) external onlyOwner {
        mmUSD.transferOwnership(_newOwner);
    }

    function setPause(bool pause) external onlyOwner {
        if (pause) {
            _pause();
        } else {
            _unpause();
        }
    }

    function setStableOutputPath(address[] memory _path) external onlyOwner whenPaused {
        stableOutputPath = _path;
    }

    function setStableInputPath(address[] memory _path) external onlyOwner whenPaused {
        stableInputPath = _path;
    }

    function setFeeToken(address tokenAddress) external onlyOwner whenPaused {
        feeTokenAddress = tokenAddress;
        FeeToken = IERC20(tokenAddress);
    }

    function setStableToken(address tokenAddress) external onlyOwner whenPaused {
        stableTokenAddress = tokenAddress;
        StableToken = IERC20(tokenAddress);
    }

    function setRouterAddress(address routerAddress) external onlyOwner whenPaused {
        PancakeRouter = IPancakeRouter(routerAddress);
    }

    function OwnerWithdraw(address tokenAddress, uint256 amount) external onlyOwner whenNotPaused {
        IERC20 Token = IERC20(tokenAddress);
        require(Token.balanceOf(address(this)) >= amount, "Insufficient Token");

        Token.safeTransfer(address(msg.sender), amount);
        emit DevWithdraw(msg.sender, amount);
    }

    function mint(uint256 amount) external whenNotPaused {
        require(amount > 0, "Amount must be more than 0");
        require(StableToken.balanceOf(address(msg.sender)) >= amount, "Insufficient amount in your wallet");

        (, uint256 fee) = getMintFee(amount);
        require(FeeToken.balanceOf(address(msg.sender)) >= fee, "Insufficient fee in your wallet");
        require(maxFeeTxAmount == 0 || fee <= maxFeeTxAmount, "Exceed fee limit");

        StableToken.safeTransferFrom(address(msg.sender), address(this), amount);
        FeeToken.safeTransferFrom(address(msg.sender), address(this), fee);
        mmUSD.mint(address(msg.sender), amount);

        totalMintFee = totalMintFee.add(fee);
        swapHistory[msg.sender].push(SwapLog(SwapType.mint, amount, fee));

        emit Mint(msg.sender, amount, fee);
    }

    function mintFromOnlyStable(uint256 amount) external whenNotPaused {
        require(amount > 0, "Amount must be more than 0");
        require(StableToken.balanceOf(address(msg.sender)) >= amount, "Insufficient amount in your wallet");

        (uint256 stableFee, uint256 approxFee) = getMintFee(amount);
        require(maxFeeTxAmount == 0 || approxFee <= maxFeeTxAmount, "Exceed fee limit");

        uint256 availableAmount = amount.sub(stableFee);
        uint256 minAmountOut = 0;
        uint256 deadline = block.timestamp + 20 minutes;
        uint256 beforeSwapFee = totalFee();

        StableToken.safeTransferFrom(address(msg.sender), address(this), amount);
        StableToken.approve(address(PancakeRouter), stableFee);
        PancakeRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            stableFee,
            minAmountOut,
            stableInputPath,
            address(this),
            deadline
        );
        mmUSD.mint(address(msg.sender), availableAmount);

        uint256 fee = totalFee() - beforeSwapFee;
        totalMintFee = totalMintFee.add(fee);
        swapHistory[msg.sender].push(SwapLog(SwapType.mint, availableAmount, fee));

        emit Mint(msg.sender, availableAmount, fee);
    }

    function redeem(uint256 amount) external whenNotPaused {
        require(amount > 0, "Amount must be more than 0");
        require(mmUSD.balanceOf(address(msg.sender)) >= amount, "Insufficient amount in your wallet");

        (, uint256 fee) = getRedeemFee(amount);
        require(FeeToken.balanceOf(address(msg.sender)) >= fee, "Insufficient fee in your wallet");
        require(maxFeeTxAmount == 0 || fee <= maxFeeTxAmount, "Exceed fee limit");

        StableToken.safeTransfer(address(msg.sender), amount);
        FeeToken.safeTransferFrom(address(msg.sender), address(this), fee);
        mmUSD.burnFrom(address(msg.sender), amount);

        totalRedeemFee = totalRedeemFee.add(fee);
        swapHistory[msg.sender].push(SwapLog(SwapType.redeem, amount, fee));

        emit Redeem(msg.sender, amount, fee);
    }

    function redeemFromOnlyStable(uint256 amount) external whenNotPaused {
        require(amount > 0, "Amount must be more than 0");
        require(mmUSD.balanceOf(address(msg.sender)) >= amount, "Insufficient amount in your wallet");

        (uint256 stableFee, uint256 approxFee) = getRedeemFee(amount);
        require(maxFeeTxAmount == 0 || approxFee <= maxFeeTxAmount, "Exceed fee limit");

        uint256 availableAmount = amount.sub(stableFee);
        uint256 minAmountOut = 0;
        uint256 deadline = block.timestamp + 20 minutes;
        uint256 beforeSwapFee = totalFee();

        StableToken.approve(address(PancakeRouter), stableFee);
        PancakeRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            stableFee,
            minAmountOut,
            stableInputPath,
            address(this),
            deadline
        );
        mmUSD.burnFrom(address(msg.sender), amount);
        StableToken.safeTransfer(address(msg.sender), availableAmount);

        uint256 fee = totalFee() - beforeSwapFee;
        totalRedeemFee = totalRedeemFee.add(fee);
        swapHistory[msg.sender].push(SwapLog(SwapType.redeem, availableAmount, fee));

        emit Redeem(msg.sender, availableAmount, fee);
    }
}
