contract LakeViewMeta is ERC20, Ownable {

    using SafeMath for uint256;

    IUniswapV2Router02 public pcsV2Router;
    address public immutable pcsV2BNBPair;
    address public immutable pcsV2BUSDPair;
    address public constant deadAddress = address(0xdead);

    struct Fee {
        uint16 marketingFee;
        uint16 liquidityFee;
        uint16 burnFee;
    }

    bool private swapping;

    Fee public buyFee;
    Fee public sellFee;

    uint256 public swapTokensAtAmount;
    uint256 public maxBuyAmount;
    uint256 public maxSellAmount;
    uint256 public maxWalletAmount;

    uint16 private totalBuyFee;
    uint16 private totalSellFee;

    bool public swapEnabled;
    bool public isTradingEnabled;
    bool public isLiqInBUSD;

    address payable public _marketingWallet = payable(address(0x123));
    address private constant BUSD = 0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56; //0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56;

    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) private _isExcludedFromLimit;
    

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping(address => bool) public automatedMarketMakerPairs;

    event UpdateRouter(address indexed newAddress, address indexed oldAddress);

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 coinReceived,
        uint256 tokensIntoLiqudity
    );

    modifier lockTheSwap() {
        swapping = true;
        _;
        swapping = false;
    }

    constructor() ERC20("LakeViewMeta", "LVM") {
        buyFee.marketingFee = 40;
        buyFee.liquidityFee = 20;
        buyFee.burnFee = 10;
        totalBuyFee = 70;

        sellFee.marketingFee = 40;
        sellFee.liquidityFee = 20;
        sellFee.burnFee = 10;
        totalSellFee = 70;

        _marketingWallet = payable(address(0x123));
IUniswapV2Router02 _pcsV2Router = IUniswapV2Router02(
            //0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );
        // Create a BNB pair for this new token
        address _pcsV2BNBPair = IUniswapV2Factory(_pcsV2Router.factory())
            .createPair(address(this), _pcsV2Router.WETH());

        address _pcsV2BUSDPair = IUniswapV2Factory(_pcsV2Router.factory())
            .createPair(address(this), BUSD);


        pcsV2Router = _pcsV2Router;
        pcsV2BNBPair = _pcsV2BNBPair;
        pcsV2BUSDPair = _pcsV2BUSDPair;

        automatedMarketMakerPairs[_pcsV2BNBPair] = true;
        automatedMarketMakerPairs[_pcsV2BUSDPair] = true;

        // Exclude from paying fees or having max transaction amount
        excludeFromFees(owner(), true);
        excludeFromFees(address(this), true);

        swapEnabled = true;

        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(owner(), 1 * 10**9 * (10**9));
        _approve(address(this), address(pcsV2Router), type(uint256).max);

        maxWalletAmount = totalSupply().mul(1).div(100);
        maxBuyAmount = totalSupply().mul(5).div(1000);
        maxSellAmount = totalSupply().mul(5).div(1000);
        swapTokensAtAmount = totalSupply().mul(5).div(10000);
    }

    function rescueContractBNB() external onlyOwner {
        (bool success,) = address(msg.sender).call{value: address(this).balance}("Completed");
        require(success, "Failed. Check addresses.");
    }

    receive() external payable {}

    function decimals() public pure override returns (uint8) {
        return 9;
    }

    function enableTrading() external onlyOwner {
        isTradingEnabled = true;
    }

    function updateRouter(address newAddress) public onlyOwner {
        require(
            newAddress != address(pcsV2Router),
            "Token: The router already has that address"
        );
        emit UpdateRouter(newAddress, address(pcsV2Router));
        pcsV2Router = IUniswapV2Router02(newAddress);
        _approve(address(this), address(pcsV2Router), type(uint256).max);
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(
            _isExcludedFromFees[account] != excluded,
            "Token: Account is already the value of 'excluded'"
        );
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function claimStuckTokens(address _token) external onlyOwner {
        require(_token != address(this), "No rugs");
        if (_token == address(0x0)) {
            payable(owner()).transfer(address(this).balance);
            return;
        }

        
        IERC20 erc20token = IERC20(_token);
        uint256 balance = erc20token.balanceOf(address(this));
        erc20token.transfer(owner(), balance);
    }

    function excludefromLimit(address account, bool excluded)
        external
        onlyOwner
    {
        _isExcludedFromLimit[account] = excluded;
    }


    function excludeMultipleAccountsFromFees(
        address[] calldata accounts,
        bool excluded
    ) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function setAutomatedMarketMakerPair(address pair, bool value)
        public
        onlyOwner
    {
        require(
            pair != pcsV2BNBPair && pair != pcsV2BUSDPair,
            "Token: The PancakeSwap pair cannot be removed"
        );

        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }
function setBuyFee(
        uint16 marketing,
        uint16 liquidity,
        uint16 burn
    ) external onlyOwner {
        buyFee.marketingFee = marketing;
        buyFee.liquidityFee = liquidity;
        buyFee.burnFee = burn;
        totalBuyFee = burn + marketing + liquidity;
    }

    function setSellFee(
        uint16 marketing,
        uint16 liquidity,
        uint16 burn
    ) external onlyOwner {
        sellFee.marketingFee = marketing;
        sellFee.liquidityFee = liquidity;
        sellFee.burnFee = burn;
        totalSellFee = marketing + liquidity + burn;
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function setMarketingWallet(address newWallet) external onlyOwner {
        _marketingWallet = payable(newWallet);
    }

    function setSwapEnabled(bool value) external onlyOwner {
        swapEnabled = value;
    }

    function setswapTokensAtAmount(uint256 value) external onlyOwner {
        swapTokensAtAmount = value;
    }

    function setMaxWallet(uint256 value) external onlyOwner {
    maxWalletAmount = value;
    }

    function setMaxBuyAmount(uint256 value) external onlyOwner {
        maxBuyAmount = value;
    }

    function setMaxSellAmount(uint256 value) external onlyOwner {
        maxSellAmount = value;
    }

    function setIsLiqInBUSD(bool value) external onlyOwner {
        isLiqInBUSD = value;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        super._beforeTokenTransfer(from, to, amount);

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        uint256 contractTokenBalance = balanceOf(address(this));
        bool overMinimumTokenBalance = contractTokenBalance >=
            swapTokensAtAmount;

        if (
            swapEnabled &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            overMinimumTokenBalance
        ) {
            contractTokenBalance = swapTokensAtAmount;
            uint16 totalFee = totalBuyFee + totalSellFee;

            uint256 swapTokens = contractTokenBalance
                .mul(buyFee.liquidityFee + sellFee.liquidityFee)
                .div(totalFee);
            swapAndLiquify(swapTokens);

            uint256 marketingTokens = contractTokenBalance
                .mul(buyFee.marketingFee + sellFee.marketingFee)
                .div(totalFee);
            swapAndSendToMarketing(marketingTokens);

            uint256 burnTokens = contractTokenBalance
                .mul(sellFee.burnFee + buyFee.burnFee)
                .div(totalFee);
            super._transfer(address(this), deadAddress, burnTokens);
        }

        bool takeFee = true;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 fees;

            require(isTradingEnabled, "Trading not enabled yet");

            if (automatedMarketMakerPairs[to]) {
                fees = totalSellFee;
                if (!_isExcludedFromLimit[from]) {
                    require(amount <= maxSellAmount, "Sell exceeds limit");
                    require(balanceOf(to) + amount <= maxWalletAmount,"Balance exceeds limit");
                }
            } else if (automatedMarketMakerPairs[from]) {
                fees = totalBuyFee;
                if (!_isExcludedFromLimit[to]) {
                    require(amount <= maxBuyAmount, "Buy exceeds limit");
                    require(balanceOf(to) + amount <= maxWalletAmount,"Balance exceeds limit");
                }
            }

            if (!automatedMarketMakerPairs[to] && _isExcludedFromLimit[to]) {
                require(balanceOf(to) + amount <= maxWalletAmount,"Balance exceeds limit");
            }
uint256 feeAmount = amount.mul(fees).div(1000);
            amount = amount.sub(feeAmount);

            super._transfer(from, address(this), feeAmount);
        }

        super._transfer(from, to, amount);
    }

    function swapAndSendToMarketing(uint256 tokens) private lockTheSwap {
        uint256 initialBalance = address(this).balance;

        swapTokensForBNB(tokens);
        // Swapping tokens for BNB
        uint256 newBalance = address(this).balance.sub(initialBalance);
        _marketingWallet.transfer(newBalance);
    }

    function swapAndLiquify(uint256 tokens) private lockTheSwap {
        // Split the contract balance into halves
        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);
        uint256 initialBalance;
        uint256 newBalance;

        if (!isLiqInBUSD) {
            initialBalance = address(this).balance;
            // swap tokens for ETH
            swapTokensForBNB(half);
            // how much ETH did we just swap into?
            newBalance = address(this).balance.sub(initialBalance);
            // add liquidity to uniswap
            addLiquidityBNB(otherHalf, newBalance);
        } else {
            initialBalance = IERC20(BUSD).balanceOf(address(this));

            swapTokensForBUSD(half);

            newBalance = IERC20(BUSD).balanceOf(address(this)).sub(
                initialBalance
            );

            addLiquidityBUSD(otherHalf, newBalance);
        }
    }

    function swapTokensForBNB(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = pcsV2Router.WETH();

        // make the swap
        pcsV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp + 60
        );
    }

    function swapTokensForBUSD(uint256 tokenAmount) private {
        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = pcsV2Router.WETH();
        path[2] = BUSD;

        // make the swap
        pcsV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of BUSD
            path,
            address(this),
            block.timestamp + 60
        );
    }

    function addLiquidityBNB(uint256 tokenAmount, uint256 ethAmount) private {

        // add the liquidity
        pcsV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0),
            block.timestamp
        );
    }

    function addLiquidityBUSD(uint256 tokenAmount, uint256 busdAmount) private {
        // approve token transfer to cover all possible scenarios
        IERC20(BUSD).approve(address(pcsV2Router), busdAmount);

        bool isThisToken0 = address(this) < BUSD;
        (address tokenA, address tokenB) = isThisToken0
            ? (address(this), BUSD)
            : (BUSD, address(this));
        (uint256 amountA, uint256 amountB) = isThisToken0
            ? (tokenAmount, busdAmount)
            : (busdAmount, tokenAmount);

        // add the liquidity
        pcsV2Router.addLiquidity(
            tokenA,
            tokenB,
            amountA,
            amountB,
            0,
            0,
            deadAddress,
            block.timestamp + 60
        );
    }
}
