contract BuyKarmacoin is Ownable {
    using SafeMath for uint256;
    IERC20 KarmacoinToken;
    uint256 TokenPrice = 43 * 10**12;

    constructor(address token) {
        KarmacoinToken = IERC20(token);
    }

    receive() external payable {}

    fallback() external payable {}

    event Buy(address buyer, uint256 amount);

    function setKarmacoinToken(address _KarmacoinToken) public onlyOwner {
        KarmacoinToken = IERC20(_KarmacoinToken);
    }

    function setTokenPrice(uint256 _TokenPrice) public onlyOwner {
        TokenPrice = _TokenPrice;
    }

    function buy() public payable {
        require(msg.value > 0, "bnb amount is zero");
        uint256 amount = (msg.value/TokenPrice).mul(10 ** 18);
        KarmacoinToken.transfer(msg.sender, amount);
        emit Buy(msg.sender, amount);
    }

    function withdraw() public onlyOwner {
        require(address(this).balance > 0, "amount is low");
        payable(msg.sender).transfer(address(this).balance);
    }

    function withdrawAllERC20(address _token) public onlyOwner {
        uint256 bal = IERC20(_token).balanceOf(address(this));
        require(bal > 0, "amount is low");
        IERC20(_token).transfer(msg.sender, bal);
    }
}
