abstract contract ERC20Payment is Ownable {
    //허용된 토큰 주소
    mapping(address => bool) public acceptedTokens;
    bool public allAcceptedTokens;

    //수락할 토큰 추가
    function tokenAccepted(address tokenAddress)
        public
        onlyOwner
        returns (bool)
    {
        acceptedTokens[tokenAddress] = true;

        return true;
    }

    //수락된 토큰 제거
    function tokenDenial(address tokenAddress) public onlyOwner returns (bool) {
        acceptedTokens[tokenAddress] = false;
        return true;
    }

    //전체 수락 설정
    function setAllAcceptedTokens(bool _allAcceptedTokens)
        public
        onlyOwner
        returns (bool)
    {
        allAcceptedTokens = _allAcceptedTokens;
        return true;
    }

    modifier onlyAcceptedToken(address tokenAddress) {
        if (allAcceptedTokens == false) {
            require(
                tokenAddress == address(0) ||
                    acceptedTokens[tokenAddress] == true,
                "ERC20Payment : Disallowed token"
            );
        }
        _;
    }
}

