//수수료 기능 , 소유자 기능등 추가
contract MultiNftMarket is ERC1155, TransferFees, ERC20Payment {
    string private name_;
    string private symbol_;
    uint256 maxRoyaltyFee; // Base 1000
    bool canUpdateRoyaltyMember;


    struct SaleInfo {
        bool isForSale;
        uint256 amount;
        uint256 price;
        address tokenAddress;
    }

    struct RoyaltyMember {
        address member;
        uint256 royalty;
    }

    mapping(uint256 => mapping(address => SaleInfo)) public saleMap;
    mapping(uint256 => address) public creators; // TokenId => Owner's address
    mapping(uint256 => RoyaltyMember[]) public royaltyMembers;

    event Minted(
        address indexed minter,
        uint256 price,
        uint256 nftID,
        uint256 amount,
        string uri
    );
    event Buy(uint256 tokenId, address indexed seller, uint256 amount);
    event UpdateSale(
        address indexed seller,
        bool isForSale,
        uint256 tokenId,
        uint256 amount,
        uint256 price
    );
    event NewCreator(address owner, uint256 tokenId);

    constructor(string memory _baseURI) ERC1155(_baseURI) TransferFees(1) {
        name_ = "Snailhouse";
        symbol_ = "Snailhouse";
        maxRoyaltyFee = 300;
        canUpdateRoyaltyMember = false;
    }

    function updateRoyaltyMembers(
        RoyaltyMember[] memory members,
        uint256 _tokenId
    ) public {
        require(canUpdateRoyaltyMember, "Update royalty member is disabled");
        require(creators[_tokenId] == msg.sender, "Not creator");
        saveRoyaltyMember(members, _tokenId);
    }

    function setMaxRoyaltyFee(uint256 _maxRoyaltyFee) public onlyOwner {
        maxRoyaltyFee = _maxRoyaltyFee;
    }

    //Read
    function name() public view virtual returns (string memory) {
        return name_;
    }

    //Read
    function symbol() public view virtual returns (string memory) {
        return symbol_;
    }

    /** Write
     * @dev  See {IERC1155-safeTransferFrom}.
     */
    function safeTransferFrom(
        address _from,
        address _to,
        uint256 _tokenId,
        uint256 _amount,
        bytes memory _data
    ) public virtual override {
        require(
            _from == _msgSender() || isApprovedForAll(_from, _msgSender()),
            "ERC1155: caller is not owner nor approved"
        );

        require(transferBalance(_tokenId, _from) >= _amount);

        _safeTransferFrom(_from, _to, _tokenId, _amount, _data);
    }

    /** Write
     * @dev  See {IERC1155-safeBatchTransferFrom}.
     */
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory _tokenIds,
        uint256[] memory amounts,
        bytes memory data
    ) public virtual override {
        require(
            from == _msgSender() || isApprovedForAll(from, _msgSender()),
            "ERC1155: transfer caller is not owner nor approved"
        );
        require(
            _tokenIds.length == amounts.length,
            "ERC1155: ids and amounts length mismatch"
        );

        for (uint256 i = 0; i < amounts.length; ++i) {
            require(transferBalance(_tokenIds[i], from) >= amounts[i]);
        }

        _safeBatchTransferFrom(from, to, _tokenIds, amounts, data);
    }

    //거래 가능한 잔액 Read
    function transferBalance(uint256 _tokenId, address _from)
        public
        view
        virtual
        returns (uint256)
    {
        SaleInfo storage sale = saleMap[_tokenId][_from];
        uint256 fromBalance = _balances[_tokenId][_from];

        if (sale.isForSale == false) {
            return fromBalance;
        }

        return fromBalance - sale.amount;
    }

    function validateRoyaltyFee(RoyaltyMember[] memory members) public view {
        uint256 totalRoyalty = 0;
        for (uint256 i = 0; i < members.length; i++) {
            totalRoyalty += members[i].royalty;
        }

        require(totalRoyalty <= maxRoyaltyFee, "Total Royalty is gt Max");
    }

    function saveRoyaltyMember(RoyaltyMember[] memory members, uint256 _id)
        internal
    {
        for (uint256 i = 0; i < members.length; i++) {
            royaltyMembers[_id].push(members[i]);
        }
    }

    function saleMint(
        address _tokenAddress,
        string memory _tokenURI,
        uint256 _amount,
        uint256 _price,
        RoyaltyMember[] memory members
    ) public onlyAcceptedToken(_tokenAddress) returns (uint256) {
        validateRoyaltyFee(members);
        uint256 _tokenId = totalCreator() + 1;

        _mint(_msgSender(), _tokenId, _amount, "");
        _setTokenURI(_tokenId, _tokenURI);

        saleMap[_tokenId][_msgSender()] = SaleInfo(
            true,
            _amount,
            _price,
            _tokenAddress
        );

        creators[_tokenId] = msg.sender;
        if (members.length > 0) {
            saveRoyaltyMember(members, _tokenId);
        }

        emit NewCreator(msg.sender, _tokenId);
        emit Minted(_msgSender(), _price, _tokenId, _amount, _tokenURI);
        emit UpdateSale(_msgSender(), true, _tokenId, _amount, _price);
        return _tokenId;
    }

    //즉시 구매 Write
    function buy(
        uint256 _tokenId,
        address _seller,
        uint256 _amount
    ) external payable {
        SaleInfo storage sale = saleMap[_tokenId][_seller];

        require(_exists(_tokenId)); //발행된 토큰인지
        require(_msgSender() != _seller); //판매자와 구매자가 동일 주소인지
        require(sale.isForSale == true && sale.amount >= _amount); //판매중인 수량이 구매 수량을 초과하는지
        if (sale.tokenAddress == address(0)) {
            require((_amount * sale.price) >= msg.value); //전송된 금액이 총판매액 인지
        }

        _trade(_tokenId, _seller, _amount);
    }

    function calculateRoyaltyValue(
        uint256 _tokenId,
        address _seller,
        uint256 _amount
    ) public view returns (uint256) {
        uint256 royaltyValue = 0;
        SaleInfo storage sale = saleMap[_tokenId][_seller];
        RoyaltyMember[] storage members = royaltyMembers[_tokenId];

        for (uint256 i = 0; i < members.length; i++) {
            uint256 royalty = members[i].royalty;
            uint256 memberRoyalty = ((_amount * sale.price) * royalty) / 1000;
            royaltyValue += memberRoyalty;
        }

        return royaltyValue;
    }

    function transferNativeRoyaltyValue(
        uint256 _tokenId,
        address _seller,
        uint256 _amount
    ) internal {
        RoyaltyMember[] storage members = royaltyMembers[_tokenId];
        SaleInfo storage sale = saleMap[_tokenId][_seller];

        for (uint256 i = 0; i < members.length; i++) {
            address member = members[i].member;
            uint256 royalty = members[i].royalty;
            uint256 memberRoyalty = ((_amount * sale.price) * royalty) / 1000;

            payable(member).transfer(memberRoyalty);
        }
    }

    function transferTokenRoyaltyValue(
        uint256 _tokenId,
        address _seller,
        uint256 _amount
    ) internal {
        RoyaltyMember[] storage members = royaltyMembers[_tokenId];
        SaleInfo storage sale = saleMap[_tokenId][_seller];

        for (uint256 i = 0; i < members.length; i++) {
            address member = members[i].member;
            uint256 royalty = members[i].royalty;
            uint256 memberRoyalty = ((_amount * sale.price) * royalty) / 1000;

            TransferHelper.safeTransferFrom(
                sale.tokenAddress,
                msg.sender,
                member,
                memberRoyalty
            );
        }
    }

    function _trade(
        uint256 _tokenId,
        address _seller,
        uint256 _amount
    ) internal {
        address payable contractOwner = payable(_owner);
        address payable _buyer = payable(_msgSender());
        address payable sellerAddress = payable(_seller);

        SaleInfo storage sale = saleMap[_tokenId][_seller];

        _safeTransferFrom(_seller, _msgSender(), _tokenId, _amount, "");

        // Commission for market
        uint256 _commissionValue = ((_amount * sale.price) * feeRate()) / 1000;

        // Royalty fee
        uint256 royaltyValue = calculateRoyaltyValue(
            _tokenId,
            _seller,
            _amount
        );

        uint256 _sellerValue = (_amount * sale.price) -
            _commissionValue -
            royaltyValue;

        if (sale.tokenAddress == address(0)) {
            sellerAddress.transfer(_sellerValue);

            contractOwner.transfer(_commissionValue);

            if (royaltyValue > 0) {
                transferNativeRoyaltyValue(_tokenId, _seller, _amount);
            }

            // If buyer sent more than price, we send them back their rest of funds
            if ((_amount * sale.price) > msg.value) {
                _buyer.transfer(msg.value - (_amount * sale.price));
            }
        } else {
            TransferHelper.safeTransferFrom(
                sale.tokenAddress,
                msg.sender,
                sellerAddress,
                _sellerValue
            );

            TransferHelper.safeTransferFrom(
                sale.tokenAddress,
                msg.sender,
                contractOwner,
                _commissionValue
            );

            if (royaltyValue > 0) {
                transferTokenRoyaltyValue(_tokenId, _seller, _amount);
            }
        }

        if ((sale.amount - _amount) <= 0) {
            saleMap[_tokenId][_seller].amount = 0;
            saleMap[_tokenId][_seller].isForSale = false;
        } else {
            saleMap[_tokenId][_seller].amount = sale.amount - _amount; //판매중인 수량 조절
        }

        emit Buy(_tokenId, _seller, sale.amount);
        emit UpdateSale(
            _seller,
            saleMap[_tokenId][_seller].isForSale,
            _tokenId,
            saleMap[_tokenId][_seller].amount,
            sale.price
        );
    }

    //판매 상태 변경 Write
    function updateSale(
        address _tokenAddress,
        uint256 _tokenId,
        bool _isForSale,
        uint256 _amount,
        uint256 _price
    ) public onlyAcceptedToken(_tokenAddress) returns (bool) {
        require(_exists(_tokenId)); //발행된 토큰인지

        SaleInfo storage sale = saleMap[_tokenId][_msgSender()];

        if (_isForSale == true) {
            require(balanceOf(_msgSender(), _tokenId) >= _amount); //판매할 수량이 소유중인 개수 초과할수 없음

            sale.isForSale = true;
            sale.amount = _amount;
            sale.price = _price;
            sale.tokenAddress = _tokenAddress;
        } else {
            sale.isForSale = false;
        }

        saleMap[_tokenId][_msgSender()] = sale;
        emit UpdateSale(_msgSender(), _isForSale, _tokenId, _amount, _price);
        return true;
    }

    //소각 Write
    function burn(
        address _owner,
        uint256 _tokenId,
        uint256 _amount
    ) public returns (bool) {
        require(
            _owner == _msgSender() ||
                isApprovedForAll(_owner, _msgSender()) == true,
            "Need operator approval for 3rd party burns."
        );
        require(transferBalance(_tokenId, _owner) >= _amount);

        _burn(_owner, _tokenId, _amount);

        return true;
    }
}
