contract SwapToken is Ownable {
    using SafeMath for uint256;
    IERC20 public tokenA;
    IERC20 public tokenB;
    uint256 public rate = 60; 
    uint256 public refrate = 0;
    string private _name;
    string private tokenB_name;
    uint256 public pointRate = 1;
    address public refaddr;
    constructor(
        string memory _tokenA_name,
        address _tokenA_address,
        string memory _tokenB_name,
        address _tokenB_address,
        address _refaddr
    ) public {
        _name = string(abi.encodePacked("Swap", " ", _tokenA_name, ":", _tokenB_name)); 
        tokenA = IERC20(_tokenA_address);
        tokenB = IERC20(_tokenB_address);
        tokenB_name = _tokenB_name;
        refaddr = _refaddr;
    }

    function swap(uint256 _amount) public {
        require(_amount > 1*1e18, "amount to small");
        require(_amount == rate.mul(1e18), "amount mistake");
	
		
		uint256 _payback = _amount.mul(pointRate).div(rate);
		uint256 _payref = _amount.mul(refrate).div(100);


		// contract must have funds to keep this commitment
		require(tokenB.balanceOf(address(this)) > _payback, "insufficient contract bal");

		require(tokenA.transferFrom(msg.sender, address(this), _amount), "transfer failed");
		require(tokenB.transfer(msg.sender, _payback), "transfer failed");
		if (refaddr != address(0)) {
		    require(tokenA.transfer(refaddr, _payref), "transfer failed");
		 }
	
    }

    function setPointRate(uint256 _pointRate) public onlyOwner {
        pointRate = _pointRate;
    }

    function setRefAddr(address _refAddr) public onlyOwner {
        refaddr = _refAddr;
    }

    function setRate(uint256 _rate) public onlyOwner {
        rate = _rate;
    }

    function setrefRate(uint256 _refrate) public onlyOwner {
        refrate = _refrate;
    }
 
    function aBalance() public view returns (uint256) {
        return tokenA.balanceOf(address(this));
    }

    function bBalance() public view returns (uint256) {
        return tokenB.balanceOf(address(this));
    }

    function ownerReclaim() public onlyOwner {
        tokenA.transfer(msg.sender, tokenA.balanceOf(address(this)));
    }

    function flushBNB() public onlyOwner {
        uint256 bal = address(this).balance.sub(1);
        msg.sender.transfer(bal);
    }

    function setTokenA(string memory _tokenA_name, address _tokenA_address) public onlyOwner {
        tokenA = IERC20(_tokenA_address);
        _name = string(abi.encodePacked("Swap", " ", _tokenA_name, ":", tokenB_name));
    }

}
