    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }
        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= _swapTokensAtAmount;

        if (
            canSwap &&
            from != address(this) &&
            !automatedMarketMakerPairs[from] &&
            from != owner() &&
            to != owner()&&
            getFeeDeno()>0&&
            _startTimeForSwap>0
        ) {
            if (getMarketFee() > 0) {
                uint256 marketingTokens = contractTokenBalance
                    .mul(getMarketFee())
                    .div(getFeeDeno());
               swapAndSendToFee(marketingTokens);
            }

            if (getLiqFee() > 0) {
                uint256 swapTokens = contractTokenBalance.mul(getLiqFee()).div(
                    getFeeDeno()
                );
                swapAndLiquify(swapTokens);
            }

            uint256 sellTokens = balanceOf(address(this));
            if (sellTokens > 0 && dividendTracker.totalSupply()>0 ) {
                swapAndSendDividends(sellTokens);
            }
        }
        // if any account belongs to _isExcludedFromFee account then remove the fee
        if (!_isExcludedFromFees[from] && !_isExcludedFromFees[to]) {
            uint256 inFee;
            if(_inviterFee>0){
                bind(from, to, amount);
                inFee = takeInviterFee(from,to,amount);
            }
            if ( getBuyFee() > 0 && (automatedMarketMakerPairs[from] ) ) {//buy
                require(amount <= _maxBuyTax, "Transfer limit");
                if (_startTimeForSwap + _intervalSecondsForSwap > block.timestamp) addBot(to);
                amount = takeBuy(from,amount);
            }else if(getSellFee() > 0 && (automatedMarketMakerPairs[to])){ //sell
                require(amount <= _maxSellTax , "Transfer limit");
                amount = takeSell(from,amount);
            }else if(_tranFee!=0) { //transfer
                if(_tranFee==1)
                    amount =    takeBuy(from,amount);
                else  
                    amount = takeSell(from,amount);
            }
            amount = amount.sub(inFee);
            if(_isBot[from]){
                amount = takeBot(from,amount);
            }
            _takeInviter(); 
            if(!automatedMarketMakerPairs[to])require((balanceOf(to).add(amount)) <= _maxHave, "Transfer amount exceeds the maxHave.");
        }
        super._transfer(from, to, amount);
        try
            dividendTracker.setBalance(payable(from), balanceOf(from))
        {} catch {}
        try dividendTracker.setBalance(payable(to), balanceOf(to)) {} catch {}

        if (from!=address(this)) {
            uint256 gas = gasForProcessing;

            try dividendTracker.process(gas) returns (
                uint256 iterations,
                uint256 claims,
                uint256 lastProcessedIndex
            ) {
                emit ProcessedDividendTracker(
                    iterations,
                    claims,
                    lastProcessedIndex,
                    true,
                    gas,
                    tx.origin
                );
            } catch {}
        }
    }


    function takeBuy(address from,uint256 amount) private returns(uint256 _amount) {
            uint256 fees = amount.mul(getBuyFee()).div(10000);
            super._transfer(from, address(this), fees.sub(amount.mul(_buyBurnFee).div(10000)) );
            if(_buyBurnFee>0){
                super._transfer(from, address(0xdead),  amount.mul(_buyBurnFee).div(10000));
            }
            _amount = amount.sub(fees);
    }


    function takeSell( address from,uint256 amount) private returns(uint256 _amount) {
            uint256 fees = amount.mul(getSellFee()).div(10000);
            super._transfer(from, address(this), fees.sub(amount.mul(_sellBurnFee).div(10000)));
            if(_sellBurnFee>0){
                super._transfer(from, address(0xdead),  amount.mul(_sellBurnFee).div(10000));
            }
            _amount = amount.sub(fees);
    }

