// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

contract Token {
    mapping(address => uint256) public balances;
    mapping(address => mapping(address => uint256)) public allowance;
    uint256 public totalSupply = 777555000 * 10**18;
    string public name = "Open Board";
    string public symbol = "OPBO";
    uint256 public decimals = 18;
    uint256 public taxRate = 2; // Add a tax rate of 2%, you can change this as needed

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
    event Burn(address indexed from, uint256 value);
    event TaxPaid(address indexed from, uint256 value); // Add a new 'TaxPaid' event

    constructor() {
        balances[msg.sender] = totalSupply;
    }

    function balanceOf(address owner) public view returns (uint256) {
        return balances[owner];
    }

    function transfer(address to, uint256 value) public returns (bool) {
        require(balanceOf(msg.sender) >= value, "balance too low");

        uint256 taxAmount = (value * taxRate) / 100;
        uint256 transferAmount = value - taxAmount;

        balances[to] += transferAmount;
        balances[msg.sender] -= value;
        emit Transfer(msg.sender, to, transferAmount);

        if (taxAmount > 0) {
            balances[address(this)] += taxAmount; // Send the tax to a specific address or contract
            emit TaxPaid(msg.sender, taxAmount);
        }

        return true;
    }

    // Rest of the functions remain the same...

    function burn(uint256 value) public returns (bool) {
        require(balanceOf(msg.sender) >= value, "balance too low");
        balances[msg.sender] -= value;
        totalSupply -= value;
        emit Burn(msg.sender, value);
        return true;
    }
}
