pragma solidity 0.8.18;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

// import "hardhat/console.sol";

interface IAirdropNFT {
    function safeMint(address to) external;
}

interface IFaucet {
    function isNetPositive(address _addr) external view returns (bool);

    function userInfo(
        address _addr
    )
        external
        view
        returns (
            address upline,
            uint256 deposit_time,
            uint256 deposits,
            uint256 payouts,
            uint256 direct_bonus,
            uint256 match_bonus,
            uint256 last_airdrop
        );
}

contract AirdropMinter is Ownable, Pausable {
    using Address for address payable;

    IAirdropNFT public nftContract;
    uint256 public price = 0.01 ether;
    uint256 public refPercentage = 1000; //10%
    uint256 public launchDate = 1685563200;
    uint256 public minted;

    IFaucet public faucet;

    mapping(address => uint256) public refAccounts;

    constructor(IAirdropNFT _nftContract, IFaucet _faucet) {
        nftContract = _nftContract;
        faucet = _faucet;
    }

    /*** PUBLIC FUNCTIONS */
    function mint(address ref) external payable whenNotPaused {
        require(block.timestamp >= launchDate, "Minter: Not launched yet");
        require(msg.value >= price, "Minter: Invalid msg.value");
        require(ref != _msgSender(), "Minter: You cannot refer yourself");
        nftContract.safeMint(_msgSender());
        minted += 1;
        (, , uint256 airdrop) = getFaucetAccountStats(_msgSender());
        uint256 refRewards = calculateRefRewards(airdrop);
        refAccounts[ref] += refRewards;
    }

    /*** GETTERS */
    function getTotalMinted() external view returns (uint256) {
        return minted;
    }

    function getFaucetAccountStats(
        address _account
    )
        public
        view
        returns (uint256 deposits, bool netPositive, uint256 airdrop)
    {
        (, , deposits, , , , ) = faucet.userInfo(_account);
        netPositive = faucet.isNetPositive(_account);
        uint256 multiplier = netPositive ? 150 : 100;
        airdrop = (multiplier * deposits) / 100;
    }

    function calculateRefRewards(
        uint256 airdrop
    ) public view returns (uint256 refRewards) {
        refRewards = (airdrop * refPercentage) / 10000;
    }

    /*** ADMIN FUNCTIONS */
    function setNFTContract(address _nftContract) external onlyOwner {
        nftContract = IAirdropNFT(_nftContract);
    }

    function withdraw() external onlyOwner {
        payable(_msgSender()).sendValue(address(this).balance);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function setRefPercentage(uint256 _refPercentage) external onlyOwner {
        refPercentage = _refPercentage;
    }

    function setLaunchDate(uint256 _launchDate) external onlyOwner {
        launchDate = _launchDate;
    }
}
