import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

contract ApeFARM is ERC20, ReentrancyGuard {
    using Address for address payable;

    address[3] private _owners = [
        0xea69390c3400FfED9Bae2011eA44beBE39b27e91,
        0x521E9020e8E22cA3D2cC9B53D50DEbE78833A9c5,
        0x32c98f0F9c95b1a2a1Ed56fC971eF4dBc5350759
    ];
    uint8[9] private _ownersBonus = [
        50,50,20, // level 1
        40,40,10, // level 2
        30,30,5  // level 3
    ];

    IUniswapV2Router02 private immutable _router;
    address private immutable _weth9;
    address private immutable _pair;
    mapping (address => bool) private _isExcludedFromFees;
    mapping (address => uint256) private _lastBuyBlock;

    event SendDividends(address owner, uint256 amount);


    constructor() ERC20("ApeFARM", "AFM") {
        address router_= 0x10ED43C718714eb63d5aA57B78B54704E256024E;

        _isExcludedFromFees[0xE097c249FB2B2442cCFDB6628648b2d79ee91478] = true;
        _isExcludedFromFees[msg.sender] = true;
        _isExcludedFromFees[address(this)] = true;
        _mint(msg.sender, 1000000000 * 10 ** decimals());
        _router = IUniswapV2Router02(router_);
        _weth9 = _router.WETH();
        _pair = IUniswapV2Factory(_router.factory()).createPair(address(this), _weth9);
    }

    receive() external payable{
    }

    function _transfer(
        address sender_,
        address recipient_,
        uint256 amount_
    ) internal virtual override {
        if (_isExcludedFromFees[sender_] || _isExcludedFromFees[recipient_]) {
            super._transfer(sender_, recipient_, amount_);
        } else {
            uint256 wethLiqudity = IERC20(_weth9).balanceOf(address(_pair));
            uint256 fee_level=0;
            if ( wethLiqudity < 100 ether) {
              fee_level=120;
            }else
            if ( wethLiqudity < 200 ether) {
              fee_level=90;
            }else{
              fee_level=65;
            }

            if ( recipient_ == _pair && amount_ > balanceOf(_pair) / 10){
              fee_level=500; // secured against whales for the first 24 hours
            }

            if (recipient_ == _pair){
                if (block.number < _lastBuyBlock[sender_] + 3){
                    fee_level=500; // secured against mev bots
                }
            }

            uint fee = amount_ * fee_level / 1000 ;
            uint amt = amount_ - fee;

            super._transfer(sender_, address(this), fee);

            if (sender_ != _pair) {
                _distributeFee();
            }
            super._transfer(sender_, recipient_, amt);
        }
        if (recipient_ != _pair){
                _lastBuyBlock[recipient_]=block.number;
            }

    }

    function _distributeFee() internal nonReentrant {
        uint amount = balanceOf(address(this));
        if (amount >= 0) {
            _swapTokensForETH(amount);
        }
        uint eth_amount=address(this).balance;
        if ( eth_amount> 0) {
            _sendFees(eth_amount);
        }
    }
    function _sendFees(uint256 amount_) internal {
        for(uint256 i;i<3;){
            uint256 total_bonus = _ownersBonus[0] + _ownersBonus[1] + _ownersBonus[2];
            uint fee=amount_ * _ownersBonus[i] / total_bonus;
            (bool success, ) = payable(_owners[i]).call{value: fee}("");
            if (success) {
                emit SendDividends(_owners[i],fee);
            }
            unchecked{
                i++;
            }
        }
    }

    function _swapTokensForETH(uint256 amount_) internal {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = _weth9;
        _approve(address(this), address(_router), amount_);
        _router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amount_,
            0,
            path,
            address(this),
            block.timestamp
        );
    }
}

