// a library for handling binary fixed point numbers (https://en.wikipedia.org/wiki/Q_(number_format))
library FixedPoint {
    // range: [0, 2**112 - 1]
    // resolution: 1 / 2**112
    struct uq112x112 {
        uint224 _x;
    }

    // range: [0, 2**144 - 1]
    // resolution: 1 / 2**112
    struct uq144x112 {
        uint _x;
    }

    uint8 private constant RESOLUTION = 112;

    // encode a uint112 as a UQ112x112
    function encode(uint112 x) internal pure returns (uq112x112 memory) {
        return uq112x112(uint224(x) << RESOLUTION);
    }

    // encodes a uint144 as a UQ144x112
    function encode144(uint144 x) internal pure returns (uq144x112 memory) {
        return uq144x112(uint256(x) << RESOLUTION);
    }

    // divide a UQ112x112 by a uint112, returning a UQ112x112
    function div(uq112x112 memory self, uint112 x) internal pure returns (uq112x112 memory) {
        require(x != 0, 'FixedPoint: DIV_BY_ZERO');
        return uq112x112(self._x / uint224(x));
    }

    // multiply a UQ112x112 by a uint, returning a UQ144x112
    // reverts on overflow
    function mul(uq112x112 memory self, uint y) internal pure returns (uq144x112 memory) {
        uint z;
        require(y == 0 || (z = uint(self._x) * y) / y == uint(self._x), "FixedPoint: MULTIPLICATION_OVERFLOW");
        return uq144x112(z);
    }

    // returns a UQ112x112 which represents the ratio of the numerator to the denominator
    // equivalent to encode(numerator).div(denominator)
    function fraction(uint112 numerator, uint112 denominator) internal pure returns (uq112x112 memory) {
        require(denominator > 0, "FixedPoint: DIV_BY_ZERO");
        return uq112x112((uint224(numerator) << RESOLUTION) / denominator);
    }

    // decode a UQ112x112 into a uint112 by truncating after the radix point
    function decode(uq112x112 memory self) internal pure returns (uint112) {
        return uint112(self._x >> RESOLUTION);
    }

    // decode a UQ144x112 into a uint144 by truncating after the radix point
    function decode144(uq144x112 memory self) internal pure returns (uint144) {
        return uint144(self._x >> RESOLUTION);
    }
}

// library with helper methods for oracles that are concerned with computing average prices
library ThugSwapOracleLibrary {
    using FixedPoint for *;

    // helper function that returns the current block timestamp within the range of uint32, i.e. [0, 2**32 - 1]
    function currentBlockTimestamp() internal view returns (uint32) {
        return uint32(block.timestamp % 2 ** 32);
    }

    // produces the cumulative price using counterfactuals to save gas and avoid a call to sync.
    function currentCumulativePrices(
        address pair
    ) internal view returns (uint price0Cumulative, uint price1Cumulative, uint32 blockTimestamp) {
        blockTimestamp = currentBlockTimestamp();
        price0Cumulative = IThugSwapPair(pair).price0CumulativeLast();
        price1Cumulative = IThugSwapPair(pair).price1CumulativeLast();

        // if time has elapsed since the last update on the pair, mock the accumulated price values
        (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast) = IThugSwapPair(pair).getReserves();
        if (blockTimestampLast != blockTimestamp) {
            // subtraction overflow is desired
            uint32 timeElapsed = blockTimestamp - blockTimestampLast;
            // addition overflow is desired
            // counterfactual
            price0Cumulative += uint(FixedPoint.fraction(reserve1, reserve0)._x) * timeElapsed;
            // counterfactual
            price1Cumulative += uint(FixedPoint.fraction(reserve0, reserve1)._x) * timeElapsed;
        }
    }
}

library ThugSwapLibrary {
    using SafeMath for uint;
    // returns sorted token addresses, used to handle return values from pairs sorted in this order
    function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, 'ThugSwapLibrary: IDENTICAL_ADDRESSES');
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), 'ThugSwapLibrary: ZERO_ADDRESS');
    }

    // calculates the CREATE2 address for a pair without making any external calls
    function pairFor(address factory, address tokenA, address tokenB) internal pure returns (address pair) {
        (address token0, address token1) = sortTokens(tokenA, tokenB);
        pair = address(uint(keccak256(abi.encodePacked(
                hex'ff',
                factory,
                keccak256(abi.encodePacked(token0, token1)),
                hex'0b3961eeccfbf746d2d5c59ee3c8ae3a5dcf8dc9b0dfb6f89e1e8ca0b32b544b' // init code hash
            ))));
    }
}

contract HyperBurn is BEP20, Ownable, Pausable {
    using SafeMath for uint256;

    /// @dev thugSwap listing rate
    uint256 private constant INITIAL_TOKENS_PER_BNB = 50 * 1 ether;

    /// @dev max burn percentage
    uint256 private constant BURN_PCT = 33;

    /// @dev min burn percentage
    uint256 private constant MIN_BURN_PCT = 5;

    /// @dev reserve percentage of the burn percentage
    uint256 private constant BURN_RESERVE_PCT = 25;

    /// @dev redist pct
    uint256 private constant REDIST_PCT = 50;

    /// @notice WBNB token address
    address public constant WBNB = address(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);

    /// @notice self-explanatory
    address public constant thugSwapFactory = address(0xaC653cE27E04C6ac565FD87F18128aD33ca03Ba2);

    address public immutable burnReservesAddress;

    address public immutable initialDistributionAddress;    

    TokenTimelock public burnReservesVault;

    /// @notice liquidity sources (e.g. ThugSwapRouter) 
    mapping(address => bool) public whitelistedSenders;

    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    mapping (address => bool) private _isExcluded;
    address[] private _excluded;

    address public thugSwapPair;

    /// @notice Whether or not this token is first in streetswap pair
    bool public isThisToken0;

    /// @notice last TWAP update time
    uint32 public blockTimestampLast;

    /// @notice last TWAP cumulative price
    uint256 public priceCumulativeLast;

    /// @notice last TWAP average price
    uint256 public priceAverageLast;

    /// @notice TWAP min delta (2-min)
    uint256 public minDeltaTwap;
    
    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal = 681 * 1e3 * 1e18;
    uint256 private _rTotal = (MAX - _tTotal.mul(2));
    uint256 private _tFeeTotal;

    event TwapUpdated(uint256 priceCumulativeLast, uint256 blockTimestampLast, uint256 priceAverageLast);

    constructor(
        uint256 _minDeltaTwap,
        address _initialDistributionAddress,
        address _burnReservesAddress
    ) 
    public
    Ownable()
    BEP20("HyperBurn", "HYPR")
    {
        setMinDeltaTwap(_minDeltaTwap);
        initialDistributionAddress = _initialDistributionAddress;
        burnReservesAddress = _burnReservesAddress;
        _createBurnReservesVault(_burnReservesAddress);
        _distributeTokens(_initialDistributionAddress);
        _initializePair();
        _pause();
    }

    modifier whenNotPausedOrInitialDistribution() {
        require(!paused() || msg.sender == initialDistributionAddress, "!paused && !initialDistributionAddress");
        _;
    }

    // display total redistributions
    function totalHyperDrops() public view returns (uint256) {
        return _tFeeTotal;
    }
    
    /**
     * @dev RFI Style Distributions.
     */
    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }
    
    /**
     * @notice total burnt from supply
     */
    function totalBurn() public view  returns (uint256) {
        return ((681 * 1e3 * 1e18) - _tTotal);
    }

    /**
     * @dev Balance Of Using RFI distribution
     */
    function balanceOf(address account) public view override returns (uint256) {
        if (_isWhitelistedSender(account)) return _tOwned[account];
        return _tokenFromReflection(_rOwned[account]);
    }

    modifier onlyInitialDistributionAddress() {
        require(msg.sender == initialDistributionAddress, "!initialDistributionAddress");
        _;
    }

    // re-distribution rfi logic
    function _tokenFromReflection(uint256 rAmount) internal view returns(uint256) {
        require(rAmount <= _rTotal, "Amount must be less than total reflections");
        uint256 currentRate =  _getRate();
        return rAmount.div(currentRate);
    }


    /**
     * @dev Unpauses all transfers from the distribution address (initial liquidity pool).
     */
    function unpause() external virtual onlyInitialDistributionAddress {
        super._unpause();
    }

    /**
     * @dev Min time elapsed before twap is updated.
     */
    function setMinDeltaTwap(uint256 _minDeltaTwap) public onlyOwner {
        minDeltaTwap = _minDeltaTwap;
    }

    /**
     * @dev Initializes the TWAP cumulative values for the burn curve.
     */
    function initializeTwap() external onlyInitialDistributionAddress {
        require(blockTimestampLast == 0, "twap already initialized");
        (uint256 price0Cumulative, uint256 price1Cumulative, uint32 blockTimestamp) = 
            ThugSwapOracleLibrary.currentCumulativePrices(thugSwapPair);

        uint256 priceCumulative = isThisToken0 ? price1Cumulative : price0Cumulative;
        
        blockTimestampLast = blockTimestamp;
        priceCumulativeLast = priceCumulative;
        priceAverageLast = INITIAL_TOKENS_PER_BNB;
    }

    /**
     * @dev whitelist and exclude
     */
    function setWhitelistedSender(address _address, bool _whitelisted) public onlyOwner {
        if(_whitelisted == true) {
            whitelistedSenders[_address] = _whitelisted;
            if(_rOwned[_address] > 0) {
                _tOwned[_address] = _tokenFromReflection(_rOwned[_address]);
            }
            _isExcluded[_address] = _whitelisted;
            _excluded.push(_address);
        } else {
            whitelistedSenders[_address] = _whitelisted;
            for (uint256 i = 0; i < _excluded.length; i++) {
               if (_excluded[i] == _address) {
                  _excluded[i] = _excluded[_excluded.length - 1];
                  _tOwned[_address] = 0;
                  _isExcluded[_address] = _whitelisted;
                  _excluded.pop();
                   break;
                }
            }
        }
    }

    function _distributeTokens(
        address _initialDistributionAddress
    ) 
    internal
    {
        // Initial Supply
        setWhitelistedSender(_initialDistributionAddress, true);
        _tOwned[_initialDistributionAddress] = _tTotal;
        _rOwned[_initialDistributionAddress] = _rTotal;
        emit Transfer(address(0), _initialDistributionAddress, _tTotal);

    }

    function _createBurnReservesVault(address _burnReservesAddress) internal {
        burnReservesVault = new TokenTimelock(_burnReservesAddress, block.timestamp + 14 days);
        setWhitelistedSender(address(burnReservesVault), true);
    }

    function _initializePair() internal {
        (address token0, address token1) = ThugSwapLibrary.sortTokens(address(this), address(WBNB));
        isThisToken0 = (token0 == address(this));
        thugSwapPair = ThugSwapLibrary.pairFor(thugSwapFactory, token0, token1);

        setWhitelistedSender(thugSwapPair, true);
    }
    
    /**
     * @dev RFI compatible burns
     */
    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "BEP20: burn from the zero address");

        uint256 currentRate =  _getRate();
        uint256 rBurningAmount = amount.mul(currentRate);
        _tTotal = _tTotal.sub(amount);
        _rTotal = _rTotal.sub(rBurningAmount);
        emit Transfer(account, address(0), amount);
    }

    //Set pair contract address manually
    function setPairAddr(address pairaddr) public onlyOwner {
        (address token0, address token1) = ThugSwapLibrary.sortTokens(address(this), address(WBNB));
        require(token1 != address(0),"token1 is address 0");
        
        isThisToken0 = (token0 == address(this));
        thugSwapPair = pairaddr;

        setWhitelistedSender(thugSwapPair, true);
    }
  
    function _isWhitelistedSender(address _sender) internal view returns (bool) {
        return whitelistedSenders[_sender];
    }

    function _updateTwap() internal virtual returns (uint256) {
        (uint price0Cumulative, uint price1Cumulative, uint32 blockTimestamp) =
            ThugSwapOracleLibrary.currentCumulativePrices(thugSwapPair);
        uint32 timeElapsed = blockTimestamp - blockTimestampLast; // overflow is desired

        if (timeElapsed > minDeltaTwap) {
            uint256 priceCumulative = isThisToken0 ? price1Cumulative : price0Cumulative;

            // cumulative price is in (uq112x112 price * seconds) units so we simply wrap it after division by time elapsed
            FixedPoint.uq112x112 memory priceAverage = FixedPoint.uq112x112(
                uint224((priceCumulative - priceCumulativeLast) / timeElapsed)
            );

            priceCumulativeLast = priceCumulative;
            blockTimestampLast = blockTimestamp;

            priceAverageLast = FixedPoint.decode144(FixedPoint.mul(priceAverage, 1 ether));

            emit TwapUpdated(priceCumulativeLast, blockTimestampLast, priceAverageLast);
        }

        return priceAverageLast;
    }

    function _transfer(address sender, address recipient, uint256 amount)
        internal
        virtual
        override
        whenNotPausedOrInitialDistribution()
    {
        uint256 currentRate =  _getRate();
        
        if (!_isWhitelistedSender(sender) && !_isWhitelistedSender(recipient)) {
            uint256 scaleFactor = 1e18;
            uint256 currentAmountOutPerEth = _updateTwap();
            uint256 currentBurnPct = BURN_PCT.mul(scaleFactor);
            if (currentAmountOutPerEth < INITIAL_TOKENS_PER_BNB) {
                // 50 / (INITIAL_TOKENS_PER_BNB / currentAmountOutPerEth)
                scaleFactor = 1e9;
                currentBurnPct = currentBurnPct.mul(scaleFactor)
                    .div(INITIAL_TOKENS_PER_BNB.mul(1e18)
                        .div(currentAmountOutPerEth));
                uint256 minBurnPct = MIN_BURN_PCT * scaleFactor / 10;
                currentBurnPct = currentBurnPct > minBurnPct ? currentBurnPct : minBurnPct;
            }

            uint256 totalBurnAmount = amount.mul(currentBurnPct).div(100).div(scaleFactor);
            uint256 burnReserveAmount = totalBurnAmount.mul(BURN_RESERVE_PCT).div(100);
            uint256 tFee = totalBurnAmount.mul(REDIST_PCT).div(100);
            uint256 burnAmount = burnReserveAmount;
            uint256 redistributionAmount = tFee.mul(currentRate);

            _rOwned[sender] = _rOwned[sender].sub(amount.mul(currentRate));
            
            uint256 amount2 = amount.sub(totalBurnAmount);
            _rOwned[recipient] = _rOwned[recipient].add(amount2.mul(currentRate));
            _distributeFee(redistributionAmount, tFee);
            _tOwned[address(burnReservesVault)] = _tOwned[address(burnReservesVault)].add(burnReserveAmount);
            _rOwned[address(burnReservesVault)] = _rOwned[address(burnReservesVault)].add(burnReserveAmount.mul(currentRate));

            emit Transfer(sender, address(burnReservesVault), burnReserveAmount);
            _burn(sender, burnAmount);
            emit Transfer(sender, recipient, amount2);
        } else if (!_isWhitelistedSender(sender) && _isWhitelistedSender(recipient)) {
            uint256 scaleFactor = 1e18;
            uint256 currentAmountOutPerEth = _updateTwap();
            uint256 currentBurnPct = BURN_PCT.mul(scaleFactor);
            if (currentAmountOutPerEth < INITIAL_TOKENS_PER_BNB) {
                // 50 / (INITIAL_TOKENS_PER_BNB / currentAmountOutPerEth)
                scaleFactor = 1e9;
                currentBurnPct = currentBurnPct.mul(scaleFactor)
                    .div(INITIAL_TOKENS_PER_BNB.mul(1e18)
                        .div(currentAmountOutPerEth));
                uint256 minBurnPct = MIN_BURN_PCT * scaleFactor / 10;
                currentBurnPct = currentBurnPct > minBurnPct ? currentBurnPct : minBurnPct;
            }

            uint256 totalBurnAmount = amount.mul(currentBurnPct).div(100).div(scaleFactor);
            uint256 burnReserveAmount = totalBurnAmount.mul(BURN_RESERVE_PCT).div(100);
            uint256 tFee = totalBurnAmount.mul(REDIST_PCT).div(100);
            uint256 burnAmount = burnReserveAmount;
            uint256 redistributionAmount = tFee.mul(currentRate);

            _rOwned[sender] = _rOwned[sender].sub(amount.mul(currentRate));
            
            uint256 amount2 = amount.sub(totalBurnAmount);
            _tOwned[recipient] = _tOwned[recipient].add(amount2);
            _rOwned[recipient] = _rOwned[recipient].add(amount2.mul(currentRate));
            _distributeFee(redistributionAmount, tFee);
            _tOwned[address(burnReservesVault)] = _tOwned[address(burnReservesVault)].add(burnReserveAmount);
            _rOwned[address(burnReservesVault)] = _rOwned[address(burnReservesVault)].add(burnReserveAmount.mul(currentRate));


            emit Transfer(sender, address(burnReservesVault), burnReserveAmount);
            _burn(sender, burnAmount);
            emit Transfer(sender, recipient, amount2);
        } else if (_isWhitelistedSender(sender) && _isWhitelistedSender(recipient)) {
            _tOwned[sender] = _tOwned[sender].sub(amount);
            _rOwned[sender] = _rOwned[sender].sub(amount.mul(currentRate));
            _tOwned[recipient] = _tOwned[recipient].add(amount);
            _rOwned[recipient] = _rOwned[recipient].add(amount.mul(currentRate));
            emit Transfer(sender, recipient, amount);
        } else {
            _tOwned[sender] = _tOwned[sender].sub(amount);
            _rOwned[sender] = _rOwned[sender].sub(amount.mul(currentRate));
            _rOwned[recipient] = _rOwned[recipient].add(amount.mul(currentRate));
            emit Transfer(sender, recipient, amount);
        }
    }

    function _distributeFee(uint256 rFee, uint256 tFee) private {
        _rTotal = _rTotal.sub(rFee);
        _tFeeTotal = _tFeeTotal.add(tFee);
    }

    function _getRate() private view returns(uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns(uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }
    
    // view burn percentage
    function currentBurnPercent() public view returns (uint256) {
        uint256 scaleFactor = 1e18;
        uint256 currentAmountOutPerEth = getLastTwap();
        uint256 currentBurnPct = BURN_PCT.mul(scaleFactor);
        
        if (currentAmountOutPerEth < INITIAL_TOKENS_PER_BNB) {
            // 50 / (INITIAL_TOKENS_PER_BNB / currentAmountOutPerEth)
            scaleFactor = 1e9;
            currentBurnPct = currentBurnPct.mul(scaleFactor)
            .div(INITIAL_TOKENS_PER_BNB.mul(1e18)
            .div(currentAmountOutPerEth));
            uint256 minBurnPct = MIN_BURN_PCT * scaleFactor / 10;
            currentBurnPct = currentBurnPct > minBurnPct ? currentBurnPct : minBurnPct;
        }
        return currentBurnPct;
    }

    function getCurrentTwap() public view returns (uint256) {
        (uint price0Cumulative, uint price1Cumulative, uint32 blockTimestamp) = 
            ThugSwapOracleLibrary.currentCumulativePrices(thugSwapPair);
        uint32 timeElapsed = blockTimestamp - blockTimestampLast;

        uint256 priceCumulative = isThisToken0 ? price1Cumulative : price0Cumulative;

        FixedPoint.uq112x112 memory priceAverage = FixedPoint.uq112x112(
            uint224((priceCumulative - priceCumulativeLast) / timeElapsed)
        );

        return FixedPoint.decode144(FixedPoint.mul(priceAverage, 1 ether));
    }

    function getLastTwap() public view returns (uint256) {
        return priceAverageLast;
    }
}
