/**
 * @dev A token holder contract that will allow a beneficiary to extract the
 * tokens after a given release time.
 *
 * Useful for simple vesting schedules like "advisors get all of their tokens
 * after 1 year".
 */
contract TokenTimelock  {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    // ERC20 basic token contract being held
    IERC20 private immutable _token;

    // beneficiary of tokens after they are released
    address private immutable _beneficiary;

    // timestamp when token release is enabled
    uint256 private immutable _firstReleaseTime;
    uint256 private _lastReleaseTime;
    uint256 private _decimals = 18;

    uint256 private _totalSupply = 12600 * 10 ** _decimals ;//Record the total number of releases

    uint256 private _totalReleased ;//Record total released quantity
    uint256 private year1Amount = 4200 * 10 ** _decimals;//Quantity released in the first year
    uint256 private year2Amount = 3150 * 10 ** _decimals;//Quantity released in the second year
    uint256 private year3Amount = 3150 * 10 ** _decimals;//Quantity released in the third year
    uint256 private year4Amount = 1050 * 10 ** _decimals;//Quantity released in the fourth year
    uint256 private year5Amount = 1050 * 10 ** _decimals;//Quantity released in the fifth year

 
    
    

    // ERC20 basic token contract being held

 
    
    uint256 private _releaseTimes = 0;//Record the total number of releases
    uint256 private _releaseCycle = 30;//Release cycle, once every 30 days
    uint256 private oneday = 1*60*60*24;//Total seconds of 1 day


    /**
     * @dev Deploys a timelock instance that is able to hold the token specified, and will only release it to
     * `beneficiary_` when {release} is invoked after `releaseTime_`. The release time is specified as a Unix timestamp
     * (in seconds).
     */
    constructor(
        IERC20 token_,
        address beneficiary_,
        uint256 firstReleaseTime_
    ) {
        require(firstReleaseTime_ > block.timestamp, "TokenTimelock: release time is before current time");
        _token = token_;
        _beneficiary = beneficiary_;
        _firstReleaseTime = firstReleaseTime_;
    }

    /**
     * @dev Returns the token being held.
     */
    function token() public view virtual returns (IERC20) {
        return _token;
    }

    /**
     * @dev Returns the beneficiary that will receive the tokens.
     */
    function beneficiary() public view virtual returns (address) {
        return _beneficiary;
    }

    /**
     * @dev Returns the time when the tokens are released in seconds since Unix epoch (i.e. Unix timestamp).
     */
    function firstReleaseTime() public view virtual returns (uint256) {
        return _firstReleaseTime;
    }

    function lastReleaseTime() public view virtual returns (uint256) {
        return _lastReleaseTime;
    }

    
    function totalSupply() public view virtual returns (uint256) {
        return _totalSupply;
    }

    function decimals() public view virtual returns (uint256) {
        return _decimals;
    }

 
    function totalReleased() public view virtual returns (uint256) {
        return _totalReleased;
    }

 
    function releaseTimes() public view virtual returns (uint256) {
        return _releaseTimes;
    }

    function releaseCycle() public view virtual returns (uint256) {
        return _releaseCycle;
    }

  

    function release() public virtual {
        uint256 releaseTime = _firstReleaseTime + (_releaseTimes * _releaseCycle * oneday );//Release time
        require(block.timestamp >= releaseTime, "TokenTimelock: current time is before release time");//Judge whether the release time is up, otherwise it will not be released
        _lastReleaseTime = releaseTime;
        uint256 amount;//Quantity released this time

        if(_releaseTimes<=11){//Quantity released in the first year
            amount = year1Amount;
        }else if(_releaseTimes>=12 && _releaseTimes<=23){//Quantity released in the second year
            amount = year2Amount;
        }else if(_releaseTimes>=24 && _releaseTimes<=35){//Quantity released in the third year
            amount = year3Amount;
        }else if(_releaseTimes>=36 && _releaseTimes<=47){//Quantity released in the fourth year
            amount = year4Amount;
        }else if(_releaseTimes>=48){//Quantity released in the fifth year
            amount = year5Amount;
        }
        amount = amount.div(12);//The release quantity is divided into 12 times
        require(amount > 0, "TokenTimelock: no tokens to release");
        _totalReleased = _totalReleased.add(amount);//Count the total released quantity
        token().safeTransfer(beneficiary(), amount);//Start releasing to the specified ore pool
        _releaseTimes = _releaseTimes + 1;//Total release times increased by 1
    }
}
