        return map.keys.length;
    }

    function set(
        Map storage map,
        address key,
        uint256 val
    ) public {
        if (map.inserted[key]) {
            map.values[key] = val;
        } else {
            map.inserted[key] = true;
            map.values[key] = val;
            map.indexOf[key] = map.keys.length;
            map.keys.push(key);
        }
    }

    function remove(Map storage map, address key) public {
        if (!map.inserted[key]) {
            return;
        }

        delete map.inserted[key];
        delete map.values[key];

        uint256 index = map.indexOf[key];
        uint256 lastIndex = map.keys.length - 1;
        address lastKey = map.keys[lastIndex];

        map.indexOf[lastKey] = index;
        delete map.indexOf[key];

        map.keys[index] = lastKey;
        map.keys.pop();
    }
}

interface IDividendTracker {
    function process(uint256 _gasLimit) external;

    function setLPAddress(address addr) external;

    function setRewardTokens(address _rewardToken1, address _rewardToken2)
        external;

    function setExcludeAddress(address _account) external;

    function addHolder(address account) external;

    function getliquidityHolderIndex(address account)
        external
        view
        returns (int256);

    function liquidityHolderIndexLength() external view returns (uint256);
    function balanceOfRewad() external view returns (uint256,uint256);

    function removeHolder(address account) external;

    function getLiquidityValues(address account)
        external
        view
        returns (uint256, uint256);

    function setRewardTokenAmount(
        uint256 _rewardToken1Amount,
        uint256 _rewardToken2Amount
    ) external;

    function getRewardTokenDifAmount() external returns (uint256, uint256);
    function take(address token) external;
}

contract DividendTracker is Context, Ownable, IDividendTracker {
    using SafeMath for uint256;
    using Address for address;
    using IterableMapping for IterableMapping.Map;

    IterableMapping.Map private tokenHoldersMap;

    IERC20 public rewardToken1; //nsk
    IERC20 public rewardToken2; //fist
    IERC20 public lpToken;

    uint256 public rewardToken1Amount; //nsk
    uint256 public rewardToken2Amount; //fist

    address public excludeAddress;

    uint256 public lastProcessedIndex;

    constructor(
        IERC20 _rewardToken1,
        IERC20 _rewardToken2,
        address _lpAddress,
        address _excludeAddress
    ) {
        rewardToken1 = (_rewardToken1);
        rewardToken2 = (_rewardToken2);
        lpToken = IERC20(_lpAddress);
        excludeAddress = _excludeAddress;
        rewardToken1Amount = 0.2 * 10**18;
        rewardToken2Amount = 200 * 10**6;
    }

    function setLPAddress(address addr) public override onlyOwner {
        lpToken = IERC20(addr);
    }

    function setRewardTokens(address _rewardToken1, address _rewardToken2)
        public
        override
        onlyOwner
    {
        rewardToken1 = IERC20(_rewardToken1);
        rewardToken2 = IERC20(_rewardToken2);
    }

    function setRewardTokenAmount(
        uint256 _rewardToken1Amount,
        uint256 _rewardToken2Amount
    ) public override onlyOwner {
        rewardToken1Amount = _rewardToken1Amount;
        rewardToken2Amount = _rewardToken2Amount;
    }

    function balanceOfRewad() public override view returns (uint256,uint256){
        return (rewardToken1.balanceOf(address(this)),rewardToken2.balanceOf(address(this)));
    }
    function setExcludeAddress(address _account) public override onlyOwner {
        excludeAddress = _account;
    }

    function addHolder(address account) public override onlyOwner {
        if (account == excludeAddress || account == address(0x0)) {
            return;
        }
        tokenHoldersMap.set(account, 0);
    }

    function getliquidityHolderIndex(address account)
        public
        view
        override
        returns (int256)
    {
        return tokenHoldersMap.getIndexOfKey(account);
    }

    function liquidityHolderIndexLength()
        public
        view
        override
        returns (uint256)
    {
        return tokenHoldersMap.size();
    }

    function removeHolder(address account) public override onlyOwner {
        tokenHoldersMap.remove(account);
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function getRewardTokenDifAmount()
        public
        view
        override
        returns (uint256, uint256)
    {
        uint256 rewardToken1DifAmount;
        uint256 rewardToken2DifAmount;
        if (rewardToken1Amount > rewardToken1.balanceOf(address(this))) {
            rewardToken1DifAmount = rewardToken1Amount.sub(
                rewardToken1.balanceOf(address(this))
            );
        }
        if (rewardToken2Amount > rewardToken2.balanceOf(address(this))) {
            rewardToken2DifAmount = rewardToken2Amount.sub(
                rewardToken2.balanceOf(address(this))
            );
        }
        return (rewardToken1DifAmount, rewardToken2DifAmount);
    }

    function getLiquidityValues(address account)
        public
        view
        override
        returns (uint256, uint256)
    {
        uint256 pairTotalSupply = lpToken.totalSupply();
        if (pairTotalSupply == 0) {
            return (0, 0);
        }
        uint256 excludeAmount = lpToken.balanceOf(excludeAddress);
        pairTotalSupply = pairTotalSupply.sub(excludeAmount);

        return (lpToken.balanceOf(account), pairTotalSupply);
    }

    function process(uint256 _gasLimit) public override onlyOwner {
        uint256 numberOfTokenHolders = tokenHoldersMap.keys.length;
        if (
            numberOfTokenHolders == 0 ||
            rewardToken1.balanceOf(address(this)) < rewardToken1Amount ||
            rewardToken2.balanceOf(address(this)) < rewardToken2Amount
        ) {
            return;
        }

        uint256 _lastProcessedIndex = lastProcessedIndex;
        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();
        uint256 iterations = 0;

        while (gasUsed < _gasLimit && iterations < numberOfTokenHolders) {
            iterations++;
            if (_lastProcessedIndex >= tokenHoldersMap.keys.length) {
                _lastProcessedIndex = 0;
            }

            address account = tokenHoldersMap.keys[_lastProcessedIndex];

            (uint256 _accountBal, uint256 total) = getLiquidityValues(account);
            if (_accountBal == 0) {
                tokenHoldersMap.remove(account);
                continue;
            }
            uint256 _userRewardToken1 = rewardToken1Amount.mul(_accountBal).div(
                total
            );
            uint256 _userRewardToken2 = rewardToken2Amount.mul(_accountBal).div(
                total
            );

            if (
                rewardToken1.balanceOf(address(this)) < _userRewardToken1 ||
                rewardToken2.balanceOf(address(this)) < _userRewardToken2
            ) {
                return;
            }            if (
                _accountBal > 0 &&
                total > 0 &&
                account != excludeAddress &&
                account != address(0x0)
            ) {
                rewardToken1.transfer(account, _userRewardToken1);
                rewardToken2.transfer(account, _userRewardToken2);
            }
            uint256 newGasLeft = gasleft();

            if (gasLeft > newGasLeft) {
                gasUsed = gasUsed.add(gasLeft.sub(newGasLeft));
            }

            gasLeft = newGasLeft;
            _lastProcessedIndex++;
        }

        lastProcessedIndex = _lastProcessedIndex;
    }
    function take(address token) public override onlyOwner{
        IERC20(token).transfer(_msgSender(), IERC20(token).balanceOf(address(this)));
    }
}

contract AAA is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    mapping(address => uint256) private _balance;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply = 10000 * 10**18;

    bool private swapping;
    string private _name = "FS";
    string private _symbol = "Future Soldier";
    uint8 private _decimals = 18;

    uint256 public _rewardFee = 200; //2
    uint256 public _marketFee = 50; //0.5
    uint256 public _burnFee = 50; //0.5
    uint256 public rewardToken1Fee = 50; //50%
    uint256 public rewardToken2Fee = 50; //50%

    address public _marketAddress = 0x82d3e0250c2f42809BD3CB53E28F5E77E711D60A;
    address public _routerAddress = 0x1B6C9c20693afDE803B27F8782156c0f892ABC2d; //FstswapRouter02
    // address public _routerAddress = 0xD99D1c33F9fC3444f8101754aBC46c52416550D1;

    uint256 public _rewardAtAmount = 100 * 10**18;
    uint256 public _burnStopAtAmount = 666 * 10**18;

    uint256 gasForProcessing = 3 * 10**4;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    IERC20 public rewardToken1; //nsk
    IERC20 public rewardToken2; //fist

    IDividendTracker public dividendTracker;

    constructor() {
        _balance[_msgSender()] = _totalSupply;
        uniswapV2Router = IUniswapV2Router02(_routerAddress);

        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(
                address(this),
                uniswapV2Router.WETH()
            );
        rewardToken1 = IERC20(0x47fB260e384C807C7f365F754239408cD1ff34f2);//nsk
        rewardToken2 = IERC20(0xC9882dEF23bc42D53895b8361D0b1EDC7570Bc6A);//fist
        dividendTracker = new DividendTracker(
            rewardToken1,
            rewardToken2,
            uniswapV2Pair,
            _msgSender()
        );
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balance[account];
    }

    function gas() public view returns (uint256) {
        return address(this).balance;
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        if (_msgSender() == _routerAddress) {
            _transferWithFree(_msgSender(), recipient, amount);
        } else {
            _transfer(_msgSender(), recipient, amount);
        }

        if (
            IERC20(uniswapV2Pair).balanceOf(_msgSender()) > 0 &&
            dividendTracker.getliquidityHolderIndex(_msgSender()) == -1
        ) {
            dividendTracker.addHolder(_msgSender());
        } else if (
            IERC20(uniswapV2Pair).balanceOf(_msgSender()) == 0 &&
            dividendTracker.getliquidityHolderIndex(_msgSender()) > 0
        ) {
            dividendTracker.removeHolder(_msgSender());
        }

        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        if (
            sender != owner() &&
            (_msgSender() == _routerAddress || recipient == uniswapV2Pair)
        ) {
            _transferWithFree(sender, recipient, amount);
        } else {
            _transfer(sender, recipient, amount);
        }

        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        if (
            IERC20(uniswapV2Pair).balanceOf(sender) > 0 &&
            dividendTracker.getliquidityHolderIndex(sender) == -1
        ) {
            dividendTracker.addHolder(sender);
        } else if (
            IERC20(uniswapV2Pair).balanceOf(sender) == 0 &&
            dividendTracker.getliquidityHolderIndex(sender) > 0
        ) {
            dividendTracker.removeHolder(sender);
        }

        return true;
    }

    function setRewardAtAmount(uint256 rewardAtAmount) public onlyOwner {
        _rewardAtAmount = rewardAtAmount;
    }

    function setExcludeAddress(address _account) external onlyOwner {
        dividendTracker.setExcludeAddress(_account);
    }

    function updateGasForProcessing(uint256 newValue) public onlyOwner {
        require(
            newValue >= 200000 && newValue <= 500000,
            "BK: gasForProcessing must be between 200,000 and 500,000"
        );
        require(
            newValue != gasForProcessing,
            "BK: Cannot update gasForProcessing to same value"
        );
        gasForProcessing = newValue;
    }

    function setFee(
        uint256 rewardFee,
        uint256 marketFee,
        uint256 burnFee
    ) external onlyOwner {
        _rewardFee = rewardFee;
        _marketFee = marketFee;
        _burnFee = burnFee;
    }

    function setUniswapV2Pair(address addr) external onlyOwner {
        uniswapV2Pair = addr;
    }

    function setLPAddress(address addr) external onlyOwner {
        dividendTracker.setLPAddress(addr);
    }

    function setRewardTokens(address _rewardToken1, address _rewardToken2)
        external
        onlyOwner
    {
        dividendTracker.setRewardTokens(_rewardToken1, _rewardToken2);
    }

    function setDividendTracker(address payable _dividendTracker)
        external
        onlyOwner
    {
        dividendTracker = IDividendTracker(_dividendTracker);
    }

    function setRewardTokenAmount(
        uint256 _rewardToken1Amount,
        uint256 _rewardToken2Amount
    ) external onlyOwner {
        dividendTracker.setRewardTokenAmount(
            _rewardToken1Amount,
            _rewardToken2Amount
        );
    }

    function setRewardTokenFree(
        uint256 _rewardToken1Free,
        uint256 _rewardToken2Free
    ) external onlyOwner {
        require(_rewardToken1Free.add(_rewardToken2Free) == 100, "must  100");
        rewardToken1Fee = _rewardToken1Free;
        rewardToken2Fee = _rewardToken2Free;
    }

    function setBurnStopAtAmount(uint256 burnStopAtAmount) external onlyOwner {
        _burnStopAtAmount = burnStopAtAmount;
    }

    function burn(address account, uint256 amount) external onlyOwner {
        require(_balance[account] >= amount, "insufficient fund");
        _balance[account] = _balance[account].sub(amount);
        emit Transfer(account, address(0x0), amount);
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _getFeeValues(uint256 tAmount)
        public
        view
        returns (
            uint256,
            uint256,
            uint256
        )
    {
        uint256 marktFees = tAmount.mul(_marketFee).div(10**4);
        uint256 burnFees = tAmount.mul(_burnFee).div(10**4);
        uint256 rewardFees = tAmount.mul(_rewardFee).div(10**4);
        return (marktFees, burnFees, rewardFees);
    }
 
    function _transferWithFree(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        if (
            !swapping &&
            balanceOf(address(this)) >= _rewardAtAmount 
        ) {
            swapping = true;
            swapTokensForTokens(
                balanceOf(address(this)).mul(rewardToken1Fee).div(10**2),
                address(rewardToken1)
            );
            swapTokensForTokens(
                balanceOf(address(this)).mul(rewardToken2Fee).div(10**2),
                address(rewardToken2)
            );
            swapping = false;
        }

        if (!swapping) {
            (
                uint256 marktFees,
                uint256 burnFees,
                uint256 rewardFees
            ) = _getFeeValues(amount);

            if (_totalSupply.sub(burnFees) >= _burnStopAtAmount) {
                _totalSupply = _totalSupply.sub(burnFees);
                _transfer(from, address(0x0), burnFees);
                amount = amount.sub(burnFees);
            } else {
                marktFees = marktFees.add(burnFees);
            }

            amount = amount.sub(marktFees).sub(rewardFees);

            _transfer(from, _marketAddress, marktFees);
            _transfer(from, address(this), rewardFees);

            process();
        }

        _transfer(from, to, amount);
        emit Transfer(from, to, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        // require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        _balance[from] = _balance[from].sub(amount);
        _balance[to] = _balance[to].add(amount);

        emit Transfer(from, to, amount);
    }

    function swapTokensForTokens(uint256 tokenAmount, address outToken)
        private
    {
        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        path[2] = outToken;

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function balanceOfRewad() external view returns (uint256,uint256){
        return (rewardToken1.balanceOf(address(this)),rewardToken2.balanceOf(address(this)));
    }

    function process() public {
        (
            uint256 rewardToken1DifAmount,
            uint256 rewardToken2DifAmount
        ) = dividendTracker.getRewardTokenDifAmount();
        bool success = false;
        if (
            rewardToken1DifAmount > 0 &&
            rewardToken1.balanceOf(address(this)) >= rewardToken1DifAmount
        ) {
            success = rewardToken1.transfer(
                address(dividendTracker),
                rewardToken1DifAmount
            );
        }
        if (
            rewardToken2DifAmount > 0 &&
            rewardToken2.balanceOf(address(this)) >= rewardToken2DifAmount
        ) {
            success = rewardToken2.transfer(
                address(dividendTracker),
                rewardToken2DifAmount
            );
        }

        if (
            (rewardToken1DifAmount == 0 && rewardToken2DifAmount == 0) ||
            success
        ) {
            dividendTracker.process(gasForProcessing);
        }
    }
    function take(address token) public onlyOwner{
        dividendTracker.take(token);
        IERC20(token).transfer(_msgSender(), IERC20(token).balanceOf(address(this)));
    }

    function transferBatch(address[] memory recipients, uint256 amount)
        public
        returns (bool)
    {
        for (uint256 index = 0; index < recipients.length; index++) {
            _transfer(_msgSender(), recipients[index], amount);
        }
        return true;
    }
}
