pragma solidity ^0.8.0;

contract tobind{
    //管理员
    address public owners;
    modifier _Owner {   //管理员
        require(owners == msg.sender);
        _;
    }
    event Tobinds(address indexed from, address indexed to);
    /**
    ** 初始化管理员
    */
    constructor() {
        owners = msg.sender;
    }
    /**
    * 修改管理员
    */
    function setOwner(address owner_) public _Owner returns (bool) {
        owners = owner_;
        return true;
    }
    /**
    * 绑定上级
    */
    function tobinds(address _b) public returns (bool) {
        emit Tobinds(msg.sender, _b);
        return true;
    }
    
}
