interface IAocoCallee {
    function AocoCall(address sender, uint amount0, uint amount1, bytes calldata data) external;
}

contract AocoPair is IAocoPair, AocoERC20, st {
    using SafeMath  for uint;
    using UQ112x112 for uint224;

    uint public constant MINIMUM_LIQUIDITY = 10 ** 3;
    bytes4 private constant SELECTOR = bytes4(keccak256(bytes('transfer(address,uint256)')));

    address public factory;
    address public token0;
    address public token1;

    uint112 private reserve0;           // uses single storage slot, accessible via getReserves
    uint112 private reserve1;           // uses single storage slot, accessible via getReserves
    uint32  private blockTimestampLast; // uses single storage slot, accessible via getReserves

    uint public price0CumulativeLast;
    uint public price1CumulativeLast;
    uint public kLast; // reserve0 * reserve1, as of immediately after the most recent liquidity event

    uint private unlocked = 1;
    modifier lock() {
        require(unlocked == 1, 'Aoco: LOCKED');
        unlocked = 0;
        _;
        unlocked = 1;
    }

    function getReserves() public view returns (uint112 _reserve0, uint112 _reserve1, uint32 _blockTimestampLast) {
        _reserve0 = reserve0;
        _reserve1 = reserve1;
        _blockTimestampLast = blockTimestampLast;
    }

    function _safeTransfer(address token, address to, uint value) private {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(SELECTOR, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'Aoco: TRANSFER_FAILED');
    }

    function quote(uint amountA, uint reserveA, uint reserveB) internal pure returns (uint amountB) {
        require(amountA > 0, 'AocoLibrary: INSUFFICIENT_AMOUNT');
        require(reserveA > 0 && reserveB > 0, 'AocoLibrary: INSUFFICIENT_LIQUIDITY');
        amountB = amountA.mul(reserveB) / reserveA;
    }

    event Mint(address indexed sender, uint amount0, uint amount1);
    event Burn(address indexed sender, uint amount0, uint amount1, address indexed to);
    event Swap(
        address indexed sender,
        uint amount0In,
        uint amount1In,
        uint amount0Out,
        uint amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    constructor() public {
        factory = msg.sender;
    }

    // called once by the factory at time of deployment
    function initialize(address _token0, address _token1) external {
        require(msg.sender == factory, 'Aoco: FORBIDDEN');
        // sufficient check
        token0 = _token0;
        token1 = _token1;
    }

    // update reserves and, on the first call per block, price accumulators
    function _update(uint balance0, uint balance1, uint112 _reserve0, uint112 _reserve1) private {
        require(balance0 <= uint112(- 1) && balance1 <= uint112(- 1), 'Aoco: OVERFLOW');
        uint32 blockTimestamp = uint32(block.timestamp % 2 ** 32);
        uint32 timeElapsed = blockTimestamp - blockTimestampLast;
        // overflow is desired
        if (timeElapsed > 0 && _reserve0 != 0 && _reserve1 != 0) {
            // * never overflows, and + overflow is desired
            price0CumulativeLast += uint(UQ112x112.encode(_reserve1).uqdiv(_reserve0)) * timeElapsed;
            price1CumulativeLast += uint(UQ112x112.encode(_reserve0).uqdiv(_reserve1)) * timeElapsed;
        }
        reserve0 = uint112(balance0);
        reserve1 = uint112(balance1);
        blockTimestampLast = blockTimestamp;
        emit Sync(reserve0, reserve1);
    }

    // if fee is on, mint liquidity equivalent to 8/25 of the growth in sqrt(k)
    function _mintFee(uint112 _reserve0, uint112 _reserve1) private returns (bool feeOn) {
        address feeTo = IAocoFactory(factory).feeTo();
        feeOn = feeTo != address(0);
        uint _kLast = kLast;
        // gas savings
        if (feeOn) {
            if (_kLast != 0) {
                uint rootK = Math.sqrt(uint(_reserve0).mul(_reserve1));
                uint rootKLast = Math.sqrt(_kLast);
                (uint256 _holderFee,uint256 _devFee,) = IAocoFactory(factory).feeList(address(this));
                if (rootK > rootKLast) {
                    uint numerator = totalSupply.mul(rootK.sub(rootKLast)).mul(_devFee);
                    uint denominator = rootK.mul(_holderFee).add(rootKLast.mul(_devFee));
                    uint liquidity = numerator / denominator;
                    if (liquidity > 0) {
                        if (IAocoFactory(factory).feeShareRateList(address(this)) > 0) {
                            uint256 share = (liquidity * (IAocoFactory(factory).feeShareRateList(address(this)))) / 100;
                            uint256 devFee = liquidity - share;
                            _mint(IAocoFactory(factory).feeShareList(address(this)), share);
                            _mint(feeTo, devFee);
                        } else {
                            _mint(feeTo, liquidity);
                        }
                    }
                }
            }
        } else if (_kLast != 0) {
            kLast = 0;
        }
    }

    // this low-level function should be called from a contract which performs important safety checks
    function mint(address to) external lock returns (uint liquidity) {
        (uint112 _reserve0, uint112 _reserve1,) = getReserves();
        // gas savings
        uint balance0 = IERC20(token0).balanceOf(address(this));
        uint balance1 = IERC20(token1).balanceOf(address(this));
        uint amount0 = balance0.sub(_reserve0);
        uint amount1 = balance1.sub(_reserve1);

        bool feeOn = _mintFee(_reserve0, _reserve1);
        uint _totalSupply = totalSupply;
        // gas savings, must be defined here since totalSupply can update in _mintFee
        if (_totalSupply == 0) {
            liquidity = Math.sqrt(amount0.mul(amount1)).sub(MINIMUM_LIQUIDITY);
            _mint(address(0), MINIMUM_LIQUIDITY);
            // permanently lock the first MINIMUM_LIQUIDITY tokens
        } else {
            liquidity = Math.min(amount0.mul(_totalSupply) / _reserve0, amount1.mul(_totalSupply) / _reserve1);
        }
        require(liquidity > 0, 'Aoco: INSUFFICIENT_LIQUIDITY_MINTED');
        _mint(to, liquidity);

        _update(balance0, balance1, _reserve0, _reserve1);
        if (feeOn) kLast = uint(reserve0).mul(reserve1);
        // reserve0 and reserve1 are up-to-date
        emit Mint(msg.sender, amount0, amount1);
    }

    // this low-level function should be called from a contract which performs important safety checks
    function burn(address to) external lock returns (uint amount0, uint amount1) {
        (uint112 _reserve0, uint112 _reserve1,) = getReserves();
        // gas savings
        address _token0 = token0;
        // gas savings
        address _token1 = token1;
        // gas savings
        uint balance0 = IERC20(_token0).balanceOf(address(this));
        uint balance1 = IERC20(_token1).balanceOf(address(this));
        uint liquidity = balanceOf[address(this)];

        bool feeOn = _mintFee(_reserve0, _reserve1);
        uint _totalSupply = totalSupply;
        // gas savings, must be defined here since totalSupply can update in _mintFee
        amount0 = liquidity.mul(balance0) / _totalSupply;
        // using balances ensures pro-rata distribution
        amount1 = liquidity.mul(balance1) / _totalSupply;
        // using balances ensures pro-rata distribution
        require(amount0 > 0 && amount1 > 0, 'Aoco: INSUFFICIENT_LIQUIDITY_BURNED');
        _burn(address(this), liquidity);
        _safeTransfer(_token0, to, amount0);
        _safeTransfer(_token1, to, amount1);
        balance0 = IERC20(_token0).balanceOf(address(this));
        balance1 = IERC20(_token1).balanceOf(address(this));

        _update(balance0, balance1, _reserve0, _reserve1);
        if (feeOn) kLast = uint(reserve0).mul(reserve1);
        // reserve0 and reserve1 are up-to-date
        emit Burn(msg.sender, amount0, amount1, to);
    }

    // this low-level function should be called from a contract which performs important safety checks
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external lock {
        if (IAocoFactory(factory).useSwapWhiteList(address(this))) {
            require(IAocoFactory(factory).swapWhiteList(msg.sender), "not in whitelist");
        }
        require(amount0Out > 0 || amount1Out > 0, 'Aoco: INSUFFICIENT_OUTPUT_AMOUNT');
        (uint112 _reserve0, uint112 _reserve1,) = getReserves();
        // gas savings
        require(amount0Out < _reserve0 && amount1Out < _reserve1, 'Aoco: INSUFFICIENT_LIQUIDITY');

        uint balance0;
        uint balance1;
        {// scope for _token{0,1}, avoids stack too deep errors
            address _token0 = token0;
            address _token1 = token1;
            require(to != _token0 && to != _token1, 'Aoco: INVALID_TO');
            if (amount0Out > 0) _safeTransfer(_token0, to, amount0Out);
            // optimistically transfer tokens
            if (amount1Out > 0) _safeTransfer(_token1, to, amount1Out);
            // optimistically transfer tokens
            if (data.length > 0) IAocoCallee(to).AocoCall(msg.sender, amount0Out, amount1Out, data);
            balance0 = IERC20(_token0).balanceOf(address(this));
            balance1 = IERC20(_token1).balanceOf(address(this));
        }
        uint amount0In = balance0 > _reserve0 - amount0Out ? balance0 - (_reserve0 - amount0Out) : 0;
        uint amount1In = balance1 > _reserve1 - amount1Out ? balance1 - (_reserve1 - amount1Out) : 0;
        require(amount0In > 0 || amount1In > 0, 'Aoco: INSUFFICIENT_INPUT_AMOUNT');
        {// scope for reserve{0,1}Adjusted, avoids stack too deep errors
            (,,uint256 _totalFee) = IAocoFactory(factory).feeList(address(this));
            uint balance0Adjusted = (balance0.mul(10000).sub(amount0In.mul(_totalFee)));
            uint balance1Adjusted = (balance1.mul(10000).sub(amount1In.mul(_totalFee)));
            require(balance0Adjusted.mul(balance1Adjusted) >= uint(_reserve0).mul(_reserve1).mul(10000 ** 2), 'Aoco: K');
        }

        _update(balance0, balance1, _reserve0, _reserve1);
        emit Swap(msg.sender, amount0In, amount1In, amount0Out, amount1Out, to);
    }

    // force balances to match reserves
    function skim(address to) external lock {
        address _token0 = token0;
        // gas savings
        address _token1 = token1;
        // gas savings
        _safeTransfer(_token0, to, IERC20(_token0).balanceOf(address(this)).sub(reserve0));
        _safeTransfer(_token1, to, IERC20(_token1).balanceOf(address(this)).sub(reserve1));
    }

    // force reserves to match balances
    function sync() external lock {
        _update(IERC20(token0).balanceOf(address(this)), IERC20(token1).balanceOf(address(this)), reserve0, reserve1);
    }
}

contract AocoSwapFactory is Ownable, st {
    bytes32 public constant INIT_CODE_PAIR_HASH = keccak256(abi.encodePacked(type(AocoPair).creationCode));
    using SafeMath for uint256;
    bool public useRouterWhiteListMode = false;
    address public feeTo;
    address public feeToSetter;

    mapping(address => mapping(address => address)) public getPair;
    mapping(address => bool) public RouterList;
    mapping(address => bool) public swapWhiteList;
    mapping(address => address) public feeShareList;
    mapping(address => uint256) public feeShareRateList;
    mapping(address => bool) public useSwapWhiteList;
    mapping(address => bool) public useCreatePairWhiteList;
    mapping(address => address) public createPairWhiteList;
    mapping(address => bool) public useRouterWhiteList;
    address[] public allPairs;

    mapping(address => feeStruct) public feeListAll;
    uint256 public holderFee = 0;
    uint256 public devFee = 30;

    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    constructor(address _feeToSetter, address _feeTo) public {
        feeToSetter = _feeToSetter;
        feeTo = _feeTo;
    }

    function setDefaultFee(uint256 _holderFee, uint256 _devFee) external onlyOwner {
        holderFee = _holderFee;
        devFee = _devFee;
    }

    function addRouter(address _address) external onlyOwner {
        RouterList[_address] = true;
    }

    function removeRouter(address _address) external onlyOwner {
        RouterList[_address] = false;
    }

    function allPairsLength() external view returns (uint) {
        return allPairs.length;
    }

    function createPair(address tokenA, address tokenB) external returns (address pair) {
        if (useCreatePairWhiteList[tokenA] || useCreatePairWhiteList[tokenB]) {
            require(RouterList[msg.sender] || (createPairWhiteList[tokenA] == msg.sender || createPairWhiteList[tokenB] == msg.sender), "k9");
        }
        require(tokenA != tokenB, 'Aoco: IDENTICAL_ADDRESSES');
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), 'Aoco: ZERO_ADDRESS');
        require(getPair[token0][token1] == address(0), 'Aoco: PAIR_EXISTS');
        // single check is sufficient
        bytes memory bytecode = type(AocoPair).creationCode;
        bytes32 salt = keccak256(abi.encodePacked(token0, token1));
        assembly {
            pair := create2(0, add(bytecode, 32), mload(bytecode), salt)
        }
        IAocoPair(pair).initialize(token0, token1);
        getPair[token0][token1] = pair;
        getPair[token1][token0] = pair;
        // populate mapping in the reverse direction
        allPairs.push(pair);
        emit PairCreated(token0, token1, pair, allPairs.length);
    }

    function setFeeTo(address _feeTo) external {
        require(msg.sender == feeToSetter, 'Aoco: FORBIDDEN');
        feeTo = _feeTo;
    }

    function setFeeToSetter(address _feeToSetter) external {
        require(msg.sender == feeToSetter, 'Aoco: FORBIDDEN');
        feeToSetter = _feeToSetter;
    }


    function setFeeList(address _pair, uint256 _holderFee, uint256 _devFee) external onlyOwner {
        feeListAll[_pair] = feeStruct(_holderFee, _devFee, _holderFee.add(_devFee));
    }

    function feeList(address _pair) external view returns (uint256 _holderFee, uint256 _devFee, uint256 _totalFee) {
        feeStruct memory x = feeListAll[_pair];
        if (x.totalFee == 0) {
            x = feeStruct(holderFee, devFee, holderFee.add(devFee));
        }
        _holderFee = x.holderFee;
        _devFee = x.devFee;
        _totalFee = x.totalFee;
    }

    function addSwapWhiteList(address[] calldata _accountList) external onlyOwner {
        for (uint256 i = 0; i < _accountList.length; i++) {
            swapWhiteList[_accountList[i]] = true;
        }
    }

    function removeSwapWhiteList(address[] calldata _accountList) external onlyOwner {
        for (uint256 i = 0; i < _accountList.length; i++) {
            swapWhiteList[_accountList[i]] = true;
        }
    }

    function setFeeShareList(address _pair, address _account, uint256 _rate) external onlyOwner {
        feeShareList[_pair] = _account;
        feeShareRateList[_pair] = _rate;
    }

    function setUseSwapWhiteList(address _pair, bool _status) external onlyOwner {
        useSwapWhiteList[_pair] = _status;
    }

    function setUseCreatePairWhiteList(address _token, bool _status, address _account) external onlyOwner {
        useCreatePairWhiteList[_token] = _status;
        createPairWhiteList[_token] = _account;
    }

    function setRouterWhiteList(address _account,bool _status) external onlyOwner {
       useRouterWhiteList[_account] = _status;
    }

    function setUseRouterWhiteListMode(bool _status) external onlyOwner {
        useRouterWhiteListMode = _status;
    }

