contract RewardsManager is Ownable, Wrappable, Automation, IRewardManager {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    event SetAccount(address sender, uint256 wDexAmount, uint256 totalWDex);
    event CollectReward(address account, address rewardToken, uint256 amount);

    mapping(address => mapping (address => uint256)) claimedRewards;
    mapping(address => uint256) totalRewards;
    mapping(address => uint256) totalClaimed;
    IERC20 wDex;
    address rewardToken;

    constructor (address wrapper) {
        wDex = IERC20(wrapper);
    }

    function claimableRewards(address account) external view returns(uint256) {
        return _claimableRewards(account, rewardToken);
    }

    function claimableRewardsForToken(address account, address _rewardToken) external view returns(uint256) {
        return _claimableRewards(account, _rewardToken);
    }

    function collectRewards(address account, address _rewardToken) external override onlyWrapper returns(uint256) {
        uint256 amount = _claimableRewards(account, _rewardToken);
        claimedRewards[_rewardToken][account] += amount;
        totalClaimed[_rewardToken] += amount;
        emit CollectReward(account, _rewardToken, amount);
        return amount;
    }

    function _claimableRewards(address account, address _rewardToken) internal view returns(uint256) {
        uint256 totalWdex = wDex.totalSupply();
        // avaoid div by zero
        if (totalWdex == 0) {
            return 0;
        }
        uint256 accountWdex = wDex.balanceOf(account);

        uint256 accountClaimable = accountWdex.mul(totalRewards[_rewardToken]).div(totalWdex);
        uint256 accountClaimed = claimedRewards[_rewardToken][account];

        if (accountClaimable.sub(accountClaimed) > 0) {
            return accountClaimable - accountClaimed;
        } else {
            return 0;
        }
    }

    function setAccount(address sender, uint256 wDexAmount, uint256 _totalWDex) external override onlyWrapper returns(bool) {
        emit SetAccount(sender, wDexAmount, _totalWDex);
        return true;
    }

    // not currently supported
    function setRewardToken(address _rewardTokenAddress) external override onlyWrapper returns (bool) {
        rewardToken = _rewardTokenAddress;
        return true;
    }

    // #########################
    // ##                     ##
    // ##      Automation     ##
    // ##                     ##
    // #########################
    function logDisbursement(uint256 eth) external override onlyAutomation {
        totalRewards[rewardToken] += eth;
    }
}


