        uint8 _decimals
    ) ERC20("wDEX-SHARE", "wDEX-SHARE") Ownable() {
        decimalsValue = _decimals;

        dexIRA = IERC20(_dexIra);
        dexShare = IERC20(_dexShare);

        DEXIRA dexProxy = DEXIRA(_dexIra);
        if (_rewardToken != address(0)) {
            dexProxy.setRewardToken(_rewardToken);
        }
        reward = _rewardToken;
    }

    function decimals() public override(ERC20) view returns (uint8) {
        return decimalsValue;
    }

    // #########################
    // ##                     ##
    // ##    wDEX Public      ##
    // ##                     ##
    // #########################

    function wrap(uint amount) external whenNotPaused nonReentrant {
        _collectRewards(reward);

        _mint(_msgSender(), amount);

        // call ratio calculator
        uint256 dexIraAmount;
        uint256 dexShareAmount;
        (dexIraAmount, dexShareAmount) = ratioManager.calculateWrap(amount);

        // must be after the mint so the totals are right
        require(rewardsManager.setAccount(_msgSender(), balanceOf(_msgSender()), totalSupply()));

        // message call transfers (must be approved by user before this)
        require(dexIRA.transferFrom(_msgSender(), address(this), dexIraAmount));
        require(dexShare.transferFrom(_msgSender(), address(this), dexShareAmount));
    }

    function unwrap(uint amount) external whenNotPaused nonReentrant {
        uint256 dexIraAmount;
        uint256 dexShareAmount;
        (dexIraAmount, dexShareAmount)  = ratioManager.calculateUnwrap(address(this), amount);
        require(dexIRA.transfer(_msgSender(), dexIraAmount));
        require(dexShare.transfer(_msgSender(), dexShareAmount));

        // we don't require this because it is acceptable to not have anything to collect
        _collectRewards(reward);

        // this must be done after the calculate supply routine
        _burn(_msgSender(), amount);
        // this needs to be after the burn so that we set the balance and total supply correctly.
        require(rewardsManager.setAccount(_msgSender(), balanceOf(_msgSender()), totalSupply()));
    }

    // Safety method to allow reclaiming tokens if the reward claiming is buggered.
    function unwrapNoCollect(uint amount) external whenNotPaused {
        uint256 dexIraAmount;
        uint256 dexShareAmount;
        (dexIraAmount, dexShareAmount)  = ratioManager.calculateUnwrap(address(this), amount);
        require(dexIRA.transfer(_msgSender(), dexIraAmount));
        require(dexShare.transfer(_msgSender(), dexShareAmount));

        // this must be done after the calculate supply routine
        _burn(_msgSender(), amount);
    }

    function collectRewards() external whenNotPaused nonReentrant {
        require(_collectRewards(reward), "Nothing to collect");
    }

    function collectSpecificRewards(address _reward) external whenNotPaused nonReentrant {
        require(_collectRewards(_reward), "Nothing to collect");
    }

    function _collectRewards(address _reward) private returns(bool) {
        uint256 amount = rewardsManager.collectRewards(_msgSender(), _reward);
        if (amount > 0) {
            if (_reward != address(0)) {
                IERC20 rewardERC20 = IERC20(_reward);
                require(rewardERC20.balanceOf(address(this)) >= amount, "contract doesn't have balance to cover collection");
                require(rewardERC20.transfer(_msgSender(), amount), "Unable to collect");
            } else {
                require(address(this).balance >= amount, "contract doesn't have balance to cover collection");
                address payable recipient = payable(_msgSender());
                require(recipient.send(amount), "Unable to collect");
            }
            return true;
        } else {
            return false;
        }
    }

    /* @dev set the reward token for the entire wrapped tokenset
     */
    function setRewardToken(address _reward) external onlyOwner {
        // dexIRA
        DEXIRA dexIraToken = DEXIRA(address(dexIRA));
        require(dexIraToken.setRewardToken(_reward), "Unable to set reward token");
        require(rewardsManager.setRewardToken(_reward), "Unable to set reward token on reward manager");
        reward = _reward;
    }

    function setRewardsManager(address _rewardsManager) external onlyOwner {
        rewardsManager = IRewardManager(_rewardsManager);
        rewardsManager.setRewardToken(address(reward));
    }

    function getRewardsManager() external view returns(address) {
        return address(rewardsManager);
    }

    function setRatioManager(address _ratioManager) external onlyOwner {
        ratioManager = IRatioCalculator(_ratioManager);
    }

    function getRatioManager() external view returns(address) {
        return address(ratioManager);
    }

    receive() external payable nonReentrant {
        if (reward == address(0)) {
            rewardsManager.logDisbursement(msg.value);
            emit Received(msg.sender, msg.value);
        } else {
            require(false, "Rewards in eth not accepted");
        }
    }

    function pause() external onlyOwner {
        _pause();
    }
    function unpause() external onlyOwner {
        _unpause();
    }
}
