    "contracts/libraries/DataTypes.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

/// @title DataTypes
/// @notice This library contains the different data types for the Crafting system.
library DataTypes {

    /// @notice Enum to store the steps in the Crafting process.
    enum Step {
        CREATION,
        STYLING,
        ASSEMBLY
    }

    /// @notice Enum to store the two types of NFTs available in the Crafting.
    enum NftType {
        ARMOR,
        WEAPON
    }

    /// @notice Enum to store the different Elements.
    enum Elements {
        BULLET,
        LASER,
        METAL,
        POLYVALENT
    }

    /// @notice Enum to store the data for the Rarities.
    enum Rarity {
        COMMON,
        UNCOMMON,
        RARE,
        EPIC,
        LEGENDARY,
        NO_RARITY
    }

    /// @notice Enum to store the different Weapon Pieces.
    enum WeaponPieces {
        HILT,
        CROSS_GUARD,
        BLADE,
        HEAD,
        GRIP,
        BARREL,
        BODY,
        MAGAZINE,
        REFLEX_SIGHT,
        HOLOGRAPHIC_SIGHT,
        LASER_POINTER,
        SILENCER,
        STOCK,
        X4_SIGHT,
        BAYONETTA,
        STRIKER,
        BIPOD,
        GRENADE_LAUNCHER,
        ATTACHED_SHOTGUN,
        SNIPER_SIGHT,
        BARREL_BRAKE
    }

    /// @notice Struct to store the types for the Weapon Pieces.
    enum WeaponPieceTypes {
        MAIN,
        SIGHT,
        UNDER_BARREL,
        ACCESSORY
    }

    /// @notice Enum to store the different Weapon Types.
    enum WeaponTypes {
        KNIFE,
        BLADE,
        BLUNT,
        PISTOL,
        REVOLVER,
        SHOTGUN,
        REPEATER_SHOTGUN,
        ASSAULT_SMG,
        HIGH_RATE_SMG,
        LIGHT_MACHINE_GUN,
        ASSAULT_RIFLE,
        SNIPER_RIFLE,
        PRECISION_RIFLE
    }

    /// @notice Enum to store the different Armor Pieces.
    enum ArmorPieces {
        HELMET, 
        CHEST,
        SHOULDERS,
        FOREARMS,
        ARMS,
        GLOVES,
        LEGS,
        KNEEPADS,
        BOOTS
    }
}
"
    },
    "contracts/libraries/SharedStructs.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import { DataTypes } from "./DataTypes.sol";

/// @title SharedStructs
/// @notice This library contains the different shared structs for the Crafting system.
library SharedStructs {

    struct RequestData {
        uint256 tokenId;
        uint8 tier;
    }

    struct RangesByTier {
        uint16 minRange;
        uint16 maxRange;
    }

    struct ArmorTierConfig {
        address armorAddress;
        uint8 armorQuantity;
        uint8 armorTierId;
        uint8 armorRarityId;
        string armorRarityName;
        uint8 collectionId;
        string collectionName;
    }

    struct WeaponTierConfig {
        address weaponAddress;
        uint8 weaponQuantity;
        uint8 weaponTierId;
        uint8 weaponRarityId;
        string weaponRarityName;
        uint8 collectionId;
        string collectionName;
    }

    struct ItemInfo {
        uint8 tier;
        uint8 rarityId;
        string rarityName;
        uint8 collectionId;
        string collectionName;
        address firstOwner;
        uint8 piece;
    }

    /// @notice Struct to store the data for the Resources.
    struct Resource {
        address resourceAddress;
        uint256 amount;
    }

    /// @notice Struct to store the Weapon Piece information. 
    struct WeaponPieceInfo {
        uint8 numAesthetics; 
        DataTypes.WeaponPieceTypes weaponPieceType;
        mapping(uint8 => DataTypes.Elements[]) aestheticsInfo; // Aesthetic ID => Element ID[]
    }

    /// @notice Struct to store the Weapon information.
    struct WeaponInfo {
        string description;      
        DataTypes.WeaponPieces[] weaponPieces;
        mapping(uint8 => mapping(DataTypes.Step => CraftingInfo)) craftingInfo; // Tier => Step => CraftingInfo
    }

    /// @notice Struct to store the Armor Piece information. 
    struct ArmorPieceInfo {
        string description;
        uint8 numAesthetics; 
        mapping(uint8 => mapping(DataTypes.Step => CraftingInfo)) craftingInfo; // Tier => Step => CraftingInfo
    }

    /// @notice Struct to store the Crafting information (Tier/Step dependent data).
    struct CraftingInfo {
        uint64 craftingTime;
        uint256 skipTimePrice;
        Resource[] resources;
    }

    /// @notice Struct to store the information of the crafting items (Armor pieces and Weapons).
    struct CraftingItem {
        DataTypes.NftType nftType;
        uint8 itemId;
        uint8 tier;
        DataTypes.Elements element;
        uint8 rarityBooster;
        DataTypes.Rarity rarity;
        address owner;
        uint256 tokenId;
        uint8[] aesthetics;
    }

    // @notice Struct to store the information needed for the Chainlink fallback.
    struct CraftingRequestData {
        uint256 craftingId;
        uint8 tier;
        uint8 rarityBoosterId;
    }
}
"
