    "contracts/storage/CraftingSharedStorage.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";

import "./interfaces/ICraftingSharedStorage.sol";
import "./interfaces/IArmorsController.sol";
import "./interfaces/IWeaponsController.sol";
import "./interfaces/IRarityController.sol";

import {SharedStructs} from "../libraries/SharedStructs.sol";

/// @title CraftingSharedStorage contract
/// @author Urriza
/// @notice A contract for managing the shared information in the Crafting system.
/// @dev Inherits from Initializable, AccessControlEnumerableUpgradeable and PausableUpgradeable for upgradeability,
/// access control and pausable features. It also implements the ICraftingSharedStorage interface.
contract CraftingSharedStorage is
    Initializable,
    AccessControlEnumerableUpgradeable,
    PausableUpgradeable,
    ICraftingSharedStorage
{
    using CountersUpgradeable for CountersUpgradeable.Counter;

    // ============== CONSTANTS ==============

    /// @notice The role identifier for the manager who can change the configuration variables.
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

    /// @notice Constant to store a role identifier for the pauser.
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    /// @notice Constant to store a role identifier for the accounts that will be able to add new crafting items (Creation step).
    bytes32 public constant CREATION_ROLE = keccak256("CREATION_ROLE");

    /// @notice Constant to store a role identifier for the accounts that will be able to set the element and the aesthetics of the items (Styling step).
    bytes32 public constant STYLING_ROLE = keccak256("STYLING_ROLE");

    /// @notice Constant to store a role identifier for the accounts that will be able to set the rarity of the items (Assembly step).
    bytes32 public constant ASSEMBLY_ROLE = keccak256("ASSEMBLY_ROLE");

    // ============== VARIABLES ==============

    /// @notice Counter for the item craftings.
    CountersUpgradeable.Counter public craftingIdCounter;

    /// @notice Variable for knowing if contract has been initialized.
    bool public initialized;

    /// @notice Variable to store the NFT collection id.
    uint8 public collectionId;

    /// @notice Variable to store the NFT collection name.
    string public collectionName;

    /// @notice Variable to store the default URI for the NFTs.
    string public defaultURI;    

    /// @notice Variable to store the address of the token used for the crafting skip time.
    address public craftingToken;

    /// @notice Variable to store the crafting fee.
    uint256 public craftingFee;

    /// @notice Variable to store the payment recipient address.
    address public paymentRecipient;

    /// @notice Variable to store the fee recipient address.
    address public feeRecipient;

    /// @notice Variable to store the ArmorsController contract address.
    IArmorsController public armorsController;

    /// @notice Variable to store the WeaponsController contract address.
    IWeaponsController public weaponsController;

    /// @notice Variable to store the RarityController contract address.
    IRarityController public rarityController;

    /// @notice Mapping to store the element descriptions.
    mapping(DataTypes.Elements => string) public elementDescriptions;

    /// @notice Mapping to store the information about the item craftings.
    mapping(uint256 => SharedStructs.CraftingItem) public craftingItems;

    /// @notice Storage gap for possible new variables.
    uint256[37] __gapCraftingSharedStorage;

    // ============== MODIFIERS ==============

    /// @notice Modifier that will return true only when contract is initialized.
    modifier isInitialized() {
        if (!initialized) revert ContractNotInitialized();
        _;
    }

    //============== CONSTRUCTOR ==============

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // ============== INITIALIZE ==============

    /// @notice Initializes the CraftingSharedStorage contract, setting up the access control and the information regarding the shared information.
    /// @dev Grants the deployer the default admin role, manager and pauser roles.
    /// @param _collectionId The collection id.
    /// @param _collectionName The collection name.
    /// @param _defaultURI The default URI for the NFTs.    
    /// @param craftingTokenAddress The crafting token.
    /// @param _craftingFee The crafting fee.
    /// @param paymentRecipientAddress The payment recipient.
    /// @param feeRecipientAddress The fee recipient.
    /// @param armorsControllerAddress The ArmorsController contract address.
    /// @param weaponsControllerAddress The WeaponsController contract address.
    /// @param rarityControllerAddress The RarityController contract address.
    function initialize(
        uint8 _collectionId,
        string calldata _collectionName,
        string calldata _defaultURI,
        address craftingTokenAddress,
        uint256 _craftingFee,
        address paymentRecipientAddress,
        address feeRecipientAddress,
        IArmorsController armorsControllerAddress,
        IWeaponsController weaponsControllerAddress,
        IRarityController rarityControllerAddress
    ) external initializer {
        if (craftingTokenAddress == address(0)) revert CraftingTokenZeroAddress();
        craftingToken = craftingTokenAddress;

        if (paymentRecipientAddress == address(0)) revert PaymentRecipientZeroAddress();
        paymentRecipient = paymentRecipientAddress;

        if (feeRecipientAddress == address(0)) revert FeeRecipientZeroAddress();
        feeRecipient = feeRecipientAddress;

        if (address(armorsControllerAddress) == address(0)) revert ArmorsControllerZeroAddress();
        armorsController = armorsControllerAddress;

        if (address(weaponsControllerAddress) == address(0)) revert WeaponsControllerZeroAddress();
        weaponsController = weaponsControllerAddress;

        if (address(rarityControllerAddress) == address(0)) revert RarityControllerZeroAddress();
        rarityController = rarityControllerAddress;

        craftingFee = _craftingFee;

        __AccessControlEnumerable_init();
        __Pausable_init();

        initialized = true;
        collectionId = _collectionId;
        collectionName = _collectionName;
        defaultURI = _defaultURI;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /// @inheritdoc ICraftingSharedStorage
    /*function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /// @inheritdoc ICraftingSharedStorage
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }*/

    /// @inheritdoc ICraftingSharedStorage
    function setCollectionId(
        uint8 _collectionId
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        collectionId = _collectionId;

        emit CollectionIdChanged(msg.sender, _collectionId);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setCollectionName(
        string calldata _collectionName
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        collectionName = _collectionName;

        emit CollectionNameChanged(msg.sender, _collectionName);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setDefaultURI(
        string calldata _defaultURI
    ) 
        external 
        isInitialized
        onlyRole(MANAGER_ROLE) 
    {
        defaultURI = _defaultURI;

        emit DefaultURIChanged(msg.sender, _defaultURI);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setCraftingToken(
        address craftingTokenAddress
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        if (craftingTokenAddress == address(0))
            revert CraftingTokenZeroAddress();
        craftingToken = craftingTokenAddress;

        emit CraftingTokenChanged(msg.sender, craftingTokenAddress);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setCraftingFee(
        uint256 _craftingFee
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        craftingFee = _craftingFee;

        emit CraftingFeeChanged(msg.sender, _craftingFee);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setPaymentRecipient(
        address paymentRecipientAddress
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        if (paymentRecipientAddress == address(0))
            revert PaymentRecipientZeroAddress();
        paymentRecipient = paymentRecipientAddress;

        emit PaymentRecipientChanged(msg.sender, paymentRecipientAddress);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setFeeRecipient(
        address feeRecipientAddress
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        if (feeRecipientAddress == address(0)) revert FeeRecipientZeroAddress();
        feeRecipient = feeRecipientAddress;

        emit FeeRecipientChanged(msg.sender, feeRecipientAddress);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setArmorsController(
        address armorsControllerAddress
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        if (armorsControllerAddress == address(0)) revert ArmorsControllerZeroAddress();
        armorsController = IArmorsController(armorsControllerAddress);

        emit ArmorsControllerChanged(msg.sender, armorsControllerAddress);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setWeaponsController(
        address weaponsControllerAddress
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        if (weaponsControllerAddress == address(0)) revert WeaponsControllerZeroAddress();
        weaponsController = IWeaponsController(weaponsControllerAddress);

        emit WeaponsControllerChanged(msg.sender, weaponsControllerAddress);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setRarityController(
        address rarityControllerAddress
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        if (rarityControllerAddress == address(0)) revert RarityControllerZeroAddress();
        rarityController = IRarityController(rarityControllerAddress);

        emit RarityControllerChanged(msg.sender, rarityControllerAddress);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setElementDescription(
        DataTypes.Elements elementId,
        string calldata elementDescription
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        elementDescriptions[elementId] = elementDescription;

        emit ElementDescriptionChanged(
            msg.sender,
            elementId,
            elementDescription
        );
    }

    /// @inheritdoc ICraftingSharedStorage
    function getCraftingItem(
        uint256 craftingId
    ) external view returns (SharedStructs.CraftingItem memory) {
        return craftingItems[craftingId];
    }

    /// @inheritdoc ICraftingSharedStorage
    function addNewCrafting(
        DataTypes.NftType nftType,
        uint8 itemId,
        uint8 tier,
        address owner,
        DataTypes.Elements element,
        uint256 tokenId
    ) external isInitialized onlyRole(CREATION_ROLE) returns (uint256 craftingId) {
        craftingId = craftingIdCounter.current();

        craftingItems[craftingId] = SharedStructs.CraftingItem({
            nftType: nftType,
            itemId: itemId,
            tier: tier,
            element: element,
            rarityBooster: 1,
            rarity: DataTypes.Rarity.NO_RARITY,
            owner: owner,
            tokenId: tokenId,
            aesthetics: new uint8[](0)
        });
        craftingIdCounter.increment();

        emit CraftingItemAdded(
            msg.sender,
            craftingId,
            nftType,
            itemId,
            tier,
            owner,
            element,
            tokenId
        );
    }

    /// @inheritdoc ICraftingSharedStorage
    function setCraftingItemElement(
        uint256 craftingId,
        DataTypes.Elements element
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        craftingItems[craftingId].element = element;

        emit CraftingItemElementChanged(msg.sender, craftingId, element);
    }

    /// @inheritdoc ICraftingSharedStorage
    function getCraftingItemElement(
        uint256 craftingId
    ) external view returns (DataTypes.Elements) {
        return craftingItems[craftingId].element;
    }

    /// @inheritdoc ICraftingSharedStorage
    function setCraftingItemAesthetics(
        uint256 craftingId,
        uint8[] calldata aesthetics
    ) external isInitialized onlyRole(STYLING_ROLE) {
        craftingItems[craftingId].aesthetics = aesthetics;

        emit CraftingItemAestheticsChanged(msg.sender, craftingId, aesthetics);
    }

    /// @inheritdoc ICraftingSharedStorage
    function getCraftingItemAesthetics(
        uint256 craftingId
    ) external view returns (uint8[] memory) {
        return craftingItems[craftingId].aesthetics;
    }

    /// @inheritdoc ICraftingSharedStorage
    function setCraftingItemRarityBooster(
        uint256 craftingId,
        uint8 rarityBooster
    ) external isInitialized onlyRole(ASSEMBLY_ROLE) {
        craftingItems[craftingId].rarityBooster = rarityBooster;

        emit CraftingItemRarityBoosterChanged(msg.sender, craftingId, rarityBooster);
    }

    /// @inheritdoc ICraftingSharedStorage
    function setCraftingItemRarity(
        uint256 craftingId,
        DataTypes.Rarity rarity
    ) external isInitialized onlyRole(ASSEMBLY_ROLE) {
        craftingItems[craftingId].rarity = rarity;

        emit CraftingItemRarityChanged(msg.sender, craftingId, rarity);
    }

    /// @inheritdoc ICraftingSharedStorage
    function getCraftingInfo(
        DataTypes.NftType nftType,
        uint8 itemId,
        uint8 tier,
        DataTypes.Step step
    ) external view returns (SharedStructs.CraftingInfo memory craftingInfo) {
        if(nftType == DataTypes.NftType.ARMOR) {
            craftingInfo = armorsController.getArmorPieceInfo(DataTypes.ArmorPieces(itemId), tier, step);
        }
        else {
            craftingInfo = weaponsController.getWeaponTypeInfo(DataTypes.WeaponTypes(itemId), tier, step);
        }
    }

    /// @inheritdoc ICraftingSharedStorage
    function isWeaponTypeElementValid(
        uint8 itemId,
        DataTypes.Elements element
    ) external view returns (bool) {
        return weaponsController.getWeaponTypeValidElement(DataTypes.WeaponTypes(itemId), element);
    }

    /// @inheritdoc ICraftingSharedStorage
    function getCraftingItemResources(
        DataTypes.NftType nftType,
        uint8 itemId,
        uint8 tier,
        DataTypes.Step step
    ) external view returns (SharedStructs.Resource[] memory resources) {
        if(nftType == DataTypes.NftType.ARMOR) {
            resources = armorsController.getArmorPieceInfo(DataTypes.ArmorPieces(itemId), tier, step).resources;
        }
        else {
            resources = weaponsController.getWeaponTypeInfo(DataTypes.WeaponTypes(itemId), tier, step).resources;
        }
    }

    /// @inheritdoc ICraftingSharedStorage
    function getCraftingItemNumAesthetics(
        DataTypes.NftType nftType,
        uint8[2] calldata itemIds
    ) external view returns (uint8 aesthetic) {
        if(nftType == DataTypes.NftType.ARMOR) {
            aesthetic = armorsController.getArmorPieceAesthetics(DataTypes.ArmorPieces(itemIds[0]));
        }
        else {
            aesthetic = weaponsController.getWeaponPieceAesthetics(DataTypes.WeaponTypes(itemIds[0]), DataTypes.WeaponPieces(itemIds[1]));
        }
    }

    /// @inheritdoc ICraftingSharedStorage
    function getWeaponTypePieces(
        DataTypes.WeaponTypes weaponTypeId
    ) external view returns (DataTypes.WeaponPieces[] memory) {
        return weaponsController.getWeaponTypePieces(weaponTypeId);
    }   

    /// @inheritdoc ICraftingSharedStorage
    function getWeaponPieceType(
        DataTypes.WeaponTypes weaponTypeId,       
        DataTypes.WeaponPieces weaponPieceId    
    ) 
        external 
        view returns (DataTypes.WeaponPieceTypes) 
    {
        return weaponsController.getWeaponPieceType(weaponTypeId, weaponPieceId);
    }

    /// @inheritdoc ICraftingSharedStorage
    function getWeaponPieceAestheticElements(
        DataTypes.WeaponTypes weaponTypeId,       
        DataTypes.WeaponPieces weaponPieceId,
        uint8 aestheticId
    ) external view returns (DataTypes.Elements[] memory) {
        return weaponsController.getWeaponPieceAestheticElements(weaponTypeId, weaponPieceId, aestheticId);
    }

    /// @inheritdoc ICraftingSharedStorage
    function getArmorPieceAddress() external view returns (address) {
        return armorsController.armorPieceAddress();
    }

    /// @inheritdoc ICraftingSharedStorage
    function getWeaponTypeAddress() external view returns (address) {
        return weaponsController.weaponTypeAddress();
    }

    /// @inheritdoc ICraftingSharedStorage
    function getRarityDescription(
        DataTypes.Rarity rarityId
    )
        external  
        returns (string memory)
    {
        return rarityController.rarityDescriptions(rarityId);
    }

    /// @inheritdoc ICraftingSharedStorage
    function getRarityProbabilities(
        uint8 tier,
        uint8 rarityBoosterId
    ) 
        external 
        view returns (uint256[] memory) 
    {        
        return rarityController.getRarityProbabilities(tier, rarityBoosterId);
    } 

    /// @inheritdoc ICraftingSharedStorage
    function getRarityBoosterPrice(
        uint8 tier,
        uint8 rarityBoosterId
    )
        external  
        view returns (uint256)
    {
        return rarityController.rarityBoosterPrices(tier, rarityBoosterId);
    }

    /// @inheritdoc ICraftingSharedStorage
    function getRarityBoosterToken( 
    )
        external 
        view returns (address)
    {
        return rarityController.rarityBoosterToken();
    }        
}
"
    },
    "contracts/storage/interfaces/IArmorsController.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import { DataTypes } from "../../libraries/DataTypes.sol";
import { SharedStructs } from "../../libraries/SharedStructs.sol";

/// @title The interface for the Armors.
/// @notice This interface facilitates the setting of the different pieces of Armor for the crafting configuration.
interface IArmorsController {

    // ============== ERRORS ==============

    /// @notice Error when trying to set the armor piece address contract to the address 0.
    error ArmorPieceZeroAddress();

    /// @notice Error when the contract is not initialized.
    error ContractNotInitialized();

    // ============== EVENTS ==============

    /// @notice Event emitted when an armor piece description is changed.
    /// @param sender The address of the user that changes the description.
    /// @param id The id of the armor piece.  
    /// @param newDescription The new description of the armor piece.
    event ArmorPieceDescriptionChanged (
        address indexed sender,
        DataTypes.ArmorPieces indexed id,     
        string newDescription
    );

    /// @notice Event emitted when an armor piece aesthetics number is changed.
    /// @param sender The address of the user that changes the aesthetics.
    /// @param id The id of the armor piece. 
    /// @param newAestheticsNum The aesthetics number.
    event ArmorPieceAestheticsChanged (
        address indexed sender,
        DataTypes.ArmorPieces indexed id,     
        uint8 newAestheticsNum
    );

    /// @notice Event emitted when an armor piece resources from an specified tier and step are changed.
    /// @param sender The address of the user that changes the resources.
    /// @param id The id of the armor piece.
    /// @param tier The tier of the armor piece.     
    /// @param step The step of the crafting.    
    event ArmorPieceResourcesChanged (
        address indexed sender,
        DataTypes.ArmorPieces indexed id,
        uint8 indexed tier,
        DataTypes.Step step
    );

    /// @notice Event emitted when an armor piece skip time price is changed.
    /// @param sender The address of the user that changes the crafting time.
    /// @param id The id of the armor piece.
    /// @param tier The tier of the armor piece.     
    /// @param step The step of the crafting.      
    /// @param newCraftingTime The new crafting time.
    event ArmorPieceCraftingTimeChanged (
        address indexed sender,
        DataTypes.ArmorPieces indexed id,
        uint8 indexed tier,   
        DataTypes.Step step,     
        uint64 newCraftingTime
    );

    /// @notice Event emitted when an armor piece skip time price is changed.
    /// @param sender The address of the user that changes the skip time price.
    /// @param id The id of the armor piece.
    /// @param tier The tier of the armor piece. 
    /// @param step The step of the crafting.         
    /// @param newSkipTimePrice The new skip time price.
    event ArmorPieceSkipTimePriceChanged (
        address indexed sender,
        DataTypes.ArmorPieces indexed id,
        uint8 indexed tier,        
        DataTypes.Step step,
        uint256 newSkipTimePrice
    );

    /// @notice Event emitted when the address of the armor piece contract is changed.
    /// @param sender The address of the user that changes the armor piece contract address.
    /// @param newArmorPieceAddress The new address of the armor piece contract.
    event ArmorPieceAddressChanged (
        address indexed sender,
        address indexed newArmorPieceAddress
    );

    // ============== FUNCTIONS ==============

    /// @notice Pauses all armor pieces information changes.
    /// @dev Can only be called by an account with the PAUSER_ROLE.
    //function pause() external;

    /// @notice Unpauses all armor pieces information changes.
    /// @dev Can only be called by an account with the PAUSER_ROLE.
    //function unpause() external;

    /// @notice Function that sets an armor piece description.
    /// @param id The id of the armor piece.
    /// @param description The armor piece description.
    function setArmorPieceDescription(
        DataTypes.ArmorPieces id,
        string calldata description
    ) external;

    /// @notice Function that sets the description for a list or armor pieces.
    /// @param ids The ids of the armor pieces.
    /// @param descriptions The armor pieces descriptions.
    function setArmorPiecesDescription(
        DataTypes.ArmorPieces[] calldata ids,
        string[] calldata descriptions
    ) external;

    /// @notice Function that returns the armor piece description.
    /// @param armorPieceId The id of the armor piece.
    /// @return The armor piece description.
    function getArmorPieceDescription(
        DataTypes.ArmorPieces armorPieceId         
    ) external view returns (string memory);

    /// @notice Function that sets an armor piece aesthetics number.
    /// @param id The id of the armor piece.
    /// @param numAesthetics The aesthetics number.
    function setArmorPieceAesthetics(
        DataTypes.ArmorPieces id,
        uint8 numAesthetics
    ) external;

    /// @notice Function that sets the aesthetics number for a list of armor pieces.
    /// @param ids The id of the armor pieces.
    /// @param numAesthetics The aesthetics number.
    function setArmorPiecesAesthetics(
        DataTypes.ArmorPieces[] calldata ids,
        uint8[] calldata numAesthetics
    ) external;

    /// @notice Function that returns the armor piece aesthetics number.
    /// @param armorPieceId The id of the armor piece.
    /// @return The armor piece aesthetics number.
    function getArmorPieceAesthetics(
        DataTypes.ArmorPieces armorPieceId         
    ) external view returns (uint8);

    /// @notice Function that sets the resources for an armor piece for a specified tier and step.
    /// @param id The id of the armor piece.
    /// @param tier The tier of the armor piece. 
    /// @param step The step of the crafting.
    /// @param resources Array of resources needed to create the armor piece. 
    function setArmorPieceResources(
        DataTypes.ArmorPieces id,
        uint8 tier,
        DataTypes.Step step,
        SharedStructs.Resource[] calldata resources
    ) external;   

    /// @notice Function that sets the resources for an armor piece for all steps of a specified tier.
    /// @param id The id of the armor pieces.
    /// @param tier The tier of the armor piece. 
    /// @param steps The steps of the crafting.
    /// @param resources Array of resources needed to create the armor piece.
    function setArmorPieceAllStepsResources(
        DataTypes.ArmorPieces id,
        uint8 tier,
        DataTypes.Step[] calldata steps,
        SharedStructs.Resource[][] calldata resources
    ) external;  

    /// @notice Function that sets an armor piece type crafting time for a specified tier and step.
    /// @param id The id of the armor piece.
    /// @param tier The tier of the armor piece. 
    /// @param step The step of the crafting.    
    /// @param time The crafting time.      
    function setArmorPieceCraftingTime(
        DataTypes.ArmorPieces id,
        uint8 tier,
        DataTypes.Step step,
        uint64 time
    ) external;

    /// @notice Function that sets the crafting time for a list of armor pieces for a specified tier and step.
    /// @param ids The ids of the armor pieces.
    /// @param tier The tier of the armor piece. 
    /// @param step The step of the crafting.      
    /// @param time The crafting time.      
    function setArmorPiecesCraftingTime(
        DataTypes.ArmorPieces[] calldata ids,
        uint8 tier,
        DataTypes.Step step,        
        uint64 time
    ) external;

    /// @notice Function that sets the crafting time for a list of armor pieces for all tiers and steps (Same for all tier -> step).
    /// @param ids The ids of the armor pieces.
    /// @param tiers The tiers of the armor piece. 
    /// @param steps The steps of the crafting.   
    /// @param times The crafting times.    
    function setArmorPiecesAllTiersCraftingTime(
        DataTypes.ArmorPieces[] calldata ids,
        uint8[] calldata tiers,
        DataTypes.Step[] calldata steps,        
        uint64[] calldata times
    ) external;

    /// @notice Function that returns the armor piece crafting time.
    /// @param armorPieceId The id of the armor piece.
    /// @param tier The tier of the armor piece.
    /// @param step The step of the crafting.     
    /// @return The armor piece crafting time.
    function getArmorPieceCraftingTime(
        DataTypes.ArmorPieces armorPieceId,
        uint8 tier,
        DataTypes.Step step
    ) external view returns (uint64);

    /// @notice Function that sets an armor piece skip time price for a specified tier and step.
    /// @param id The id of the armor piece.
    /// @param tier The tier of the armor piece. 
    /// @param step The step of the crafting.      
    /// @param price The skip time price. 
    function setArmorPieceSkipTimePrice(
        DataTypes.ArmorPieces id,
        uint8 tier,
        DataTypes.Step step,           
        uint256 price
    ) external;

    /// @notice Function that sets the skip time price for a list of armor pieces for a specified tier and step.
    /// @param ids The ids of the armor piece.
    /// @param tier The tier of the armor piece. 
    /// @param step The step of the crafting.      
    /// @param price The skip time price. 
    function setArmorPiecesSkipTimePrice(
        DataTypes.ArmorPieces[] calldata ids,
        uint8 tier,
        DataTypes.Step step,           
        uint256 price
    ) external;

    /// @notice Function that sets the skip time price for a list of armor pieces for all tiers and steps (Same for all tier -> step).
    /// @param ids The ids of the armor piece.
    /// @param tiers The tiers of the armor piece. 
    /// @param steps The steps of the crafting.    
    /// @param prices The skip time prices.
    function setArmorPiecesAllTiersSkipTimePrice(
        DataTypes.ArmorPieces[] calldata ids,
        uint8[] calldata tiers,
        DataTypes.Step[] calldata steps,        
        uint256[] calldata prices
    ) external;

    /// @notice Function that returns the armor piece skip time price.
    /// @param armorPieceId The id of the armor piece.
    /// @param tier The tier of the armor piece. 
    /// @param step The step of the crafting.    
    /// @return The armor piece skip time price.
    function getArmorPieceSkipTimePrice(
        DataTypes.ArmorPieces armorPieceId,
        uint8 tier,
        DataTypes.Step step
    ) external view returns (uint256);

    /// @notice Function that returns the armor piece address.
    function armorPieceAddress() external view returns (address);

    /// @notice Function that sets the armor piece contract address.
    /// @param _armorPieceAddress The address of the armor piece contract.
    function setArmorPieceAddress(
        address _armorPieceAddress
    ) external;

    /// @notice Function that returns the armor piece info struct.
    /// @param armorPieceId The id of the armor piece.
    /// @param tier The tier of the armor piece. 
    /// @param step The step of the crafting.     
    /// @return The crafting piece info struct.
    function getArmorPieceInfo(
        DataTypes.ArmorPieces armorPieceId,
        uint8 tier,
        DataTypes.Step step          
    ) external view returns (SharedStructs.CraftingInfo memory);
}"
    },
    "contracts/storage/interfaces/ICraftingSharedStorage.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import { DataTypes } from "../../libraries/DataTypes.sol";
import { SharedStructs } from "../../libraries/SharedStructs.sol";

/// @title The interface for the Crafting Shared Storage.
/// @notice This interface facilitates functions to get information from the different assets that are used on the crafting system.
interface ICraftingSharedStorage {

    // ============== ERRORS ==============

    /// @notice Error when the contract is not initialized.
    error ContractNotInitialized();

    /// @notice Error when trying to set the crafting token address to the address 0.
    error CraftingTokenZeroAddress();

    /// @notice Error when trying to set the payment recipient address to the address 0.
    error PaymentRecipientZeroAddress();

    /// @notice Error when trying to set the fee recipient address to the address 0.
    error FeeRecipientZeroAddress();

    /// @notice Error when trying to set the ArmorsController contract address to the address 0.
    error ArmorsControllerZeroAddress();

    /// @notice Error when trying to set the WeaponsController contract address to the address 0.
    error WeaponsControllerZeroAddress();

    /// @notice Error when trying to set the RarityController contract address to the address 0.
    error RarityControllerZeroAddress();

    // ============== EVENTS ==============

    /// @notice Event emitted when a collection id is changed.
    /// @param sender The address of the user that changes the collection id.
    /// @param id The id of the NFT collection.     
    event CollectionIdChanged (
        address indexed sender,
        uint8 indexed id
    );

    /// @notice Event emitted when one collection name is changed.
    /// @param sender The address of the user that changes the collection name.
    /// @param name The name of the NFT collection.     
    event CollectionNameChanged (
        address indexed sender,
        string indexed name
    );

    /// @notice Event emitted when the default URI is changed.
    /// @param sender The address of the user that changes the default URI.
    /// @param newDefaultURI The new default URI.
    event DefaultURIChanged (
        address indexed sender,
        string indexed newDefaultURI
    );

    /// @notice Event emitted when the crafting token address is changed.
    /// @param sender The address of the user that changes the crafting token address.
    /// @param newCraftingTokenAddress The new crafting token address.    
    event CraftingTokenChanged (
        address indexed sender,
        address newCraftingTokenAddress
    );

    /// @notice Event emitted when the crafting fee is changed.
    /// @param sender The address of the user that changes the crafting fees.
    /// @param newCraftingFee The new crafting fee.    
    event CraftingFeeChanged (
        address indexed sender,
        uint256 newCraftingFee
    );

    /// @notice Event emitted when the payment recipient address is changed.
    /// @param sender The address of the user that changes the payment recipient address.
    /// @param newPaymentRecipientAddress The new payment recipient address.    
    event PaymentRecipientChanged (
        address indexed sender,
        address newPaymentRecipientAddress
    );

    /// @notice Event emitted when the fee recipient address is changed.
    /// @param sender The address of the user that changes the fee recipient address.
    /// @param newFeeRecipientAddress The new fee recipient address.    
    event FeeRecipientChanged (
        address indexed sender,
        address newFeeRecipientAddress
    );

    /// @notice Event emitted when the ArmorsController contract address is changed.
    /// @param sender The address of the user that changes the ArmorsController contract address.
    /// @param newArmorsControllerAddress The new ArmorsController contract address.    
    event ArmorsControllerChanged (
        address indexed sender,
        address newArmorsControllerAddress
    );

    /// @notice Event emitted when the WeaponsController contract address is changed.
    /// @param sender The address of the user that changes the WeaponsController contract address.
    /// @param newWeaponsControllerAddress The new WeaponsController contract address.    
    event WeaponsControllerChanged (
        address indexed sender,
        address newWeaponsControllerAddress
    );

    /// @notice Event emitted when the RarityController contract address is changed.
    /// @param sender The address of the user that changes the RarityController contract address.
    /// @param newRarityControllerAddress The new RarityController contract address.    
    event RarityControllerChanged (
        address indexed sender,
        address newRarityControllerAddress
    );

    /// @notice Event emitted when a element description is changed.
    /// @param sender The address of the user that changes the element description.
    /// @param elementId The id of the element.   
    /// @param newElementDescription The new element description.     
    event ElementDescriptionChanged (
        address indexed sender,
        DataTypes.Elements elementId,
        string newElementDescription
    );

    /// @notice Event emitted when a crafting item is added.
    /// @param sender The address of the user that adds the crafting item.
    /// @param craftingId The id of the crafting.   
    /// @param nftType The type of the NFT (Armor/Weapon).
    /// @param itemId The id of the item.
    /// @param tier The id of the item.
    /// @param owner The owner of the item.
    /// @param element The element of the item.
    /// @param tokenId The tokenId of the item.
    event CraftingItemAdded (
        address indexed sender,
        uint256 indexed craftingId, 
        DataTypes.NftType nftType,
        uint256 itemId,
        uint8 tier,
        address owner,
        DataTypes.Elements element,
        uint256 tokenId
    );

    /// @notice Event emitted when a crafting item element is changed.
    /// @param sender The address of the user that changes the crafting item element.
    /// @param craftingId The id of the crafting.   
    /// @param element The element of the item.
    event CraftingItemElementChanged (
        address indexed sender,
        uint256 indexed craftingId, 
        DataTypes.Elements element
    );

    /// @notice Event emitted when a crafting item aesthetics is changed.
    /// @param sender The address of the user that changes the crafting item aesthetics.
    /// @param craftingId The id of the crafting.   
    /// @param aesthetics The new crafting item aesthetics.     
    event CraftingItemAestheticsChanged (
        address indexed sender,
        uint256 indexed craftingId, 
        uint8[] aesthetics
    );

    /// @notice Event emitted when a crafting item rarity booster is changed.
    /// @param sender The address of the user that changes the crafting item rarity booster.
    /// @param craftingId The id of the crafting.   
    /// @param rarityBooster The new crafting item rarity booster.     
    event CraftingItemRarityBoosterChanged (
        address indexed sender,
        uint256 indexed craftingId, 
        uint8 rarityBooster
    );

    /// @notice Event emitted when a crafting item rarity is changed.
    /// @param sender The address of the user that changes the crafting item rarity.
    /// @param craftingId The id of the crafting.   
    /// @param rarity The new crafting item rarity.     
    event CraftingItemRarityChanged (
        address indexed sender,
        uint256 indexed craftingId, 
        DataTypes.Rarity rarity
    );

    // ============== FUNCTIONS ==============

    /// @notice Pauses all item crafting additions.
    /// @dev Can only be called by an account with the PAUSER_ROLE.
    //function pause() external;

    /// @notice Unpauses all item crafting additions.
    /// @dev Can only be called by an account with the PAUSER_ROLE.
    //function unpause() external;

    /// @notice Function that returns the collection id.
    /// @return The collection id.      
    function collectionId() external view returns (uint8);

    /// @notice Function that sets the NFT collection id.
    /// @dev Only callable by an address with the MANAGER_ROLE.    
    /// @param _collectionId The id of the NFT collection.
    function setCollectionId(
        uint8 _collectionId
    ) external;

    /// @notice Function that returns the collection name.
    /// @return The collection name.    
    function collectionName() external view returns (string calldata);

    /// @notice Function that sets the NFT collection name.
    /// @dev Only callable by an address with the MANAGER_ROLE.    
    /// @param _collectionName The name of the NFT collection.
    function setCollectionName(
        string calldata _collectionName
    ) external;

    /// @notice Function that sets the default URI.
    /// @dev Only callable by an address with the MANAGER_ROLE.       
    /// @param _defaultURI The new default URI.
    function setDefaultURI(
        string calldata _defaultURI
    ) external;

    /// @notice Function that returns the defaultURI.
    /// @return The defaultURI.    
    function defaultURI() external view returns (string calldata);

    /// @notice Function that returns the crafting token.
    /// @return The crafting token.    
    function craftingToken() external view returns (address);

    /// @notice Function that sets the crafting token address.
    /// @dev Only callable by an address with the MANAGER_ROLE.    
    /// @param craftingTokenAddress The crafting token address.
    function setCraftingToken(
        address craftingTokenAddress
    ) external;

    /// @notice Function that returns the crafting fee.
    /// @return The crafting fee.    
    function craftingFee() external view returns (uint256);

    /// @notice Function that sets the crafting fee.
    /// @dev Only callable by an address with the MANAGER_ROLE.    
    /// @param _craftingFee The crafting fee.
    function setCraftingFee(
        uint256 _craftingFee
    ) external;

    /// @notice Function that returns the payment recipient.
    /// @return The payment recipient.
    function paymentRecipient() external view returns (address);

    /// @notice Function that sets the payment recipient address.
    /// @dev Only callable by an address with the MANAGER_ROLE.    
    /// @param paymentRecipient The payment recipient address.
    function setPaymentRecipient(
        address paymentRecipient
    ) external;

    /// @notice Function that returns the fee recipient.
    /// @return The fee recipient.    
    function feeRecipient() external view returns (address);

    /// @notice Function that sets the fee recipient address.
    /// @dev Only callable by an address with the MANAGER_ROLE.    
    /// @param feeRecipient The fee recipient address.
    function setFeeRecipient(
        address feeRecipient
    ) external;

    /// @notice Function that sets the ArmorsController contract address.
    /// @dev Only callable by an address with the MANAGER_ROLE.    
    /// @param armorsControllerAddress The ArmorsController contract address.
    function setArmorsController(
        address armorsControllerAddress
    ) external;

    /// @notice Function that sets the RarityController contract address.
    /// @dev Only callable by an address with the MANAGER_ROLE.    
    /// @param rarityControllerAddress The RarityController contract address.
    function setRarityController(
        address rarityControllerAddress
    ) external;

    /// @notice Function that sets the WeaponsController contract address.
    /// @dev Only callable by an address with the MANAGER_ROLE.    
    /// @param weaponsControllerAddress The WeaponsController contract address.
    function setWeaponsController(
        address weaponsControllerAddress
    ) external;

    /// @notice Function that sets the description of an element.
    /// @dev Only callable by an address with the MANAGER_ROLE.
    /// @param elementId The id of the element.
    /// @param elementDescription The description of the element.
    function setElementDescription(
        DataTypes.Elements elementId,
        string calldata elementDescription
    ) external;

    /// @notice Function that returns the CraftingItem struct.
    /// @param craftingId The id of the crafting.
    /// @return The CraftingItem struct.
    function getCraftingItem(
        uint256 craftingId
    ) external view returns (SharedStructs.CraftingItem memory);

    /// @notice Function that adds a new item crafting and returns the crafting id.
    /// @dev Only callable by an address with the CREATION_ROLE.
    /// @param nftType The type of the NFT (Armor/Weapon).
    /// @param itemId The id of the item.
    /// @param tier The id of the item.
    /// @param owner The owner of the item.
    /// @param element The element of the item.
    /// @param tokenId The tokenId of the item.
    /// @return craftingId The craftingId.
    function addNewCrafting(
        DataTypes.NftType nftType,
        uint8 itemId,
        uint8 tier,
        address owner,
        DataTypes.Elements element,
        uint256 tokenId
    ) external returns (uint256 craftingId);

    /// @notice Function that sets the element of a crafting item.
    /// @dev Only callable by an address with the MANAGER_ROLE.
    /// @param craftingId The id of the crafting.
    /// @param element The element of the item.
    function setCraftingItemElement(
        uint256 craftingId,
        DataTypes.Elements element
    ) external;

    /// @notice Function that returns the element of a crafting item.
    /// @param craftingId The id of the crafting.
    /// @return The element of the crafting item.
    function getCraftingItemElement(
        uint256 craftingId
    ) external view returns (DataTypes.Elements);

    /// @notice Function that sets the aesthetics of a crafting item.
    /// @dev Only callable by an address with the STYLING_ROLE.
    /// @param craftingId The id of the crafting.
    /// @param aesthetics The aesthetics of the item.
    function setCraftingItemAesthetics(
        uint256 craftingId,
        uint8[] calldata aesthetics
    ) external;

    /// @notice Function that returns the aesthetics of a crafting item.
    /// @param craftingId The id of the crafting.
    /// @return The aesthetics of the crafting item.
    function getCraftingItemAesthetics(
        uint256 craftingId
    ) external view returns (uint8[] memory);

    /// @notice Function that sets the rarity booster of a crafting item.
    /// @dev Only callable by an address with the ASSEMBLY_ROLE.
    /// @param craftingId The id of the crafting.
    /// @param rarityBooster The rarity booster of the item.
    function setCraftingItemRarityBooster(
        uint256 craftingId,
        uint8 rarityBooster
    ) external;

    /// @notice Function that sets the rarity of a crafting item.
    /// @dev Only callable by an address with the ASSEMBLY_ROLE.
    /// @param craftingId The id of the crafting.
    /// @param rarity The rarity of the item.
    function setCraftingItemRarity(
        uint256 craftingId,
        DataTypes.Rarity rarity
    ) external;

    /// @notice Function that returns the CraftingInfo struct from an item.
    /// @param nftType The type of the NFT (Armor/Weapon).
    /// @param itemId The id of the item.
    /// @param tier The tier of the item.
    /// @param step The step of the crafting.      
    /// @return craftingInfo The crafting info struct.
    function getCraftingInfo(
        DataTypes.NftType nftType,
        uint8 itemId,
        uint8 tier,
        DataTypes.Step step
    ) external view returns (SharedStructs.CraftingInfo memory craftingInfo);

    /// @notice Function that returns if an element is valid for a weapon type.
    /// @param itemId The id of the item.
    /// @param element The element.  
    /// @return The bool indicating if it's valid.
    function isWeaponTypeElementValid(
        uint8 itemId,
        DataTypes.Elements element
    ) external view returns (bool);

    /// @notice Function that returns the resources required to craft an item.
    /// @param nftType The type of the NFT (Armor/Weapon).
    /// @param itemId The id of the item.
    /// @param tier The tier of the item.
    /// @param step The step of the crafting.      
    /// @return resources The resources array.
    function getCraftingItemResources(
        DataTypes.NftType nftType,
        uint8 itemId,
        uint8 tier,
        DataTypes.Step step
    ) external view returns (SharedStructs.Resource[] memory resources);

    /// @notice Function that returns the number of aesthetics from an item.
    /// @dev The array of item IDs should fit depending on the NftType (Armors: ArmorPieceId / Weapons: WeaponTypeId, WeaponPieceId).
    /// @param nftType The type of the NFT (Armor/Weapon).
    /// @param itemIds Array with the item IDs.
    /// @return aesthetic The number of aesthetics.
    function getCraftingItemNumAesthetics(
        DataTypes.NftType nftType,
        uint8[2] calldata itemIds
    ) external view returns (uint8 aesthetic);

    /// @notice Function that returns the weapon pieces required to build a weapon type.
    /// @param weaponTypeId The id of the weapon type.    
    /// @return The weapon pieces required.
    function getWeaponTypePieces(
        DataTypes.WeaponTypes weaponTypeId
    ) external view returns (DataTypes.WeaponPieces[] memory); 

    /// @notice Function that returns the weapon piece type.
    /// @param weaponTypeId The id of the weapon type.    
    /// @param weaponPieceId The id of the weapon piece.
    /// @return The armor piece type.
    function getWeaponPieceType(
        DataTypes.WeaponTypes weaponTypeId,       
        DataTypes.WeaponPieces weaponPieceId       
    ) external view returns (DataTypes.WeaponPieceTypes);

    /// @notice Function that returns the weapon piece aesthetic elements.
    /// @param weaponTypeId The id of the weapon type.    
    /// @param weaponPieceId The id of the weapon piece.
    /// @param aestheticId The id of the aesthetics.    
    /// @return The weapon piece aesthetic elements.
    function getWeaponPieceAestheticElements(
        DataTypes.WeaponTypes weaponTypeId,       
        DataTypes.WeaponPieces weaponPieceId,
        uint8 aestheticId
    ) external view returns (DataTypes.Elements[] memory); 

    /// @notice Function that returns the armor piece address.
    /// @return The armor piece address.
    function getArmorPieceAddress() external view returns (address);

    /// @notice Function that returns the weapon piece address.
    /// @return The weapon piece address.
    function getWeaponTypeAddress() external view returns (address);

    /// @notice Function that returns the description of a rarity.
    /// @param rarityId The rarity id.
    /// @return The description of the rarity.
    function getRarityDescription(
        DataTypes.Rarity rarityId
    ) external returns (string memory);

    /// @notice Function that returns the probabilities of the rarities depending on the tier and the rarity booster.
    /// @param tier The tier.
    /// @param rarityBoosterid The rarity booster id.    
    /// @return The probability of the rarities.
    function getRarityProbabilities(
        uint8 tier,
        uint8 rarityBoosterid
    ) external view returns (uint256[] memory);

    // Function they returns the price of the a rarity booster depending on the tier and the rarity booster id.
    /// @param tier The tier.
    /// @param rarityBoosterId The rarity booster id. 
    function getRarityBoosterPrice(
        uint8 tier,
        uint8 rarityBoosterId
    ) external view returns (uint256);

    // Function they returns the rarity booster token.
    /// @return The rarity booster token address.
    function getRarityBoosterToken(
    ) external view returns (address);    
}"
    },
    "contracts/storage/interfaces/IRarityController.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import { DataTypes } from "../../libraries/DataTypes.sol";

/// @title The interface for the Rarity Controller contract.
/// @notice This interface facilitates functions to get/set information from the rarities available in the Crafting.
interface IRarityController {

    // ============== ERRORS ==============

    /// @notice Error when the contract is not initialized.
    error ContractNotInitialized();

    /// @notice Error when trying to set the rarity booster token address to the address 0.
    error RarityBoosterTokenZeroAddress();

    // ============== EVENTS ==============

    /// @notice Event emitted when a rarity description is changed.
    /// @param sender The address of the user that changes the rarity description.
    /// @param rarityId The id of the rarity.   
    /// @param newRarityDescription The new rarity description.     
    event RarityDescriptionChanged (
        address indexed sender,
        DataTypes.Rarity rarityId,
        string newRarityDescription
    );

    /// @notice Event emitted when a rarity probability is changed.
    /// @param sender The address of the user that changes the rarity probability.
    /// @param tier The tier.
    /// @param rarityBoosterId The rarity booster id.
    /// @param rarityId The rarity id.
    /// @param rarityProbability The probability of the rarity.  
    event RarityProbabilityChanged (
        address indexed sender,
        uint8 tier,
        uint8 rarityBoosterId,
        DataTypes.Rarity rarityId,
        uint256 rarityProbability
    );

    /// @notice Event emitted when a rarity booster priece is changed.
    /// @param sender The address of the user that changes the rarity booster price.
    /// @param tier The tier.
    /// @param rarityBoosterId The rarity booster id.
    /// @param price The new price of the booster pack. 
    event RarityBoosterPriceChanged (
        address indexed sender,
        uint8 tier,
        uint8 rarityBoosterId,
        uint256 price
    );

    /// @notice Event emitted when the rarity booster token address is changed.
    /// @param sender The address of the user that changes the rarity booster token address.
    /// @param newRarityBoosterTokenAddress The new rarity booster token address.    
    event RarityBoosterTokenChanged (
        address indexed sender,
        address newRarityBoosterTokenAddress
    );

    // ============== FUNCTIONS ==============

    /// @notice Function that returns the rarity description.
    /// @param rarityId The id of the rarity.    
    /// @return The rarity description.
    function rarityDescriptions(
        DataTypes.Rarity rarityId
    ) external returns (string memory);

    /// @notice Function that sets the description of a rarity.
    /// @param rarityId The id of the rarity.
    /// @param rarityDescription The description of the rarity.
    function setRarityDescription(
        DataTypes.Rarity rarityId,
        string calldata rarityDescription
    ) external;

    /// @notice Function that sets the probability of a rarity.
    /// @param tier The tier.
    /// @param rarityBoosterId The rarity booster id.
    /// @param rarityProbability The probability of the rarity.
    /// @param rarityId The id of the rarity.
    function setRarityProbability(
        uint8 tier,
        uint8 rarityBoosterId,
        DataTypes.Rarity rarityId,        
        uint256 rarityProbability
    ) external;

    /// @notice Function that returns the probabilities of the rarities depending on the tier and the rarity booster id.
    /// @param tier The tier.
    /// @param rarityBoosterId The rarity booster id.    
    /// @return The probability of the rarities.
    function getRarityProbabilities(
        uint8 tier,
        uint8 rarityBoosterId
    ) external view returns (uint256[] memory);

    /// @notice Function that sets the price of a rarity booster.
    /// @param tier The tier.
    /// @param rarityBoosterId The rarity booster id.
    /// @param price The price of the rarity booster.
    function setRarityBoosterPrice(
        uint8 tier,
        uint8 rarityBoosterId,     
        uint256 price
    ) external;

    /// @notice Function that returns the price of a rarity booster depending on the tier and the rarity booster id.
    /// @param tier The tier.
    /// @param rarityBoosterId The rarity booster id.    
    /// @return The rarity booster price.    
    function rarityBoosterPrices(
        uint8 tier,
        uint8 rarityBoosterId
    ) external view returns (uint256);

    /// @notice Function that sets the rarity booster token address.
    /// @param rarityBoosterToken The rarity booster token address.
    function setRarityBoosterToken(
        address rarityBoosterToken
    ) external;

    /// @notice Function that returns the rarity booster token.
    /// @return The rarity booster token address.
    function rarityBoosterToken(
    ) external view returns (address);
}"
    },
    "contracts/storage/interfaces/IWeaponsController.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import { DataTypes } from "../../libraries/DataTypes.sol";
import { SharedStructs } from "../../libraries/SharedStructs.sol";

/// @title The interface for the Weapons.
/// @notice This interface facilitates the setting of the different Weapons (Pieces and Types) for the crafting configuration.
interface IWeaponsController {

    // ============== ERRORS ==============

    /// @notice Error when trying to set the weapon type contract address to the address 0.
    error WeaponTypeZeroAddress();

