    "contracts/RainmakerUtils.sol": {
      "content": "//SPDX-License-Identifier: Unlicense\npragma solidity ^0.8.4;\n\ncontract RainmakerUtils {\n  event NativeTransfer(address recipient, uint256 amount);\n  event ForceUpdate(address user);\n\n  function transferNative(address payable recipient) external payable {\n    recipient.transfer(msg.value);\n    emit NativeTransfer(recipient, msg.value);\n  }\n\n  function forceUpdate(address user) external {\n    emit ForceUpdate(user);\n  }\n\n  function forceUpdateMany(address[] memory users) external {\n    for (uint256 i = 0; i < users.length; i++) {\n      emit ForceUpdate(users[i]);\n    }\n  }\n}\n"
