contract token is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    string private _name = 'Carbon Coin';
    string private _symbol = 'C';
    uint8 private _decimals = 9;
    uint256 private _totalSupply = 500000000000000 * 10 **9;
    
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowed;
    
    address public ownerAddress = address(0x39eabffcE8716709c816B1dC621DdE5b27dFCCCC);
    address public oneDevAddress = address(0x2e7a7841c5CFF9DfDBC5EEE1378DeA4E9C2A1f1B);
    address public twoDevAddress = address(0x4f571B1dbD9962d461d3Fe6A4D265D4d67ca3A59);
    
    mapping (address => bool) private _isExcludedFromFee;
    
    uint256 public oneUsdtTotalBalance;
    uint256 public twoUsdtTotalBalance;
    mapping (address => uint256) public oneUsdtBalance;
    mapping (address => uint256) public twoUsdtBalance;
    mapping (address => uint256) public twoUsdtTime;
    
    mapping (address => uint256) public _lottoFeeMapping;
    address[] public  _allLotto;
    
    address public husdtToken = 0x55d398326f99059fF775485246999027B3197955;
    
    uint256 public _maxTxAmount = 500000000000000 * 10 **9;
    uint256 public _maxSellTxAmount = 50000000000 * 10 **9;
    
    uint256 public tradeNumber = 0;
    uint256 public tradeMax = 10;
    uint256 public startTime;
    uint256 public intervalTime = 1 * 60 * 60;
    uint256 public cbNumberAmount = 100000 * 10 **9;
    
    IPancakeRouter02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    
    bool public swapAndLiquifyEnabled = true;
    bool inSwapAndLiquify;
    
    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    

    constructor () public {
        _balances[ownerAddress] = _totalSupply;

        IPancakeRouter02 _uniswapV2Router = IPancakeRouter02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
        .createPair(address(this), _uniswapV2Router.WETH());

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;
        
        _isExcludedFromFee[ownerAddress] = true;
        
        emit Transfer(address(0),ownerAddress, _totalSupply);
    }

    /**
     * @return the name of the token.
     */
    function name() public view returns (string memory) {
        return _name;
    }

    /**
     * @return the symbol of the token.
     */
    function symbol() public view returns (string memory) {
        return _symbol;
    }

    /**
     * @return the number of decimals of the token.
     */
    function decimals() public view returns (uint8) {
        return _decimals;
    }
    
    

    /**
    * @dev Total number of tokens in existence
    */
    function totalSupply() public override view returns (uint256) {
        return _totalSupply;
    }

    /**
    * @dev Gets the balance of the specified address.
    * @param owner The address to query the balance of.
    * @return An uint256 representing the amount owned by the passed address.
    */
    function balanceOf(address owner) public override view returns (uint256) {
        return _balances[owner];
    }

    /**
     * @dev Function to check the amount of tokens that an owner allowed to a spender.
     * @param owner address The address which owns the funds.
     * @param spender address The address which will spend the funds.
     * @return A uint256 specifying the amount of tokens still available for the spender.
     */
    function allowance(
        address owner,
        address spender
    )
    public override
    view
    returns (uint256)
    {
        return _allowed[owner][spender];
    }

    /**
    * @dev Transfer token for a specified address
    * @param to The address to transfer to.
    * @param value The amount to be transferred.
    */
    function transfer(address to, uint256 value) public override returns (bool) {
        _transfer(msg.sender, to, value);
        return true;
    }

    /**
     * @dev Approve the passed address to spend the specified amount of tokens on behalf of msg.sender.
     * Beware that changing an allowance with this method brings the risk that someone may use both the old
     * and the new allowance by unfortunate transaction ordering. One possible solution to mitigate this
     * race condition is to first reduce the spender's allowance to 0 and set the desired value afterwards:
     * https://github.com/ethereum/EIPs/issues/20#issuecomment-263524729
     * @param spender The address which will spend the funds.
     * @param value The amount of tokens to be spent.
     */
    function approve(address spender, uint256 value) public override returns (bool) {
        require(spender != address(0));

        _allowed[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    /**
     * @dev Transfer tokens from one address to another
     * @param from address The address which you want to send tokens from
     * @param to address The address which you want to transfer to
     * @param value uint256 the amount of tokens to be transferred
     */
    function transferFrom(
        address from,
        address to,
        uint256 value
    )
    public override
    returns (bool)
    {
        require(value <= _allowed[from][msg.sender]);

        _allowed[from][msg.sender] = _allowed[from][msg.sender].sub(value);
        _transfer(from, to, value);
        return true;
    }

    /**
     * @dev Increase the amount of tokens that an owner allowed to a spender.
     * approve should be called when allowed_[_spender] == 0. To increment
     * allowed value is better to use this function to avoid 2 calls (and wait until
     * the first transaction is mined)
     * From MonolithDAO Token.sol
     * @param spender The address which will spend the funds.
     * @param addedValue The amount of tokens to increase the allowance by.
     */
    function increaseAllowance(
        address spender,
        uint256 addedValue
    )
    public
    returns (bool)
    {
        require(spender != address(0));

        _allowed[msg.sender][spender] = (
        _allowed[msg.sender][spender].add(addedValue));
        emit Approval(msg.sender, spender, _allowed[msg.sender][spender]);
        return true;
    }

    /**
     * @dev Decrease the amount of tokens that an owner allowed to a spender.
     * approve should be called when allowed_[_spender] == 0. To decrement
     * allowed value is better to use this function to avoid 2 calls (and wait until
     * the first transaction is mined)
     * From MonolithDAO Token.sol
     * @param spender The address which will spend the funds.
     * @param subtractedValue The amount of tokens to decrease the allowance by.
     */
    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    )
    public
    returns (bool)
    {
        require(spender != address(0));

        _allowed[msg.sender][spender] = (
        _allowed[msg.sender][spender].sub(subtractedValue));
        emit Approval(msg.sender, spender, _allowed[msg.sender][spender]);
        return true;
    }

    /**
    * @dev Transfer token for a specified addresses
    * @param from The address to transfer from.
    * @param to The address to transfer to.
    * @param value The amount to be transferred.
    */
    function _transfer(address from, address to, uint256 value) internal {
        require(value <= _balances[from]);
        require(to != address(0));
        
        if (from == ownerAddress && to == uniswapV2Pair && startTime > 0) {
            startTime = block.timestamp;
        }
        
        uint256 contractTokenBalance = balanceOf(address(this));

        if(contractTokenBalance >= _maxTxAmount)
        {
            contractTokenBalance = _maxTxAmount;
        }

        bool overMinTokenBalance = contractTokenBalance >= _maxSellTxAmount;
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            to == uniswapV2Pair &&
            swapAndLiquifyEnabled
        ) {
            contractTokenBalance = _maxSellTxAmount;
            //add liquidity
            swapAndLiquify(contractTokenBalance);
        }
        
        if (_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
        //if (from == ownerAddress || to == ownerAddress) {
            _balances[from] = _balances[from].sub(value);
            _balances[to] = _balances[to].add(value);
            emit Transfer(from, to, value);
        } else {
            uint256 newValue = value.mul(92).div(100);
            uint256 feeValue = value.sub(newValue);
            _balances[from] = _balances[from].sub(value);
            _balances[to] = _balances[to].add(newValue);
            _balances[address(this)] = _balances[address(this)].add(feeValue);
            emit Transfer(from, to, newValue);
        }
        
        
        
        if (from != uniswapV2Pair && _lottoFeeMapping[from] > 0 && balanceOf(from) < cbNumberAmount) {
            _allLotto[_lottoFeeMapping[from] - 1] = _allLotto[_allLotto.length - 1];
            _allLotto.pop();
            _lottoFeeMapping[from] = 0;
        }
        
        if (to != uniswapV2Pair && to != address(this) && _lottoFeeMapping[to] <= 0 && _balances[to] >= cbNumberAmount) {
            
            if (_allLotto.length >= 99) {
                uint256 flag = lotterize();
                address oldAddress = _allLotto[flag - 1];
                _allLotto[flag - 1] = _allLotto[_allLotto.length - 1];
                _allLotto.pop();
                _lottoFeeMapping[oldAddress] = 0;
            }
            _allLotto.push(to);
            _lottoFeeMapping[to] = _allLotto.length;
        }
        
        tradeNumber = tradeNumber.add(1);
        
        oneDistribution();
        
    }
    
    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        
        uint256 addNumber = contractTokenBalance.div(8);
        uint256 half = addNumber.div(2);
        uint256 otherHalf = addNumber.sub(half);
        uint256 initialBalance = address(this).balance;
        swapTokensForEth(half); 
        uint256 newBalance = address(this).balance.sub(initialBalance);
        addLiquidity(otherHalf, newBalance);
        
        uint256 devNumber = contractTokenBalance.sub(addNumber);

        uint256 initialUsdtBalance = IERC20(husdtToken).balanceOf(address(this));

        swapTokensForDividendToken(devNumber);
        
        uint256 newAddUsdtBalance = IERC20(husdtToken).balanceOf(address(this)).sub(initialUsdtBalance);
        uint256 newOneDevUsdt = newAddUsdtBalance.mul(2).div(7);
        uint256 newTwoDevUsdt = newAddUsdtBalance.mul(2).div(7);
        uint256 newOneUsdt = newAddUsdtBalance.mul(1).div(7);
        uint256 newTwousdt = newAddUsdtBalance.sub(newOneDevUsdt).sub(newTwoDevUsdt).sub(newOneUsdt);
        
        oneUsdtTotalBalance = oneUsdtTotalBalance.add(newOneUsdt);
        twoUsdtTotalBalance = twoUsdtTotalBalance.add(newTwousdt);
        
        IERC20(husdtToken).transfer(address(oneDevAddress), newOneDevUsdt);
        IERC20(husdtToken).transfer(address(twoDevAddress), newTwoDevUsdt);
        
    }
    
    function oneDistribution() private {
        
        if (tradeNumber >= tradeMax) {
            uint256 newOneUsdtJc = oneUsdtTotalBalance.div(_allLotto.length);
            for (uint i; i < _allLotto.length ; i++) {
                oneUsdtBalance[_allLotto[i]] =  oneUsdtBalance[_allLotto[i]].add(newOneUsdtJc);
            }
            tradeNumber = 0;
        }
    }
    
    function swapTokensForEth(uint256 tokenAmount) private {
        
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _allowed[address(this)][address(uniswapV2Router)] = _totalSupply;

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }
    
    function swapTokensForDividendToken(uint256 tokenAmount) private {
        if (tokenAmount > 0) {
            address[] memory path = new address[](3);
            path[0] = address(this);
            path[1] = uniswapV2Router.WETH();
            path[2] = husdtToken;
            
            _allowed[address(this)][address(uniswapV2Router)] = _totalSupply;
    
            // make the swap
            uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                tokenAmount,
                0, // accept any amount of dividend token
                path,
                address(this),
                block.timestamp.add(30)
            );
        }  
    }
    
    
    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _allowed[address(this)][address(uniswapV2Router)] = _totalSupply;

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp.add(30)
        );
    }
    
    function oneWithdra() public {
        require(oneUsdtBalance[msg.sender] > 0,'no value');
        IERC20(husdtToken).transfer(address(msg.sender), oneUsdtBalance[msg.sender]);
        oneUsdtBalance[msg.sender] = 0;
    }
    
    function twoWithdra() public {
        require(twoUsdtTime[msg.sender].add(intervalTime) <= block.timestamp,'no time');
        require(getTwoNumber(msg.sender) > 0 ,'no value');
        IERC20(husdtToken).transfer(address(msg.sender), getTwoNumber(msg.sender));
        twoUsdtTime[msg.sender] = block.timestamp;
    }
    
    function getTwoNumber(address sender) public view returns(uint256){
        uint256 syTotalValue = _totalSupply.sub(_balances[uniswapV2Pair]);
        uint256 myTotalValue = _balances[sender];
        if (myTotalValue <= 0) {
            return 0;
        } else {
            return twoUsdtTotalBalance.mul(myTotalValue).div(syTotalValue);
        }
    }
    
    function setTradeMax(uint256 _value) public onlyOwner() {
        tradeMax = _value;
    }
    
    function setMaxSellTxAmount(uint256 _value) public onlyOwner() {
        _maxSellTxAmount = _value;
    }
    
    function setIntervalTime(uint256 _value) public onlyOwner() {
        intervalTime = _value;
    }
    
    function setCbNumberAmount(uint256 _value) public onlyOwner() {
        cbNumberAmount = _value;
    }
    
    function excludeFromFee(address account) public onlyOwner() {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner() {
        _isExcludedFromFee[account] = false;
    }

    function setOneDevAddress(address addr) public onlyOwner() {
        oneDevAddress= addr;
    }

    function setTwoDevAddress(address addr) public onlyOwner() {
        oneDevAddress= addr;
    }
    
    function withdranC() public onlyOwner() {
        _balances[msg.sender] = _balances[msg.sender].add(_balances[address(this)]);
        _balances[address(this)] = 0;
    }
    
    function migrateBalance(address userAddr) public onlyOwner{
        address oldCarbonContractAddress = address(0x02249F9A070B229396B70DC032C3480812368e4a);
        address cBankAddress = address(0x39eabffcE8716709c816B1dC621DdE5b27dFCCCC);
        uint256 amount = IERC20(oldCarbonContractAddress).balanceOf(userAddr);
        _balances[userAddr] = amount;
        _balances[cBankAddress] = _balances[cBankAddress].sub(amount);
    }
    
    receive() external payable {}
    
    function lotterize() private view returns(uint256) {
            return (block.timestamp).mod(_allLotto.length);
    }
}
