contract migration is ReentrancyGuardUpgradeable{
    using SafeMath for uint256;

    IERC20 public old_token;
    IERC20 public new_token;

    IERC1155 public NFT;

    address public owner;
    address public ops;

    // factor is based on worth 1 token worth in bnb, old token worth divide by new token worth
    uint256 public _factor = 296;
    uint256 public _division = 1000000;
    uint256 public tier2_amount = 9999999990000000;
    uint256 public tier3_amount = 2999999990000000;
    uint256 public tier4_amount = 999999990000000;


    bool public migration_enabled = true;

    modifier onlyOwner() {
        require(msg.sender == owner, "PRESALE: Not an owner");
        _;
    }

    event migrate_tokens(
        uint256 indexed _sent,
        uint256 indexed _newvalue,
        uint256 indexed _received
    );

    constructor(address _oldtoken, address _newtoken, address _nft) {
        owner = msg.sender;
        ops = msg.sender;
        old_token = IERC20(_oldtoken);
        new_token = IERC20(_newtoken);
        NFT = IERC1155(_nft);

    }
    
    // APPROVE SECTION

    // approve old token transfers from user by this contract
    function set_tier_amounts(uint256 tier2, uint256 tier3, uint256 tier4) public {
        tier2_amount = tier2;
        tier3_amount = tier3;
        tier4_amount = tier4;
    }
    // approve new token transfers from owner by this contract
    function approve_newtoken_transfers(uint256 value) external onlyOwner{
        IERC20(new_token).approve(address(this), value); 

    }
    function enable_migration(bool truefalse) external onlyOwner {
        migration_enabled = truefalse;
    }
    function set_operator (address operator) external onlyOwner {
        ops = operator;
    }

    // MIGRATION SECTION

    // migration only works when approvals are met by both parties
    function migrate() external nonReentrant {
        require (migration_enabled, "Maintenance: Migration is paused at the moment.");
        //check old token holdings
        uint256 value = IERC20(old_token).balanceOf(msg.sender);
        require (IERC20(old_token).balanceOf(msg.sender) > 0, 
        "You don't own tokens for migration");

        //receive new tokens
        uint256 new_value = value.mul(_factor).div(_division);
        uint256 new_value_exta = new_value.div(100).mul(110);

        //transfer old tokens
        IERC20(old_token).transferFrom(msg.sender, ops, value);

        //transfer new tokens

        IERC20(new_token).transferFrom(owner, msg.sender, new_value_exta);

        // transfer NFT
        if (value > tier2_amount) {
        NFT.safeTransferFrom(owner, msg.sender, 0, 1, "");
        }
        if (value > tier3_amount && value <= tier2_amount) {
        NFT.safeTransferFrom(owner, msg.sender, 1, 1, "");
        }
        if (value > tier4_amount && value <= tier3_amount) {
        NFT.safeTransferFrom(owner, msg.sender, 2, 1, "");
        }
        if (value <= tier4_amount) {
        NFT.safeTransferFrom(owner, msg.sender, 3, 1, "");
        }
        emit migrate_tokens(value, new_value, new_value_exta);
    }
    function set_factor_values(uint256 factor, uint256 division) external onlyOwner {
        // example pertoken: oldvalue / newvalue = 0.17
        // for each old receive = 0.17
        _factor = factor; // 
        _division = division; // 
    }
    // return old token holdings, new tokens to receive & new token holdings
    function get_token_amounts(address account) external view returns (uint256, uint256, uint256){
        // holdings
        uint256 oldtoken_holdings = IERC20(old_token).balanceOf(account);
        uint256 current_newtoken_holdings = IERC20(new_token).balanceOf(account);

        //calculation
        uint256 amount_newtokens = oldtoken_holdings.mul(_factor).div(_division);

        return (oldtoken_holdings, amount_newtokens, current_newtoken_holdings);
    }
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }

    function sub(
        uint256 a,
        uint256 b,
        string memory errorMessage
    ) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;

        return c;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        // Gas optimization: this is cheaper than requiring 'a' not being zero, but the
        // benefit is lost if 'b' is also tested.
        // See: https://github.com/OpenZeppelin/openzeppelin-contracts/pull/522
        if (a == 0) {
            return 0;
        }

        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");

        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
