      "content": "// SPDX-License-Identifier: MIT
// Contract by Helena.Financial
pragma solidity >=0.5 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IRouter {
    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);
}

contract HelAuctionDouble is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 public bidToken;
    IERC20 public rewardToken;
    IERC20 public BUSDToken;
    IRouter public router;
    
    address public wallet1;
    address public wallet2;

    uint256[5] public rewards = [500, 750, 1250, 2500, 5000]; // 5%, 7.5, 12.5, 25%, 50%
    uint256[2] public feeDistribution = [5000, 5000];
    uint256 public bidRemoveFee = 1000; // 10% fee
    uint256 public tokenRewardPercent = 100; // 1% on each round
    uint256 public bidMinTokenIncrement = 1e18;
    uint256 public bidMinStartAmount;

    uint256 public auctionDuration = 9600; // 8 hours
    uint256 public auctionExtendDuration = 800; // 40 mins

    uint256 public totalTokensReward;
    uint256 public pendingTokensReward;

    uint256 public round;

    struct BidData {
        address user;
        uint256 bidAmount;
        uint256 rewardAmount;
    }

    struct Round {
        uint256 highestBid;
        uint256 auctionEndBlock;
        uint256 totalBids;
        uint256 adminBidRemoveAmount;
        uint256 tokensReward;
        uint256 numBidsAll;
        bool rewardClaimed;
        bool adminRewardClaimed;
        // uint256 lastBidBlockNumber;
        // uint256 extendBlockNumber;
    }

    mapping(uint => BidData[5]) public userData;
    mapping(uint => Round) public roundData;

    constructor(address _bidToken, address _rewardToken, address _busdToken, address _routerAddress) {
        bidToken = IERC20(_bidToken);
        rewardToken = IERC20(_rewardToken);
        BUSDToken = IERC20(_busdToken);
        router = IRouter(_routerAddress);
    }

    function receiveRewardToken(uint256 amount) external onlyOwner {
        require(totalTokensReward == 0, "Auction: already received reward");
        rewardToken.safeTransferFrom(msg.sender, address(this), amount);
        totalTokensReward = amount;
        pendingTokensReward = amount;
    }

    function makeBid(uint256 _amount, bool _isClaim) external {
        require (totalTokensReward > 0, "Auction: no token to bid");
        if (block.number > roundData[round].auctionEndBlock) {
            pendingTokensReward = pendingTokensReward.sub(roundData[round].tokensReward);
            if (round > 0 && _isClaim) {
                _adminClaim(round);
                _claimReward(round);
            }
            round++;
            roundData[round].tokensReward = pendingTokensReward * tokenRewardPercent / 10000;
            roundData[round].auctionEndBlock = block.number + auctionDuration;
        }
        if (block.number < roundData[round].auctionEndBlock && block.number > roundData[round].auctionEndBlock - auctionExtendDuration) {
            roundData[round].auctionEndBlock = block.number + auctionExtendDuration;
        }
        require (_amount >= bidMinStartAmount && _amount >= roundData[round].highestBid + bidMinTokenIncrement, "Auction: bid must be higher than current amount");
        bidToken.safeTransferFrom(msg.sender, address(this), _amount);
        roundData[round].highestBid = _amount;
        if (roundData[round].totalBids == 5) {
            BidData memory bid = userData[round][0];
            for (uint i = 0; i < roundData[round].totalBids - 1; i++){
                userData[round][i] = userData[round][i+1];
            }
            uint fee = bid.bidAmount * bidRemoveFee / 10000;
            roundData[round].adminBidRemoveAmount += fee;
            bidToken.safeTransfer(bid.user, bid.bidAmount - fee);
            roundData[round].totalBids--;
        }
        userData[round][roundData[round].totalBids] = BidData(msg.sender, _amount, 0);
        roundData[round].totalBids++;
        roundData[round].numBidsAll++;
    }

    // function getTokenAmount() external view {}

    function claimReward(uint _round) external {
        _claimReward(_round);
    }

    function _claimReward(uint _round) internal {
        require (block.number > roundData[_round].auctionEndBlock, "Auction: auction not ended");
        if (roundData[_round].rewardClaimed == true) {
            return;
        }
        uint totalBids = roundData[_round].totalBids;
        if (totalBids == 5) {
            uint256 rewardAmount0 = roundData[_round].tokensReward * rewards[0] / 10000;
            uint256 rewardAmount1 = roundData[_round].tokensReward * rewards[1] / 10000;
            uint256 rewardAmount2 = roundData[_round].tokensReward * rewards[2] / 10000;
            uint256 rewardAmount3 = roundData[_round].tokensReward * rewards[3] / 10000;
            uint256 rewardAmount4 = roundData[_round].tokensReward * rewards[4] / 10000;

            rewardToken.safeTransfer(userData[_round][0].user, rewardAmount0);
            rewardToken.safeTransfer(userData[_round][1].user, rewardAmount1);
            rewardToken.safeTransfer(userData[_round][2].user, rewardAmount2);
            rewardToken.safeTransfer(userData[_round][3].user, rewardAmount3);
            rewardToken.safeTransfer(userData[_round][4].user, rewardAmount4);

            userData[_round][0].rewardAmount = rewardAmount0;
            userData[_round][1].rewardAmount = rewardAmount1;
            userData[_round][2].rewardAmount = rewardAmount2;
            userData[_round][3].rewardAmount = rewardAmount3;
            userData[_round][4].rewardAmount = rewardAmount4;

        } else if (totalBids == 4) {
            uint256 rewardAmount0 = roundData[_round].tokensReward * rewards[1] / 10000;
            uint256 rewardAmount1 = roundData[_round].tokensReward * rewards[2] / 10000;
            uint256 rewardAmount2 = roundData[_round].tokensReward * rewards[3] / 10000;
            uint256 rewardAmount3 = roundData[_round].tokensReward * rewards[4] / 10000;

            rewardToken.safeTransfer(userData[_round][0].user, rewardAmount0);
            rewardToken.safeTransfer(userData[_round][1].user, rewardAmount1);
            rewardToken.safeTransfer(userData[_round][2].user, rewardAmount2);
            rewardToken.safeTransfer(userData[_round][3].user, rewardAmount3);

            userData[_round][0].rewardAmount = rewardAmount0;
            userData[_round][1].rewardAmount = rewardAmount1;
            userData[_round][2].rewardAmount = rewardAmount2;
            userData[_round][3].rewardAmount = rewardAmount3;

        } else if (totalBids == 3) {
            uint256 rewardAmount0 = roundData[_round].tokensReward * rewards[2] / 10000;
            uint256 rewardAmount1 = roundData[_round].tokensReward * rewards[3] / 10000;
            uint256 rewardAmount2 = roundData[_round].tokensReward * rewards[4] / 10000;

            rewardToken.safeTransfer(userData[_round][0].user, rewardAmount0);
            rewardToken.safeTransfer(userData[_round][1].user, rewardAmount1);
            rewardToken.safeTransfer(userData[_round][2].user, rewardAmount2);

            userData[_round][0].rewardAmount = rewardAmount0;
            userData[_round][1].rewardAmount = rewardAmount1;
            userData[_round][2].rewardAmount = rewardAmount2;

        } else if(totalBids == 2) {
            uint256 rewardAmount0 = roundData[_round].tokensReward * rewards[3] / 10000;
            uint256 rewardAmount1 = roundData[_round].tokensReward * rewards[4] / 10000;

            rewardToken.safeTransfer(userData[_round][0].user, rewardAmount0);
            rewardToken.safeTransfer(userData[_round][1].user, rewardAmount1);

            userData[_round][0].rewardAmount = rewardAmount0;
            userData[_round][1].rewardAmount = rewardAmount1;

        } else if(totalBids == 1) {
            uint256 rewardAmount0 = roundData[_round].tokensReward * rewards[4] / 10000;
            rewardToken.safeTransfer(userData[_round][0].user, rewardAmount0);

            userData[_round][0].rewardAmount = rewardAmount0;
        }
        roundData[_round].rewardClaimed = true;
    }

    function adminClaim(uint _round) external onlyOwner {
        _adminClaim(_round);
    }

    function _adminClaim(uint _round) internal {
        require (block.number > roundData[_round].auctionEndBlock, "Auction: auction not ended");
        if (roundData[_round].adminRewardClaimed == true) {
            return;
        }
        uint256 rewardAmount = roundData[_round].adminBidRemoveAmount;
        for (uint i = 0; i < roundData[_round].totalBids; i++){
            rewardAmount += userData[_round][i].bidAmount;
        }
        uint swapAmount = rewardAmount * feeDistribution[0] / 10000;
        uint directAmount = rewardAmount - swapAmount;
        // send fees
        bidToken.safeTransfer(wallet1, swapAmount);
        bidToken.safeTransfer(wallet2, directAmount);
        roundData[_round].adminRewardClaimed = true;
    }

    function claimLastRoundRewards() external {
        _adminClaim(round - 1);
        _claimReward(round - 1);
    }

    function _swap(uint _amount) internal {
        address[] memory path = new address[](2);
        path[0] = address(bidToken);
        path[1] = address(BUSDToken);

        bidToken.approve(address(router), _amount);
        // make the swap
        router.swapExactTokensForTokens(
            _amount,
            0, // accept any amount of BUSD
            path,
            wallet1,
            block.timestamp
        );
    }

    function getUserRoundData(uint _round) external view returns(BidData[5] memory) {
        return userData[_round];
    }

    function setAddresses(address _bidToken, address _rewardToken, address _busdToken, address _router) external onlyOwner {
        bidToken = IERC20(_bidToken);
        rewardToken = IERC20(_rewardToken);
        BUSDToken = IERC20(_busdToken);
        router = IRouter(_router);
    }

    function setWalletAddresses(address _wallet1, address _wallet2) external onlyOwner {
        wallet1 = _wallet1;
        wallet2 = _wallet2;
    }

    function setFeeDistribution(uint[2] memory _feeDistribution) external onlyOwner {
        require(_feeDistribution[0] + _feeDistribution[1] == 10000, "Auction: fee distribution must add up to 100%");
        feeDistribution = _feeDistribution;
    }

    function setRewardVariables(uint[5] memory _rewards, uint _bidRemoveFee) external onlyOwner {
        require(_rewards[0] + _rewards[1] + _rewards[2] + _rewards[3] + _rewards[4] == 10000, "Auction: rewards must add up to 100%");
        rewards = _rewards;
        bidRemoveFee = _bidRemoveFee;
    }

    function setAuctionVariables(uint256 _auctionDuration, uint256 _auctionExtendDuration, uint _bidMinTokenIncrement, uint _tokenRewardPercent) external onlyOwner {
        auctionDuration = _auctionDuration;
        auctionExtendDuration = _auctionExtendDuration;
        bidMinTokenIncrement = _bidMinTokenIncrement;
        tokenRewardPercent = _tokenRewardPercent;
    }

    function setBidMinStartAmount(uint256 _bidMinStartAmount) external onlyOwner {
        bidMinStartAmount = _bidMinStartAmount;
    }

    function emergencyWithdraw(address _token, uint _amount) external onlyOwner {
        IERC20(_token).safeTransfer(msg.sender, _amount);
    }
}"
