contract token is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    mapping (address => uint256) private _balances;

    mapping (address => uint256) private _locked;

    mapping (address => mapping (address => uint256)) private _allowances;

    mapping (address => bool) private _isExcludedFromFee;

    mapping (address => address) private _inviters;

    mapping (address => bool) private _isInviter;

    mapping (address =>bool) public Pairs;

    string private _name = "Health Points Metaverse";
    string private _symbol = "HPM";
    
    uint8 private _decimals = 18;
    uint8 private _i = 6;
    uint256 public _minAmount = 1000;
    uint256 private _totalSupply = 10 *10 ** 8 * 10 ** 18;
    uint256 public _taxFee = 50;
    uint256 public _lpFee = 20;
    uint256 public _burnFee = 10;
    uint256 public _liquidityFee = 10;
    uint256 public _inviterFee = 10;
    uint256 private numTokensSellToAddToLiquidity = 5 * 10**4 * 10**18;
    uint256 public endTokanNum = 1000 * 10 ** 4 * 10 **18;
    
    IUniswapV2Router02 public immutable uniswapV2Router;
    address public   uniswapV2Pair;
    address public _usdtAddress = 0x55d398326f99059fF775485246999027B3197955;//USDT
    address public _lpAddress = 0x0A38C445f9be91B7954Ec6fCf9F8E434B2a6e641;
    address public _burnAddress = address(0);
    address public _liquidityWall = 0x36ed3b0529E85A392Aefe6A24c277EE9c948F749;
    address public pancakeroute = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    
    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = false;
    
    event MinTokensBeforeSwapUpdated(uint256 minTokensBeforeSwap);
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );
    
    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }
    
    constructor () public {
        _balances[_msgSender()] = _totalSupply;
        
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(pancakeroute);
         // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
           .createPair(address(this), _uniswapV2Router.WETH());

        Pairs[uniswapV2Pair] = true;
        Pairs[pancakeroute] = true;
        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;
        
        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        _isInviter[address(this)] = true;
        _isInviter[owner()] = true;
        _isInviter[pancakeroute] = true;
        _isInviter[uniswapV2Pair] = true;
       
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function getInviter(address sender) public view override returns(address[] memory){
        address[]  memory inviter = new address[](_i);
        for(uint8 j=0;j<_i;j++){
           address parent =  getparent(sender);
           if(parent !=address(0)){
               inviter[j] = parent;
               sender = parent;
           } 
        }

        return inviter;
    }

    function getparent (address sender) private view returns (address){
       return _inviters[sender];
    }
	
