    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}


    function _getValues(uint256 tAmount) private view returns (uint256, uint256, uint256, uint256) {
        (uint256 tTransferAmount,,uint256 tLiquidity,uint256 fAmount) = _getTValues(tAmount);
        uint256 tInviter = calculateInviterFee(tAmount);
        tTransferAmount = tTransferAmount.sub(tInviter);
        return (tTransferAmount,tInviter,tLiquidity,fAmount);
    }

    function _getTValues(uint256 tAmount) private view returns (uint256, uint256, uint256,uint256) {
        uint256 tLpFee = calculateLpFee(tAmount);
        uint256 tBurnFee = calculateBurnFee(tAmount);
        uint256 tLiquidity = calculateLiquidityFee(tAmount);
        uint256 tTransferAmount = tAmount.sub(tLpFee).sub(tLiquidity).sub(tBurnFee);
        uint256 fTransferAmount = tAmount.sub(tBurnFee);
        return (tTransferAmount,tLpFee,tLiquidity,fTransferAmount);
    }

    function _getTaxFee(uint256 tAmount) private view returns (uint256, uint256) {
        uint256 rFee = calculateTaxFee(tAmount);
        uint256 rTransferAmount = tAmount.sub(rFee);
        return (rTransferAmount, rFee);
    }

    function maxSellToAddToLiquidity() public view returns (uint256 ){
        return _balances[uniswapV2Pair].mul(5).div(100);// 5% of uniswapV2Pair balance
    }

    
    function _takeLiquidity(uint256 tLiquidity) private {
        _balances[address(this)] = _balances[address(this)].add(tLiquidity);
    }
    
    function calculateTaxFee(uint256 _amount) private  view returns (uint256) {
        if(isBurn())
            return _amount.mul(_taxFee).div(10**3);
        return 0;
    }

    function calculateLiquidityFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_liquidityFee).div(10**3);
    }

    function calculateLpFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_lpFee).div(10**3);
    }

    function calculateInviterFee(uint256 _amount) private view returns (uint256){
        return _amount.mul(_inviterFee).div(10**3);
    }

    function calculateBurnFee(uint256 _amount) private view returns (uint256) {
        if(isBurn())
            return _amount.mul(_burnFee).div(10**3);
        return 0;
    }

    function calculateTotalFee() private view returns (uint256) {
        return (_lpFee + _burnFee + _liquidityFee + _inviterFee);
    }

    function isBurn() public view returns(bool){
        return _totalSupply.sub(_balances[address(0)]) > endTokanNum;
    }

    
    function isExcludedFromFee(address account) public view returns(bool) {
        return _isExcludedFromFee[account];
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        if(_balances[from] - amount <= _minAmount)
            amount = amount.sub(_minAmount);
        uint256 LockAmount = _locked[from];
        uint256 end = _balances[from].sub(LockAmount);
        require(end > amount ,"You should buy first and then sell");

        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap + liquidity lock?
        // also, don't get caught in a circular liquidity event.
        // also, don't swap & liquify if sender is uniswap pair.
        bool isFree = _isExcludedFromFee[from] || _isExcludedFromFee[to];
        uint256 contractTokenBalance = balanceOf(address(this));
        bool overMinTokenBalance = contractTokenBalance >= numTokensSellToAddToLiquidity;
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            !Pairs[from] &&
            swapAndLiquifyEnabled &&
            !isFree
        ) {
            contractTokenBalance = numTokensSellToAddToLiquidity;
            if(contractTokenBalance > maxSellToAddToLiquidity())
                contractTokenBalance = maxSellToAddToLiquidity();
            //add liquidity
            inSwapAndLiquify = true;
            
            if(_liquidityFee >0){
                uint256 swapToken = contractTokenBalance;
                swapAndLiquify(swapToken);
            }

            inSwapAndLiquify = false;
        }

        bool shouldSetInviter = !_isInviter[to];
        if(shouldSetInviter){
            _inviters[to] = from;
            _isInviter[to] = true;
        }
        //indicates if fee should be deducted from transfer
        bool takeFee = true;
        
        //if any account belongs to _isExcludedFromFee account then remove the fee
        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]){
            takeFee = false;
        }
        
        //transfer amount, it will take tax, burn, liquidity fee
        _tokenTransfer(from,to,amount,takeFee);
    }

    function _swapAndSendToFee(uint256 tokens,address to) private {
        uint256 initialBalance = IERC20(_usdtAddress).balanceOf(address(this));
        swapTokensForUsdt(tokens);
        uint256 newBalance = IERC20(_usdtAddress).balanceOf(address(this)).sub(
            initialBalance
        );
        IERC20(_usdtAddress).transfer(to, newBalance);
    }

    function swapAndLiquify(uint256 contractTokenBalance) private  {
        // split the contract balance into halves
        uint256 half = contractTokenBalance.div(2);
        uint256 otherHalf = contractTokenBalance.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract

        uint256 initialBalance = address(this).balance;
        
        // swap tokens for ETH
        swapTokensForEth(otherHalf); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered
        
        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);
        //how much usdt 
        
        // add liquidity to uniswap

        addLiquidityETH(half, newBalance);
        
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function swapTokensForUsdt(uint256 tokenAmount) private {
        address[] memory path = new address[](3);
        path[0] = address(this);
        //path[1] = address(uniswapV2Router.WETH());
        path[1] = address(uniswapV2Router.WETH());
        path[2] = address(_usdtAddress);

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidityETH(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            _liquidityWall,
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAAmount, uint256 tokenBAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), (tokenAAmount+tokenBAmount));

        // add the liquidity
        uniswapV2Router.addLiquidity(
            address(this),
            _usdtAddress,
            tokenAAmount,
            tokenBAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            _liquidityWall,
            block.timestamp
        );
    }

    function _burn(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: burn from the zero address");
        _balances[account] = _balances[account].sub(amount, "ERC20: burn amount exceeds balance");
        _balances[address(0)] =_balances[address(0)].add(amount);
        emit Transfer(account, address(0), amount);
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(address sender, address recipient, uint256 amount,bool takeFee) private {
        
        if (!takeFee) {
            _transferExcluded(sender, recipient, amount);
        } else if (!Pairs[sender] && !Pairs[recipient]) {
            _transferToTax(sender, recipient, amount);
        } else {
            require(swapAndLiquifyEnabled,'Swap not started');
            _transferSwap(sender, recipient, amount);
        }
         
    }

    function _transferToTax(address sender, address recipient, uint256 tAmount) private {
        (uint256 tTransferAmount, uint256 tFee) = _getTaxFee(tAmount);
        _balances[sender] = _balances[sender].sub(tTransferAmount);
        _balances[recipient] = _balances[recipient].add(tTransferAmount);
        _burn(sender,tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferSwap(address sender, address recipient, uint256 tAmount) private {
        (uint256 tTransferAmount,uint256 tInviter ,uint256 tLiquidity ,uint256 fTransferAmount) = _getValues(tAmount);
        if(_lpFee >0){
            uint256 swapToken =  calculateLpFee(tAmount);
            _balances[_lpAddress] = _balances[_lpAddress].add(swapToken);
            emit Transfer(sender, _lpAddress, swapToken);
        }
        if(_burnFee >0 && isBurn()){
            uint256 swapToken = calculateBurnFee(tAmount);
            _burn(sender,swapToken);
        }
        address toInviter = _inviters[sender];
        if(_inviterFee >0 && toInviter != address(0)){
            _balances[toInviter] = _balances[toInviter].add(tInviter);
            emit Transfer(sender, toInviter, tInviter);
        }
        if(_liquidityFee > 0){
            _takeLiquidity(tLiquidity);
        }
        if(Pairs[sender] && _locked[recipient]>0){
            uint256 lock = tTransferAmount > _locked[recipient] ? _locked[recipient] : tTransferAmount;
            _locked[recipient] = _locked[recipient].sub(lock);
        }
        _balances[sender] = _balances[sender].sub(fTransferAmount);
        _balances[recipient] = _balances[recipient].add(tTransferAmount);   
       
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferExcluded(address sender, address recipient, uint256 tAmount) private {
        _balances[sender] = _balances[sender].sub(tAmount);
        
        _balances[recipient] = _balances[recipient].add(tAmount);
                
        emit Transfer(sender, recipient, tAmount);
    }


