contract TokenSwap is ReentrancyGuard  {
    using SafeMath for uint256;
    
    mapping(address => uint256) public balances;
    IBEP20 public oldToken;
    IBEP20 public newToken;
    address public owner;
    bool public depositEnabled = true;
    bool public withdrawEnabled = false;
    bool public renouncedNewTokenWithdrawal = false;
    
    uint256 public divisor = 1;
    uint256 public multiplier = 1000000000;
    mapping(address => bool) public isExcludedFromWait;
    uint256 public totalDeposit;

    constructor(
        IBEP20 _oldToken,
        IBEP20 _newToken
        ) public {
        owner = msg.sender;
        oldToken = _oldToken;
        newToken = _newToken;
    }
    
    function changeNewToken(IBEP20 _newToken) public {
        require(msg.sender == owner);
        newToken = _newToken;
    }
    
    
    function transferOwnership(address _newOwner) public {
        require(msg.sender == owner);
        owner = _newOwner;
    }
    
    function changeDepositStatus(bool _status) public {
        require(msg.sender == owner);
        depositEnabled = _status; 
    }
    
    function changeWithdrawStatus(bool _status) public {
        require(msg.sender == owner);
        withdrawEnabled = _status;
    }
    
    function renounceWithdrawalOfToken() public {
        require(msg.sender == owner);
        renouncedNewTokenWithdrawal = true; 
    }
    
    function changeDivisor(uint256 _newDivisor) public{
        require(msg.sender == owner);
        divisor = _newDivisor;
    }
    function changeMultiplier(uint256 _newMultiplier) public {
        require(msg.sender == owner);
        multiplier = _newMultiplier;
    }
    
    function depositToken(uint256 amount) nonReentrant public {
        require(amount > 0, "invalid amount");
        require(depositEnabled == true, "deposits are disabled");
        uint256 allowance = oldToken.allowance(msg.sender, address(this));
        require(allowance >= amount, "not enough tokens allowed");
        oldToken.transferFrom(msg.sender, address(this), amount);
        totalDeposit = totalDeposit.add(amount);
        balances[msg.sender] = balances[msg.sender].add(amount);
        // uint256 amountNewToken = amount.mul(multiplier).div(divisor); // see below logic 
        // balances[msg.sender] = balances[msg.sender].add(amount); // see below logic 

    }

    
    function withdrawTokens() nonReentrant public {
        require(withdrawEnabled == true || isExcludedFromWait[msg.sender] == true, "not withdrawal time");
        require(balances[msg.sender] > 0, "nothing to withdraw");
        uint256 amount = balances[msg.sender].mul(multiplier).div(divisor); // see below logic
        newToken.transfer(msg.sender, amount);
        balances[msg.sender] = 0; 

        // old  token has 18 decimals  
        // new  token has 9 decimals 
        // for each token commited, we multiply by Y to get the equivalent in new token 
        // for example if old has 9 and new has 18, we multiply by 1000000000
        // if old has 18 and new has 9, we multiply by 1 and divide by 1000000000
    }
    
    function withdrawOldTokens(uint256 amount) public {
        require(msg.sender == owner);
        oldToken.transfer(msg.sender, amount);
    }

    function setWaitStatus(address _user, bool _status) public {
        require(msg.sender == owner);
        isExcludedFromWait[_user] = _status;
    }
    
    function withdrawNewTokens(uint256 amount) public {
        require(msg.sender == owner); 
        require(renouncedNewTokenWithdrawal == false);
        newToken.transfer(msg.sender, amount);
    }
    
    
}
