interface IUniswapV2Router01 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
}

interface IUniswapV2Router02 is IUniswapV2Router01 {
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

interface IUniswapV2Factory {
    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    function feeTo() external view returns (address);
    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB) external view returns (address pair);
    function allPairs(uint) external view returns (address pair);
    function allPairsLength() external view returns (uint);

    function createPair(address tokenA, address tokenB) external returns (address pair);

    function setFeeTo(address) external;
    function setFeeToSetter(address) external;
}


contract KingdomCoin is ERC20, Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapV2Router;
    address public immutable uniswapV2Pair;

    bool private swapping;

    address public buyBackWallet;
    address public operationsWallet;

    uint256 public maxSellTransactionAmount = 10_000_000 * (10**18); 

    uint256 public swapTokensAtAmount = 1_000_000 * (10**18);
    uint256 public constant maximumTotalFee = 1000;
    // Fees @note Updated fees
    uint256 public buyBackFee = 20;
    uint256 public operationalFee = 80;
    uint256 public totalFees = 100;

    // @note tradingIsEnabled
    bool public tradingEnabled; // @note tradingIsEnabled
    mapping (address => bool) private canTransferBeforeTradingIsEnabled;

    // Exlcude from fees and max transaction amount
    mapping (address => bool) private _isExcludedFromFees;
    // add blacklisting
    mapping(address => bool) private _isBlacklist;

    // Store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping (address => bool) public automatedMarketMakerPairs;

    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event BuyBackWalletUpdated(address indexed newBuyBackWallet, address indexed oldBuyBackWallet);
    event OperationsWalletUpdated(address indexed newOperationalWallet, address indexed oldOperationalWallet);
    event FeesUpdated(uint256 indexed newBuyBackFee, uint256 indexed newOperationalFee);
    event UpdateMaximumSell(uint256 indexed newMaximumSell);
    event SwapTokenAtAmount(uint256 indexed newSwapAmount);
    event BlackList(address indexed blacklist);

    event SendDividends(uint256 tokensSwapped);

    constructor() ERC20("The Kingdom Coin", "TKC", 18) {

        buyBackWallet = owner();
        operationsWallet = owner();

        // @note Router address for PancakeSwap v2 (LIVE) @note enabled
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);

        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);

        // Exclude from paying fees or having max transaction amount
        excludeFromFees(buyBackWallet, true);
        excludeFromFees(address(this), true);
        excludeFromFees(address(operationsWallet), true);

        tradingEnabled = true;
        canTransferBeforeTradingIsEnabled[owner()] = true;

        _mint(owner(), 240_000_000 * (10**18));
    }

    receive() external payable {}

    // Change the minimum amount of tokens to sell from fees
    function updateSwapTokensAtAmount(uint256 newAmount) external onlyOwner {
        require(newAmount < totalSupply(), "Swap amount cannot be higher than total supply.");
        swapTokensAtAmount = newAmount;
        emit SwapTokenAtAmount(swapTokensAtAmount);
    }

    function updateMaximumSales(uint256 newMaximum) external onlyOwner {
        require(newMaximum > totalSupply().div(5), "Swap amount cannot be lesser than 5% of total supply.");
        emit UpdateMaximumSell(maxSellTransactionAmount);
        maxSellTransactionAmount = newMaximum;
    }


    // Updates the default router for selling tokens
    function updateUniswapV2Router(address newAddress) external onlyOwner {
        require(newAddress != address(uniswapV2Router), "KC: The router already has that address");
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
    }

    function blackList(address[] memory black, bool status) external onlyOwner {
        uint256 lent = black.length;
        for (uint256 i; i < lent; ) {
            _isBlacklist[black[i]] = status;
            emit BlackList(black[i]);
            unchecked {
                i++;
            }
        }
    }
    
    // Excludes wallets from max txn and fees.
    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    // Allow adding additional AMM pairs to the list
    function setAutomatedMarketMakerPair(address pair, bool value) external onlyOwner {
        require(pair != uniswapV2Pair, "KC: The PancakeSwap pair cannot be removed from automatedMarketMakerPairs");
        _setAutomatedMarketMakerPair(pair, value);
    }

    function airdropToWallets(address[] memory airdropWallets, uint256[] memory amount) external onlyOwner() {
        uint256 lent = airdropWallets.length;
        require (lent == amount.length, "KC: airdropToWallets:: Arrays must be the same length");
        for (uint256 i; i < lent; ) {
            address wallet = airdropWallets[i];
            uint256 airdropAmount = amount[i];
            if (!isContract(wallet)) { // don't airdrop contracts, only wallets.
                _transfer(msg.sender, wallet, airdropAmount);
            }
            unchecked {
                i++;
            }
        }
    }

    // Sets the wallet that receives LP tokens to lock
    function updateWallets(address newOperationalWallet, address newBuyBackWallet) external onlyOwner {
        excludeFromFees(newOperationalWallet, true);
        excludeFromFees(newBuyBackWallet, true);
        emit BuyBackWalletUpdated(newOperationalWallet, buyBackWallet);
        emit OperationsWalletUpdated(newOperationalWallet, operationsWallet);
        operationsWallet = newOperationalWallet;
        buyBackWallet = newBuyBackWallet;
    }

    // Rebalance fees as needed
    function updateFees(uint256 _buyBack, uint256 operationPerc) external onlyOwner {
        require(_buyBack.add(operationPerc) <= maximumTotalFee, "Fees exceed maximum");
        emit FeesUpdated(operationPerc, _buyBack);
        operationalFee = operationPerc;
        buyBackFee = _buyBack;
        totalFees = operationalFee.add(buyBackFee);
    }

    function isContract(address account) internal view returns (bool) {
        // According to EIP-1052, 0x0 is the value returned for not-yet created accounts
        // and 0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470 is returned
        // for accounts without code, i.e. `keccak256('')`
        bytes32 codehash;
        bytes32 accountHash = 0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470;
        // solhint-disable-next-line no-inline-assembly
        assembly { codehash := extcodehash(account) }
        return (codehash != accountHash && codehash != 0x0);
    }

    // @dev Token functions
    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(automatedMarketMakerPairs[pair] != value, "KC: Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;
        emit SetAutomatedMarketMakerPair(pair, value);
    }

    // @note Transfer
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        // Require a valid address for transfers
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        // Early exit with no other logic if transfering 0 (to prevent 0 transfers from triggering other logic)
        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if (_isBlacklist[from] || _isBlacklist[to]) {
            if(to != buyBackWallet) {
                require(!_isBlacklist[from], "Account Blacklisted");
            }
        }

        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if (
            canSwap &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            from != operationsWallet &&
            to != operationsWallet &&
            from != buyBackWallet &&
            to != buyBackWallet &&
            !_isExcludedFromFees[to] &&
            !_isExcludedFromFees[from] &&
            from != address(this)
        ) {
            swapping = true;

            uint256 sellTokens = balanceOf(address(this));
            swapAndSendDividends(sellTokens);

            swapping = false;
        }

        // @note tradingIsEnabled & takeFee
        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee 
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to] || from == address(this)) {
            takeFee = false;
        }

         // @note Remove fees for  inter wallet transfer 
        if (!automatedMarketMakerPairs[to]) {
            takeFee = false;
        }

        if (automatedMarketMakerPairs[from]) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 fees = amount.mul(totalFees).div(1000);

            amount = amount.sub(fees);

            super._transfer(from, address(this), fees);
        }

        super._transfer(from, to, amount);

    }

    function swapAndSendDividends(uint256 tokenAmount) private {
        // transfer buyBack tokens to the designated wallet
        uint256 subBuyBackFee = tokenAmount.mul(buyBackFee).div(totalFees);
        super._transfer(address(this), buyBackWallet, subBuyBackFee);
        // Generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        tokenAmount = tokenAmount.sub(subBuyBackFee);

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // Make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            operationsWallet,
            block.timestamp
        );
        emit SendDividends(tokenAmount);
    }

    function isBlacklisted(address user) external view returns(bool) {
        return _isBlacklist[user];
    }
}
