    "contracts/NFT/Interface/InterfaceNFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.4.0 <=0.8.19;

import "erc721psi/contracts/ERC721Psi.sol";

abstract contract InterfaceNFT is ERC721Psi {
    struct Gego {
        uint256 id;
        uint256 amount;
        address author;
    }

    function mint(address to, uint256 quantity) external virtual returns (bool);

    function tokensOfOwner(
        address owner
    ) external view virtual override returns (uint256[] memory) {}
}
"
