pragma solidity ^0.8.7;


contract EcosystemToken is ERC20 {
    using Address for address;

    event Mint(address to, uint256 balance);
    event Burn(address sender, uint256 balance);

    constructor(address[] memory accounts, uint256[] memory balances) ERC20('Pomerium Ecosystem Token', 'PMG') {
        require(accounts.length == balances.length, 'params must be the same length');
        for(uint256 i = 0 ; i < accounts.length ; i++) {
            _mint(accounts[i], balances[i]);

            emit Mint(accounts[i], balances[i]);
        }
    }

    function burn(uint256 balance) public {
        _burn(_msgSender(), balance);

        emit Burn(_msgSender(), balance);
    }
}
