interface IPancakePair {
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
    event Transfer(address indexed from, address indexed to, uint256 value);

    function name() external pure returns (string memory);

    function symbol() external pure returns (string memory);

    function decimals() external pure returns (uint8);

    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function allowance(address owner, address spender)
        external
        view
        returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transfer(address to, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);

    function PERMIT_TYPEHASH() external pure returns (bytes32);

    function nonces(address owner) external view returns (uint256);

    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    event Mint(address indexed sender, uint256 amount0, uint256 amount1);
    event Burn(
        address indexed sender,
        uint256 amount0,
        uint256 amount1,
        address indexed to
    );
    event Swap(
        address indexed sender,
        uint256 amount0In,
        uint256 amount1In,
        uint256 amount0Out,
        uint256 amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint256);

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );

    function price0CumulativeLast() external view returns (uint256);

    function price1CumulativeLast() external view returns (uint256);

    function kLast() external view returns (uint256);

    function mint(address to) external returns (uint256 liquidity);

    function burn(address to)
        external
        returns (uint256 amount0, uint256 amount1);

    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external;

    function skim(address to) external;

    function sync() external;

    function initialize(address, address) external;
}

contract FogToken is ERC20, Ownable {
    using Address for address;

    uint256 public buyFee;

    uint256 public sellFee;

    address public buyPreAddress;

    address public sellPreAddress;

    mapping(address => bool) public withOutFee;
    mapping(address => bool) public blackList;

    event AddBlackUser(address indexed account, uint256 time);
    event RemoveBlackUser(address indexed account, uint256 time);

    event AddWhiteUser(address indexed account, uint256 time);
    event RemoveWhiteUser(address indexed account, uint256 time);

    constructor(address _account) ERC20("FogDao Token", "FOG") {
        _mint(_account, 1000000e18);

        sellFee = 0.01e12;
        buyFee = 0.01e12;
        buyPreAddress = _account;
        sellPreAddress = _account;
    }

    function setSellFee(uint256 _sellFee) external onlyOwner {
        require(_sellFee < 1e12, "sellFee must lt 1e12");
        sellFee = _sellFee;
    }

    function setBuyFee(uint256 _buyFee) external onlyOwner {
        require(_buyFee < 1e12, "sellFee must lt 1e12");
        buyFee = _buyFee;
    }

    function setBuyPreAddress(address _buyPreAddress) external onlyOwner {
        require(_buyPreAddress != address(0), "wrong address");
        buyPreAddress = _buyPreAddress;
    }

    function setSellPreAddress(address _sellPreAddress) external onlyOwner {
        require(_sellPreAddress != address(0), "wrong address");
        sellPreAddress = _sellPreAddress;
    }

    function _checkOnSwapPair(address pair) public view returns (bool) {
        if (pair.isContract()) {
            try IPancakePair(pair).token0() returns (address token0) {
                if (token0 == address(this)) {
                    return true;
                } else {
                    try IPancakePair(pair).token1() returns (address token1) {
                        return token1 == address(this);
                    } catch {
                        return false;
                    }
                }
            } catch {
                return false;
            }
        } else {
            return false;
        }
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(!blackList[from], "from address is locked.");
        require(!blackList[to], "to address is locked.");

        uint256 sellFeeAmount;
        uint256 buyFeeAmount;

        if (!withOutFee[from] && !withOutFee[to]) {
            if (_checkOnSwapPair(to)) {
                sellFeeAmount = (amount * sellFee) / 1e12;
                if (sellFeeAmount > 0) {
                    super._transfer(from, sellPreAddress, sellFeeAmount);
                }
            }
            if (_checkOnSwapPair(from)) {
                buyFeeAmount = (amount * buyFee) / 1e12;
                if (buyFeeAmount > 0) {
                    super._transfer(from, buyPreAddress, buyFeeAmount);
                }
            }
        }

        super._transfer(from, to, amount - sellFeeAmount - buyFeeAmount);
    }

    function addBlackUser(address[] calldata accounts) external onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            if (!blackList[accounts[i]]) {
                blackList[accounts[i]] = true;
                emit AddBlackUser((accounts[i]), block.timestamp);
            }
        }
    }

    function removeBlackUser(address[] calldata accounts) external onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            if (blackList[accounts[i]]) {
                blackList[accounts[i]] = false;
                emit RemoveBlackUser(accounts[i], block.timestamp);
            }
        }
    }

    function addWhiteUser(address[] calldata accounts) external onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            if (!withOutFee[accounts[i]]) {
                withOutFee[accounts[i]] = true;
                emit AddWhiteUser(accounts[i], block.timestamp);
            }
        }
    }

    function removeWhiteUser(address[] calldata accounts) external onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            if (withOutFee[accounts[i]]) {
                delete withOutFee[accounts[i]];
                emit RemoveWhiteUser(accounts[i], block.timestamp);
            }
        }
    }

    function clim(address token) external onlyOwner {
        IERC20(token).transfer(
            msg.sender,
            IERC20(token).balanceOf(address(this))
        );
    }
}
