    "contracts/eacreward.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./IPancakeRouter02.sol";
import "./IPancakePair.sol";

interface EACBank {
    function depositInfo(address owner, uint256 orderId) external view returns(string memory _type,uint256 lockAmount,uint256 unlockTime,uint256 takeRewardTime,uint256 takeRewardNum, uint256 takeRewardAll,uint256 lockPeriod);

    function platformInfo(string memory _type) external view returns(uint256 totalLock,uint256 lockPeriod,address rewardToken);

    function subTotalLock(uint256 _unlockAmount) external;

    function setPeriodRewardNum(address _owner, uint256 _orderId) external;
}

contract EACDepositReward is ReentrancyGuard {

    event TakeRewardEvent(address owner, uint256 amount, uint256 orderId, uint256 lockPeriod,uint256 amountUSD);
    event GrantRewardEvent(uint256 period, uint256 amount);

    address constant public rewardToken = 0x3423dE0Fd15356841493188B93eeD1AB7e3B54De;
    address immutable bank;
    uint256 constant public dividendTime = 432000;

    constructor(address _bank) {
        bank = _bank;
    }

    function getReward(address _owner, uint256 _orderId) public view returns(uint256) {
        (,uint256 lockAmount,,uint256 takeRewardTime,uint256 takeRewardNum,uint256 _takeRewardAll,) = EACBank(bank).depositInfo(_owner, _orderId);

        if (block.timestamp < (takeRewardTime + dividendTime) || takeRewardNum >= _takeRewardAll) {
            return 0;
        }

        uint256 totalLock = IERC20(rewardToken).balanceOf(bank);
        uint256 rewardAll = IERC20(rewardToken).balanceOf(address(this));
        return lockAmount * rewardAll / totalLock;
    }

    function takeReward(uint256 _orderId) public {
        uint256 rewardAmount = getReward(msg.sender, _orderId);
        require(rewardAmount > 0);
        (,,,,,,uint256 lockPeriod) = EACBank(bank).depositInfo(msg.sender, _orderId);
        emit TakeRewardEvent(msg.sender, rewardAmount, _orderId, lockPeriod, getEACPrice(rewardAmount));
        uint256 fee = rewardAmount * 5 / 100;
        EACBank(bank).setPeriodRewardNum(msg.sender,_orderId);
        IERC20(rewardToken).transfer(msg.sender, rewardAmount - fee);
        IERC20(rewardToken).transfer(0x71cc8A8b0aDFD2352ad91882F42D983436B54fEA, fee);
    }

    function takeRewardAll(uint256[] calldata orderIds) external nonReentrant {
        uint256 length = orderIds.length;
        for (uint256 i; i < length; ++i) {
            takeReward(orderIds[i]);
        }
    }

    function getEACPrice (uint256 _amount) public view returns(uint256) {
        address[] memory path = new address[](2);
        path[0] = address(0x3423dE0Fd15356841493188B93eeD1AB7e3B54De);
        path[1] = address(0x55d398326f99059fF775485246999027B3197955);
        uint256[] memory amounts = IPancakeRouter02(address(0x10ED43C718714eb63d5aA57B78B54704E256024E)).getAmountsOut(_amount, path);
        return amounts[1];
    }
}"
