    constructor(
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        uint256 initialBalance_,
        address payable feeReceiver_
    ) payable BEP20(name_, symbol_) ServicePayer(feeReceiver_, "BurnableBEP20") {
