pragma solidity 0.8.9;

contract BatchNftTransfer {
  function _batchTransfer(
    address _contract,
    address _from,
    address _to,
    uint256[] memory _tokenIds
  ) public {
    for (uint256 i = 0; i < _tokenIds.length; i++) {
      MHIERC721(_contract).safeTransferFrom(_from, _to, _tokenIds[i]);
    }
  }
}
