// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Bobo {
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalSupply;
    address payable public owner;
    uint256 public buyFee;
    uint256 public sellFee;

    mapping(address => uint256) public balanceOf;

    event Transfer(address indexed from, address indexed to, uint256 value);

    constructor() {
        name = "Bobo";
        symbol = "BOBO";
        decimals = 18;
        totalSupply = 1000000000000000000 * 10**uint256(decimals);
        owner = payable(msg.sender);
        setFees(8, 8);
        balanceOf[msg.sender] = totalSupply;
    }

    function setFees(uint256 _buyFee, uint256 _sellFee) public {
        require(msg.sender == owner, "Only the contract owner can set fees");
        require(_buyFee <= 100 && _sellFee <= 100, "Fees must be less than or equal to 100%");
        buyFee = _buyFee;
        sellFee = _sellFee;
    }

    function transfer(address to, uint256 value) external returns (bool) {
        require(value <= balanceOf[msg.sender], "Insufficient balance");

        uint256 fee = (value * sellFee) / 100;
        uint256 amount = value - fee;

        balanceOf[msg.sender] -= value;
        balanceOf[to] += amount;

        emit Transfer(msg.sender, to, amount);
        emit Transfer(msg.sender, owner, fee);

        owner.transfer(fee); // Send the fee to the contract owner's wallet

        return true;
    }
}
