}

/**
 * @title SafeBEP20
 * @dev Wrappers around BEP20 operations that throw on failure (when the token
 * contract returns false). Tokens that return no value (and instead revert or
 * throw on failure) are also supported, non-reverting calls are assumed to be
 * successful.
 * To use this library you can add a `using SafeBEP20 for IBEP20;` statement to your contract,
 * which allows you to call the safe operations as `token.safeTransfer(...)`, etc.
 */
library SafeBEP20 {
    using Address for address;

    function safeTransfer(
        IBEP20 token,
        address to,
        uint256 value
    ) internal {
        _callOptionalReturn(token, abi.encodeWithSelector(token.transfer.selector, to, value));
    }

    function safeTransferFrom(
        IBEP20 token,
        address from,
        address to,
        uint256 value
    ) internal {
        _callOptionalReturn(token, abi.encodeWithSelector(token.transferFrom.selector, from, to, value));
    }

    /**
     * @dev Deprecated. This function has issues similar to the ones found in
     * {IBEP20-approve}, and its usage is discouraged.
     *
     * Whenever possible, use {safeIncreaseAllowance} and
     * {safeDecreaseAllowance} instead.
     */
    function safeApprove(
        IBEP20 token,
        address spender,
        uint256 value
    ) internal {
        // safeApprove should only be called when setting an initial allowance,
        // or when resetting it to zero. To increase and decrease it, use
        // 'safeIncreaseAllowance' and 'safeDecreaseAllowance'
        require(
            (value == 0) || (token.allowance(address(this), spender) == 0),
            "SafeBEP20: approve from non-zero to non-zero allowance"
        );
        _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, value));
    }

    function safeIncreaseAllowance(
        IBEP20 token,
        address spender,
        uint256 value
    ) internal {
        uint256 newAllowance = token.allowance(address(this), spender) + value;
        _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, newAllowance));
    }

    function safeDecreaseAllowance(
        IBEP20 token,
        address spender,
        uint256 value
    ) internal {
        unchecked {
            uint256 oldAllowance = token.allowance(address(this), spender);
            require(oldAllowance >= value, "SafeBEP20: decreased allowance below zero");
            uint256 newAllowance = oldAllowance - value;
            _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, newAllowance));
        }
    }

    /**
     * @dev Imitates a Solidity high-level call (i.e. a regular function call to a contract), relaxing the requirement
     * on the return value: the return value is optional (but if data is returned, it must not be false).
     * @param token The token targeted by the call.
     * @param data The call data (encoded using abi.encode or one of its variants).
     */
    function _callOptionalReturn(IBEP20 token, bytes memory data) private {
        // We need to perform a low level call here, to bypass Solidity's return data size checking mechanism, since
        // we're implementing it ourselves. We use {Address.functionCall} to perform this call, which verifies that
        // the target address contains contract code and also asserts for success in the low-level call.

        bytes memory returndata = address(token).functionCall(data, "SafeBEP20: low-level call failed");
        if (returndata.length > 0) {
            // Return data is optional
            require(abi.decode(returndata, (bool)), "SafeBEP20: BEP20 operation did not succeed");
        }
    }
}

interface IERC1155Mintable {
    function mint(
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) external;

    function burn(
        address account,
        uint256 id,
        uint256 value
    ) external;

    function balanceOf(
        address account, 
        uint256 id
    ) external returns (uint256);
}

contract WSGActivationPoolV2 is ReentrancyGuard, Pausable {
    using SafeBEP20 for IBEP20Metadata;

    uint256 public lockingPeriod;
    IBEP20Metadata public immutable stakingToken;
    IERC1155Mintable public immutable nftContract;
    uint256 public tokenId;
    uint256 public activationPrice;

    address public treasury;

    struct DepositInfo {
        uint256 amount;
        uint256 unlockedAt;
        bool withdrawn;
    }

    uint256 private _totalDeposited;
    uint256 private _totalUsers;

    // tracks user deposits
    mapping(address => DepositInfo) private _deposits;

    // CONSTRUCTOR

    constructor(
        address _stakingToken,
        uint256 _lockingPeriod,
        uint256 _tokenId
    ) {
        lockingPeriod = _lockingPeriod;
        stakingToken = IBEP20Metadata(_stakingToken);
        nftContract = IERC1155Mintable(0xe86E4b3bB1846a017153CedCD0458dc9Ad835D9b);
        treasury = 0xF33c68753C09f53496455AAf7f12250Fbd014d4b;
        tokenId = _tokenId;
    }

    // VIEWS

    function totalDeposited() external view returns (uint256) {
        return _totalDeposited;
    }

    function totalUsers() external view returns (uint256) {
        return _totalUsers;
    }

    function depositOf(address account) external view returns (DepositInfo memory) {
        return _deposits[account];
    }

    // PUBLIC FUNCTIONS

    function deposit()
        external
        nonReentrant
        notPaused
    {
        require(_deposits[msg.sender].amount == 0, "!deposited");
        require(stakingToken.balanceOf(msg.sender) >= activationPrice, "!balance");

        uint256 balBefore = stakingToken.balanceOf(treasury);
        stakingToken.safeTransferFrom(msg.sender, address(treasury), activationPrice);
        uint256 balAfter = stakingToken.balanceOf(treasury);
        uint256 actualReceived = balAfter - balBefore;

        _totalDeposited = _totalDeposited + actualReceived;
        _totalUsers = _totalUsers + 1;
        _deposits[msg.sender] = DepositInfo(actualReceived, block.timestamp + lockingPeriod, false);
        
        emit Deposited(msg.sender, actualReceived);
    }

    function claim()
        external
        nonReentrant
        notPaused
    {
        require(!_deposits[msg.sender].withdrawn, "!withdrawn");
        require(block.timestamp >= _deposits[msg.sender].unlockedAt);
        _deposits[msg.sender].withdrawn = true;
        bytes memory data = new bytes(0);
        nftContract.mint(msg.sender, tokenId, 1, data);
        emit NFTClaimed(msg.sender, tokenId, 1);
    }

    // RESTRICTED FUNCTIONS

    function recoverBEP20(address tokenAddress, uint256 tokenAmount)
        external
        restricted
    {
        require(
            tokenAddress != address(stakingToken),
            "Cannot withdraw the staking tokens"
        );
        IBEP20Metadata(tokenAddress).safeTransfer(treasury, tokenAmount);
        emit Recovered(tokenAddress, tokenAmount);
    }

    function recoverBNB()
        external
        restricted
    {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            payable(treasury).transfer(address(this).balance);
            emit RecoveredBNB(balance);
        }
    }

    function setLockingPeriod(uint256 _lockingPeriod) external restricted {
        lockingPeriod = _lockingPeriod;
        emit LockingPeriodUpdated(_lockingPeriod);
    }

    function setActivationPrice(uint256 _activationPrice) external restricted {
        activationPrice = _activationPrice * 10**stakingToken.decimals();
        emit ActivationPriceUpdated(_activationPrice * 10**stakingToken.decimals());
    }

    function setTokenId(uint256 _tokenId) external restricted {
        tokenId = _tokenId;
        emit TokenIdUpdated(_tokenId);
    }

    function setTreasury(address _treasury) external restricted {
        require(_treasury != address(0), "!null");
        treasury = _treasury;
        emit TreasuryUpdated(_treasury);
    }

    // *** MODIFIERS ***

    modifier restricted {
        require(
            msg.sender == owner(),
            '!restricted'
        );

        _;
    }

    // EVENTS
    event Deposited(address indexed user, uint256 amount);
    event NFTClaimed(address indexed user, uint256 tokenId, uint256 count);
    event LockingPeriodUpdated(uint256 lockingPeriod);
    event ActivationPriceUpdated(uint256 activationPrice);
    event TokenIdUpdated(uint256 tokenId);
    event NftContractUpdated(address nftContract);
    event TreasuryUpdated(address treasury);
    event Recovered(address token, uint256 amount);
    event RecoveredBNB(uint256 amount);
}
