pragma solidity 0.8.9;

import './ERC20.sol';
import "./utils/ReentrancyGuard.sol";

contract ElCoinV2 is ERC20, ReentrancyGuard {
    modifier onlyOperator{
        require(_operators[_msgSender()], "Forbidden");
        _;
    }

    modifier canSwap{
        require(_canSwap, "Can not swap now");
        _;
    }

    mapping(address => bool) public _operators;

    IERC20 public _elCoinV1Token;
    bool public _canSwap;

    mapping(address => bool) public _isLimitedAddress;
    uint256 public _limitedAmount;

    constructor(IERC20 elCoinV1Token) ERC20("ElCoin Token", "ELCOIN", 0){
        require(address(elCoinV1Token) != address(0), "elCoinV1Token V1 is address(0)");
        _elCoinV1Token = elCoinV1Token;
        _limitedAmount = 100000000000000000000000;
        _canSwap = false;
    }

    function setOperator(address operatorAddress, bool value) public onlyOwner{
        _operators[operatorAddress] = value;
        emit OperatorSetted(operatorAddress, value);
    }

    function mint(address account, uint256 amount) public onlyOperator returns(bool){
        _mint(account, amount);
        return true;
    }

    function burn(address account, uint256 amount) public onlyOperator returns(bool){
        _burn(account, amount);
        return true;
    }

    function setElCoinV1Token(IERC20 elCoinV1Token) public onlyOperator{
        require(address(elCoinV1Token) != address(0), "elCoinV1Token is address(0)");
        _elCoinV1Token = elCoinV1Token;
    }

    function wrap(uint256 amount) external nonReentrant canSwap returns(bool){
        require(amount > 0, "amount is 0");
        
        require(_elCoinV1Token.transferFrom(_msgSender(), address(this), amount),"Can not transfer ELCOIN from sender");
        _mint(_msgSender(), amount);

        emit Wrapped(_msgSender(), amount);
        return true;
    }

    function withdrawToken(address tokenAddress, address recipient, uint256 amount) public onlyOwner returns(bool){
        require(amount > 0, "Amount is 0");
        IERC20 token = IERC20(tokenAddress);
        require(token.transfer(recipient, amount),"Can not transfer token");
        return true;
    }

    function setCanSwap(bool value) external onlyOwner{
        _canSwap = value;
    }

    function setLimitedAmount(uint256 value) external onlyOwner{
        _limitedAmount = value;
    }

    function setLimitedAddress(address account, bool value) external onlyOwner{
        _isLimitedAddress[account] = value;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if(_limitedAmount > 0 && (_isLimitedAddress[from] || _isLimitedAddress[to])){
            require(amount <= _limitedAmount, "Reach limit amount to transfer per transaction");
        }
    }

    event OperatorSetted(address operatorAddress, bool value);
    event Wrapped(address indexed account, uint256 amount);
}"
