contract CISLA is Context, IBEP20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    mapping (address => mapping (address => uint256)) private _allowances;

    mapping (address => bool) private _isExcludedFromFee;
	
	mapping(address => bool) public automatedMarketMakerPairs;
	mapping(address => bool) public _isExcludedFromMaxWallet;

    mapping (address => bool) private _isExcluded;
    address[] private _excluded;
	
	address payable public _marketingWallet;
	address payable public _islandWallet;

    uint256 private constant MAX = ~uint256(0);
    uint256 private constant _tTotal = 1000000 * 10**6 * 10**18;

    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 private _tFeeTotal;

    string private constant _name = "Crypto Island";
    string private constant _symbol = "CISLA";
    uint8 private constant _decimals = 18;

    uint256 public _taxFee = 1;
    uint256 private _previousTaxFee = _taxFee;
	
	uint256 public _marketingFee = 2;
    uint256 private _previousMarketingFee = _marketingFee;

    uint256 public _liquidityFee = 3;
    uint256 private _previousLiquidityFee = _liquidityFee;
	
	uint256 public _islandFee = 4;
    uint256 private _previousIslandFee = _islandFee;

    IPancakeSwapV2Router02 public immutable pancakeSwapV2Router;
    address public pancakeSwapV2Pair;

    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = true;

    uint256 public _maxTxAmount = 5000 * 10**6 * 10**18;
	uint256 public _maxWalletAmount = 20000 * 10**6 * 10**18;
	
    uint256 private constant numTokensSellToAddToLiquidity = 500 * 10**6 * 10**18;
	
	uint256 private launchedAt;

    event MinTokensBeforeSwapUpdated(uint256 minTokensBeforeSwap);
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(uint256 tokensSwapped, uint256 bnbReceived, uint256 tokensIntoLiquidity);
	event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor (address payable marketing, address payable _island) {
        _rOwned[_msgSender()] = _rTotal;
		
		_marketingWallet = marketing;
		_islandWallet = _island;

        IPancakeSwapV2Router02 _pancakeSwapV2Router = IPancakeSwapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E); 
         // Create a pancakeSwap pair for this new token
        pancakeSwapV2Pair = IPancakeSwapV2Factory(_pancakeSwapV2Router.factory())
            .createPair(address(this), _pancakeSwapV2Router.WETH());

        // set the rest of the contract variables
        pancakeSwapV2Router = _pancakeSwapV2Router;

        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
		
		excludeFromMaxWallet(owner(), true);
		excludeFromMaxWallet(address(this), true);
		excludeFromMaxWallet(address(0), true);
		
		_setAutomatedMarketMakerPair(pancakeSwapV2Pair, true);

        emit Transfer(address(0), _msgSender(), _tTotal);
    }

