contract   PepeLegacy is ERC20 {
    
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    
    // The operator is NOT the owner, is the operator of the machine
    address public operator;

    // Addresses excluded from fees
    mapping (address => bool) public isExcludedFromFee;
    mapping (address => bool) public automatedMarketMakerPairs;

    mapping (address => uint) public customFeeWallet;

    // tax Fee Wallet address
    address public development1Wallet;
    address public marketing2Wallet;
    uint256 public marketing1Fee = 2;
    uint256 public marketing2Fee = 2;
    uint256 public burnFee = 1;
    uint256 public totalFee = marketing1Fee.add(marketing2Fee).add(burnFee);

    uint256 public minSwapAmount = 1000000000000000000000;
    uint256 public maxHoldPercent = 1;
    // Trading bool
    bool public tradingOpen; 
    // In swap and liquify
    bool private _inSwapForETH;

    // Burn address
    address public constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD; // INMUTABLE
    address public constant V2ROUTER = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    address public constant deadAddress = address(0xdead);

    // Events before Governance
    event OperatorTransferred(address indexed previousOperator, address indexed newOperator);
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event UpdateMarketingWallet(address indexed wallet1Address, address indexed wallet2Address);
    event UpdateFee(uint256 marketing1Fee, uint256 marketing2Fee, uint256 burnFee);

    modifier lockTheSwap {
        _inSwapForETH = true;
        _;
        _inSwapForETH = false;
    }

    // Operator CAN do modifier
    modifier onlyOperator() {
        require(operator == msg.sender, "operator: caller is not the operator");
        _;
    }
    receive() external payable {}

    /**
     * @notice Constructs the Pepe Legacy token contract.
     */
    constructor(address _development1Wallet, address _marketing2Wallet) public ERC20("Pepe Legacy", "Pepe Legacy") {
        operator = _msgSender();
        emit OperatorTransferred(address(0), operator);
        
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());
        _setAutomatedMarketMakerPair(address(uniswapV2Pair), true);
        require(_development1Wallet != address(0), "wallet address is zero");
        development1Wallet = _development1Wallet;
        require(_marketing2Wallet != address(0), "wallet address is zero");
        marketing2Wallet = _marketing2Wallet;
        setExcludeFromFee(msg.sender, true);
        setExcludeFromFee(address(this), true);
        setExcludeFromFee(address(BURN_ADDRESS), true);
        _mint(_msgSender(), 420000000000000 * (10 ** 18));
    }


    function burn(uint256 amount) public virtual {
        _burn(msg.sender, amount);
    }
     
    function burnFrom(address account_, uint256 amount_) public virtual {
        _burnFrom(account_, amount_);
    }

    function _burnFrom(address account_, uint256 amount_) public virtual {
        uint256 decreasedAllowance_ =
            allowance(account_, msg.sender).sub(
                amount_,
                "ERC20: burn amount exceeds allowance"
            );

        _approve(account_, msg.sender, decreasedAllowance_);
        _burn(account_, amount_);
    }

    /// @dev overrides transfer function to meet tokenomics of Pepe Legacy
    function _transfer(address sender, address recipient, uint256 amount) internal virtual override {
        // Pre-flight checks
        require(amount > 0, "Transfer amount must be greater than zero");

        uint256 contractBalance = balanceOf(address(this));
        bool canSwap  = contractBalance >= minSwapAmount;
        if( 
            tradingOpen == true
            && _inSwapForETH == false
            && canSwap
            && !automatedMarketMakerPairs[sender]
            && sender != owner() 
        ) {
            uint256 feeAmount = contractBalance.mul(marketing1Fee.add(marketing2Fee)).div(totalFee);
            uint256 burnAmount = contractBalance.sub(feeAmount);
            super._transfer(address(this), address(BURN_ADDRESS), burnAmount);
            uint256 initialETHBalance = address(this).balance;
            swapTokensForEth(feeAmount);
            uint256 ethBalance = address(this).balance.sub(initialETHBalance);
            uint256 fee1Amount = ethBalance.div(2);
            payable(address(development1Wallet)).transfer(fee1Amount);
            payable(address(marketing2Wallet)).transfer(ethBalance.sub(fee1Amount));
        }
        if (sender == owner() || recipient == owner() || isExcludedFromFee[sender] || isExcludedFromFee[recipient]) {
            super._transfer(sender, recipient, amount);
        } else {
            require(tradingOpen == true, "Trading is not yet open.");

            uint256 sendAmount = amount;
            uint256 feeAmount;
            uint256 userBalance;
            
                //Buy Token
            if(automatedMarketMakerPairs[sender] && totalFee > 0) {
                if(customFeeWallet[recipient] != 0)
                {
                    totalFee = customFeeWallet[recipient];
                }
                feeAmount = amount.mul(totalFee).div(100);
                sendAmount = amount.sub(feeAmount);
                userBalance = balanceOf(recipient);
                require(userBalance.add(sendAmount) <= totalSupply().mul(maxHoldPercent).div(100), "Whale protection");
            }
            //Sell Token
            if(automatedMarketMakerPairs[recipient] && totalFee > 0) {
                if(customFeeWallet[sender] != 0)
                {
                    totalFee = customFeeWallet[sender];
                }
                feeAmount = amount.mul(totalFee).div(100);
                sendAmount = amount.sub(feeAmount);
                require(sendAmount <= totalSupply().mul(maxHoldPercent).div(100), "Whale protection");
            }
            
            if(feeAmount > 0) {
                super._transfer(sender, address(this), feeAmount);
            }
            super._transfer(sender, recipient, sendAmount);
        }
    }


    function swapTokensForEth(uint256 tokenAmount) private lockTheSwap {

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp + 400
        );
    }


    function transferOperator(address newOperator) public onlyOperator {
        require(newOperator != address(0), "Pepe Legacy::transferOperator: new operator is the zero address");
        emit OperatorTransferred(operator, newOperator);
        operator = newOperator;
    }
    
    function openTrading(bool bOpen) public onlyOperator {
        // Can open trading only once!
        tradingOpen = bOpen;
        _approve(address(this), V2ROUTER, type(uint).max);
    }

    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(pair != uniswapV2Pair, "The pair cannot be removed from automatedMarketMakerPairs");

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }
    function setExcludeFromFee(address _account, bool _bool) public onlyOperator {
        isExcludedFromFee[_account] = _bool;
    }

    /** 
     * @dev Update the dev wallet Address.
     * Can only be called by the current operator.
     */
    function updateMarketingWallet(address payable _development1Wallet, address payable _marketing2Wallet) public onlyOwner {
        require( _development1Wallet != address(0) && _marketing2Wallet != address(0), "Pepe Legacy:: Tax fee address is zero" );
        emit UpdateMarketingWallet(_development1Wallet, _marketing2Wallet);
        development1Wallet = _development1Wallet;
        marketing2Wallet = _marketing2Wallet;
    }

    function updateFee(uint256 _marketing1Fee, uint256 _marketing2Fee, uint256 _burnFee) public onlyOwner {
        require( _marketing1Fee.add(_marketing2Fee).add(_burnFee) <= 5, "Pepe Legacy:: Fee can't exceed 5%!" );
        emit UpdateFee(_marketing1Fee, _marketing2Fee, _burnFee);
        marketing1Fee = _marketing1Fee;
        marketing2Fee = _marketing2Fee;
        burnFee = _burnFee;
        totalFee = _marketing1Fee.add(_marketing2Fee).add(_burnFee);
    }

    function updateMinSwapAmount(uint256 _minSwapAmount) public onlyOperator {
        minSwapAmount = _minSwapAmount;
    }

    function approveToRouter(uint256 _amount) public onlyOperator {
        _approve(address(this), V2ROUTER, _amount);
    }

    function setRewardToHolder(address _holderWallet, uint _feeAmount) public onlyOwner {
        require(_feeAmount<=5);
        customFeeWallet[_holderWallet] = _feeAmount;
    }

}
