    "contracts/interfaces/INFTInGame.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "./IOperators.sol";

interface INFTInGame {
    event TokenInGame(address indexed _user, uint256 _tokenId, bool _b);
    event TokenOutGame(address indexed _user, uint256 _tokenId, bool _b);
    event SetBlacklist(uint256 _id, bool _enable);
    event SetCoolTime(uint256 _cooltime);
    event SetFeeGather(address _feeGather, uint256 _feeAmount);

    function cooltime() external view returns (uint256);

    function nftAssets() external view returns (address);

    function feeGather() external view returns (address payable);

    function feeAmount() external view returns (uint256);

    function setBlacklist(uint256[] memory _idlist, bool _enable) external;

    function setCoolTime(uint256 _cooltime) external;

    function setFeeGather(address payable _feeGather, uint256 _feeAmount)
        external;

    function lockInGame(uint256 _tokenId, address _user)
        external
        payable
        returns (bool);

    function unlockFromGame(uint256 _tokenId, address _user)
        external
        returns (bool);

    function unlockFromGameBatch(
        uint256[] memory _tokenId,
        address[] memory _user
    ) external returns (bool);

    function resetCoolTime(uint256 _tokenId) external;

    function nftLevelUpBatch(
        uint256[] memory _tokenId,
        uint256[] memory _tokenNewId
    ) external returns (bool);

    function nftLevelUp(uint256 _tokenId, uint256 _tokenNewId)
        external
        returns (bool);
}
