    "contracts/interfaces/INFTMarket.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

interface INFTMarket {
    event GoodsCreated(
        uint256 _goodsid,
        address _payToken,
        address _itemNFT,
        address _itemToken,
        uint256 _feeRate,
        bool _available
    );
    event GoodsChanged(uint256 _goodsid, uint256 _feeRate, bool _available);
    event SetFeeGather(address _feegather);

    event ItemOnSell(
        uint256 _orderid,
        address indexed _user,
        uint256 _goodsid,
        uint256 _tokenId,
        uint256 _amount,
        uint256 _price,
        uint256 _timestamp
    );
    event ItemOnBuy(
        uint256 _orderid,
        address indexed _user,
        uint256 _goodsid,
        uint256 _tokenId,
        uint256 _amount,
        uint256 _pay,
        uint256 _fee,
        uint256 _timestamp
    );
    event ItemOnCancel(
        uint256 _orderid,
        address indexed _user,
        uint256 _goodsid,
        uint256 _tokenId,
        uint256 _amount,
        uint256 _timestamp
    );

    struct MarketGoods {
        address payToken;
        address itemNFT;
        address itemToken;
        uint256 payDecimals;
        uint256 tokenDecimals;
        uint256 feeRate;
        bool available;
    }

    struct OrderItem {
        address owner;
        uint256 goodsId;
        uint256 tokenId;
        uint256 stock;
        uint256 amount;
        uint256 price;
        uint256 timestamp;
        bool available;
    }

    function setFeeGather(address _feegather) external;

    function getFeeGather() external view returns (address);

    function getMarketGoods(uint256 _goodsid)
        external
        view
        returns (MarketGoods memory);

    function getMarketGoodsLength() external view returns (uint256);

    function getOrderItem(uint256 _orderid)
        external
        view
        returns (OrderItem memory);

    function getOrderItemLength() external view returns (uint256);

    function feegather() external view returns (address);

    function addGoods(
        address _payToken,
        address _itemNFT,
        address _itemToken,
        uint256 _feeRate
    ) external;

    function setGoods(uint256 _goodsid, uint256 _feeRate) external;

    function setGoodsAvailable(uint256 _goodsid, bool _available) external;

    function onSell(
        address _user,
        uint256 _goodsid,
        uint256 _tokenIdOrAmount,
        uint256 _price
    ) external;

    function onBuy(
        uint256 _orderid,
        uint256 _amount,
        address _user
    ) external;

    function onCancel(uint256 _orderid, address _user) external;
}
