// Import the ERC20 interface and SafeMath library


interface IERC20 {
    function transfer(address _to, uint256 _value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool);

    function balanceOf(address account) external view returns (uint256);

    /**
     * @dev Sets `amount` as the allowance of `spender` over the caller's tokens.
     *
     * Returns a boolean value indicating whether the operation succeeded.
     *
     * IMPORTANT: Beware that changing an allowance with this method brings the risk
     * that someone may use both the old and the new allowance by unfortunate
     * transaction ordering. One possible solution to mitigate this race
     * condition is to first reduce the spender's allowance to 0 and set the
     * desired value afterwards:
     * https://github.com/ethereum/EIPs/issues/20#issuecomment-263524729
     *
     * Emits an {Approval} event.
     */
    function approve(address spender, uint256 amount) external returns (bool);

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) external returns (bool);
}

abstract contract ReentrancyGuard {
    uint256 private constant _NOT_ENTERED = 1;
    uint256 private constant _ENTERED = 2;

    uint256 private _status;

    constructor() {
        _status = _NOT_ENTERED;
    }

    modifier nonReentrant() {
        require(_status != _ENTERED, "ReentrancyGuard: reentrant call");
        _status = _ENTERED;

        _;
        _status = _NOT_ENTERED;
    }
}

contract MTVRPresale is ReentrancyGuard {
    using SafeMath for uint256;

    address public owner; // Contract owner
    IERC20 public mtvrToken; // The MTVR token contract
    uint256 public rate = 7350; // BNB to MTVR conversion rate (0.2 BNB = 1470 MTVR)
    uint256 public tokensSold; // Total tokens sold

    uint256 public minAmount = 0.2 ether; // Minimum BNB contribution
    uint256 public startDate = 1698796800; // Wednesday, November 1, 2023 12:00:00 AM
    uint256 public endDate = 1701648000000; // Monday, December 4, 2023 12:00:00 AM

    mapping(address => uint256) public contributions;
    address[] public investors;

    event BnbSent(address indexed buyer, uint256 bnbAmount);
    event TokensPurchased(
        address indexed buyer,
        uint256 bnbAmount,
        uint256 tokensAmount
    );
    event TokensSold(uint256 totalTokensSold);
    event ContributionsUpdated(
        address indexed contributor,
        uint256 contributionAmount
    );

    constructor(address _mtvrToken) {
        require(_mtvrToken != address(0), "Token address cannot be 0x0");
        owner = msg.sender;
        mtvrToken = IERC20(_mtvrToken);
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function");
        _;
    }

    modifier saleActive() {
        require(
            block.timestamp >= startDate && block.timestamp <= endDate,
            "Presale is not active"
        );
        _;
    }

    modifier validContribution() {
        require(
            msg.value >= minAmount,
            "Contribution amount is less than the minimum"
        );
        _;
    }

    function updateEndDate(uint256 _endDate) external onlyOwner {
        endDate = _endDate;
    }

    function tokenBalance() public view returns (uint256) {
        return mtvrToken.balanceOf(address(this));
    }

    /**
     * @dev Retrieve amount of token to transfer for purchased amount
     *
     */
    function getTokenAmountToTransfer(
        uint256 _bnbWei
    ) public view returns (uint256) {
        return ceilDiv(_bnbWei.mul(rate), 10 ** 9);
    }

    function buyTokens()
        external
        payable
        saleActive
        validContribution
        nonReentrant
    {
        uint256 bnbSent = msg.value;
        require(bnbSent > 0, "You must send BNB to contribute");
        uint256 tokensToPurchase = ceilDiv(bnbSent.mul(rate), 10 ** 9);
        require(
            mtvrToken.balanceOf(address(this)) >= tokensToPurchase,
            "Not enough tokens in the contract"
        );
        require(
            mtvrToken.transfer(msg.sender, tokensToPurchase),
            "Token transfer failed"
        );
        tokensSold = tokensSold.add(tokensToPurchase);
        contributions[msg.sender] = contributions[msg.sender].add(bnbSent);
        investors.push(msg.sender);
        emit BnbSent(msg.sender, bnbSent);
        emit TokensPurchased(msg.sender, bnbSent, tokensToPurchase);
        emit TokensSold(tokensSold);
        emit ContributionsUpdated(msg.sender, contributions[msg.sender]);
    }

    function getInvestors() external view returns (address[] memory) {
        return investors;
    }

    function withdrawBNB() external onlyOwner nonReentrant {
        uint256 balance = address(this).balance;
        (bool success, ) = payable(owner).call{value: balance}("");
        require(success, "Transfer failed");
    }

    function withdrawUnsoldTokens() external onlyOwner {
        require(block.timestamp > endDate, "Presale has not ended yet");
        uint256 unsoldTokens = mtvrToken.balanceOf(address(this));
        if (unsoldTokens > 0) {
            mtvrToken.transfer(owner, unsoldTokens);
        }
    }

    /**
     * @dev Returns the ceiling of the division of two numbers.
     *
     * This differs from standard division with `/` in that it rounds up instead
     * of rounding down.
     */
    function ceilDiv(uint256 a, uint256 b) internal pure returns (uint256) {
        // (a + b - 1) / b can overflow on addition, so we distribute.
        return a == 0 ? 0 : (a - 1) / b + 1;
    }
}
